import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useQuery, useMutation, useQueryClient } from 'react-query';
import { 
  ArrowLeft, 
  Edit, 
  Trash2, 
  Users, 
  Trophy, 
  Calendar, 
  MapPin,
  Settings,
  BarChart3,
  Bell,
  Play,
  Pause,
  CheckCircle2,
  FileText,
  Image,
  Award,
  Clock,
  Target,
  TrendingUp,
  MessageSquare,
  Download,
  Upload,
  Zap,
  Shield,
  Globe,
  Filter,
  Search,
  Plus,
  Eye,
  Share2,
  Star,
  AlertCircle,
  CheckSquare,
  XSquare
} from 'lucide-react';
import { tournamentService, tournamentKnockoutService } from '../services';
import { useAuth } from '../context/AuthContext';
import LoadingSpinner from '../components/LoadingSpinner';
import TournamentEditForm from '../components/tournament/TournamentEditForm';
import TournamentBracketGenerator from '../components/tournament/TournamentBracketGenerator';
import TournamentBracketView from '../components/tournament/TournamentBracketView';
import TournamentRoundManager from '../components/tournament/TournamentRoundManager';
import TeamRegistrationManager from '../components/tournament/TeamRegistrationManager';
import TournamentAnalytics from '../components/tournament/TournamentAnalytics';
import { formatDate, getStatusColor, getTournamentStatusLabel } from '../utils/helpers';
import toast from 'react-hot-toast';

const TournamentAdminDetailPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { user } = useAuth();
  const queryClient = useQueryClient();
  const [activeTab, setActiveTab] = useState('overview');
  const [showEditModal, setShowEditModal] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [filterStatus, setFilterStatus] = useState('all');

  const { data: tournament, isLoading, error } = useQuery(
    ['tournament', id],
    () => tournamentService.getTournamentById(id),
    {
      select: (response) => response.data || response,
      enabled: !!id
    }
  );

  const { data: teams } = useQuery(
    ['tournament-teams', id],
    () => tournamentService.getTeamsByTournament(id),
    {
      select: (response) => response.data || response,
      enabled: !!id
    }
  );

  const { data: matches } = useQuery(
    ['tournament-matches', id],
    () => tournamentService.getMatchesByTournament(id),
    {
      select: (response) => response.data || response,
      enabled: !!id
    }
  );

  const { data: bracket } = useQuery(
    ['tournament-bracket', id],
    () => tournamentService.getTournamentBracket(id),
    {
      select: (response) => response.data || response,
      enabled: !!id && tournament?.status !== 'REGISTRATION'
    }
  );

  const deleteTournamentMutation = useMutation(
    () => tournamentService.deleteTournament(id),
    {
      onSuccess: () => {
        toast.success('Đã xóa giải đấu thành công');
        navigate('/admin/tournaments');
      },
      onError: (error) => {
        toast.error(error.response?.data?.message || 'Lỗi khi xóa giải đấu');
      }
    }
  );

  const startTournamentMutation = useMutation(
    () => tournamentService.startTournament(id),
    {
      onSuccess: () => {
        toast.success('Đã bắt đầu giải đấu thành công');
        queryClient.invalidateQueries(['tournament', id]);
      },
      onError: (error) => {
        toast.error(error.response?.data?.message || 'Lỗi khi bắt đầu giải đấu');
      }
    }
  );

  const handleDelete = () => {
    if (window.confirm('Bạn có chắc chắn muốn xóa giải đấu này? Hành động này không thể hoàn tác.')) {
      deleteTournamentMutation.mutate();
    }
  };

  const handleStart = () => {
    if (window.confirm('Bạn có chắc chắn muốn bắt đầu giải đấu này?')) {
      startTournamentMutation.mutate();
    }
  };

  const canEdit = user?.role === 'ADMIN' || 
    (user?.role === 'ORGANIZER' && tournament?.createdBy?.id === user?.id);

  const tabs = [
    { 
      id: 'overview', 
      name: 'Tổng quan', 
      icon: Trophy,
      description: 'Thông tin tổng quan và trạng thái giải đấu',
      color: 'bg-gradient-to-r from-blue-500 to-blue-600'
    },
    { 
      id: 'teams', 
      name: 'Quản lý đội', 
      icon: Users,
      count: teams?.length || tournament?.currentTeams || 0,
      description: 'Quản lý đội tham gia và thành viên',
      color: 'bg-gradient-to-r from-green-500 to-green-600'
    },
    { 
      id: 'bracket', 
      name: 'Sơ đồ thi đấu', 
      icon: Trophy,
      description: 'Quản lý bracket và vòng loại trực tiếp',
      color: 'bg-gradient-to-r from-purple-500 to-purple-600'
    },
    { 
      id: 'matches', 
      name: 'Quản lý trận đấu', 
      icon: Calendar,
      count: matches?.length || 0,
      description: 'Lịch thi đấu, kết quả và thống kê trận đấu',
      color: 'bg-gradient-to-r from-orange-500 to-orange-600'
    },
    { 
      id: 'analytics', 
      name: 'Thống kê', 
      icon: BarChart3,
      description: 'Phân tích dữ liệu và báo cáo chi tiết',
      color: 'bg-gradient-to-r from-indigo-500 to-indigo-600'
    },
    { 
      id: 'notifications', 
      name: 'Thông báo', 
      icon: Bell,
      description: 'Gửi thông báo và cập nhật cho các đội',
      color: 'bg-gradient-to-r from-red-500 to-red-600'
    },
    { 
      id: 'media', 
      name: 'Tài liệu', 
      icon: FileText,
      description: 'Quản lý hình ảnh, video và tài liệu',
      color: 'bg-gradient-to-r from-teal-500 to-teal-600'
    },
    { 
      id: 'settings', 
      name: 'Cài đặt', 
      icon: Settings,
      description: 'Cấu hình nâng cao và quyền hạn',
      color: 'bg-gradient-to-r from-gray-500 to-gray-600'
    }
  ];

  const getProgressPercentage = () => {
    if (!tournament) return 0;
    const totalSteps = 5; // Registration, Start, Bracket, Matches, Complete
    let completed = 1; // Always have registration

    if (tournament.status !== 'REGISTRATION') completed++;
    if (bracket) completed++;
    if (matches?.some(m => m.status === 'COMPLETED')) completed++;
    if (tournament.status === 'COMPLETED') completed++;

    return Math.round((completed / totalSteps) * 100);
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-gray-50 to-gray-100 flex items-center justify-center">
        <div className="text-center">
          <LoadingSpinner />
          <p className="mt-4 text-gray-600">Đang tải thông tin giải đấu...</p>
        </div>
      </div>
    );
  }

  if (error || !tournament) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-gray-50 to-gray-100 flex items-center justify-center">
        <div className="text-center">
          <Trophy className="h-16 w-16 text-gray-400 mx-auto mb-4" />
          <h2 className="text-2xl font-bold text-gray-900 mb-2">Không tìm thấy giải đấu</h2>
          <p className="text-gray-600 mb-4">Giải đấu được yêu cầu không tồn tại hoặc đã bị xóa.</p>
          <button onClick={() => navigate('/admin/tournaments')} className="btn-primary">
            <ArrowLeft className="h-4 w-4 mr-2" />
            Quay lại danh sách giải đấu
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 to-gray-100">
      {/* Enhanced Header */}
      <div className="bg-white shadow-lg border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
          <div className="flex items-center justify-between mb-4">
            <div className="flex items-center space-x-4">
              <button
                onClick={() => navigate('/admin/tournaments')}
                className="text-gray-400 hover:text-gray-600 transition-colors p-2 rounded-lg hover:bg-gray-100"
              >
                <ArrowLeft className="h-6 w-6" />
              </button>

              <div className="flex items-center space-x-3">
                <div className="bg-gradient-to-r from-orange-500 to-red-500 p-3 rounded-xl shadow-lg">
                  <Trophy className="h-8 w-8 text-white" />
                </div>
                <div>
                  <h1 className="text-3xl font-bold text-gray-900">{tournament.name}</h1>
                  <div className="flex items-center space-x-4 mt-1">
                    <span className={`px-3 py-1 rounded-full text-sm font-medium ${getStatusColor(tournament.status)}`}>
                      {getTournamentStatusLabel(tournament.status)}
                    </span>
                    <div className="flex items-center space-x-1 text-sm text-gray-600">
                      <Users className="h-4 w-4" />
                      <span>{tournament.currentTeams}/{tournament.maxTeams} đội</span>
                    </div>
                    <div className="flex items-center space-x-1 text-sm text-gray-600">
                      <Calendar className="h-4 w-4" />
                      <span>{formatDate(tournament.startDate)}</span>
                    </div>
                    <div className="flex items-center space-x-1 text-sm text-gray-600">
                      <MapPin className="h-4 w-4" />
                      <span>{tournament.location}</span>
                    </div>
                  </div>
                </div>
              </div>
            </div>

            {canEdit && (
              <div className="flex items-center space-x-3">
                <button
                  onClick={() => window.open(`/tournament/${id}`, '_blank')}
                  className="flex items-center px-4 py-2 text-gray-600 hover:text-gray-800 hover:bg-gray-100 rounded-lg transition-colors"
                >
                  <Eye className="h-4 w-4 mr-2" />
                  Xem công khai
                </button>

                {tournament.status === 'REGISTRATION' && (
                  <button
                    onClick={handleStart}
                    className="flex items-center px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors shadow-md"
                    disabled={startTournamentMutation.isLoading}
                  >
                    <Play className="h-4 w-4 mr-2" />
                    {startTournamentMutation.isLoading ? 'Đang bắt đầu...' : 'Bắt đầu giải đấu'}
                  </button>
                )}

                <button
                  onClick={() => setShowEditModal(true)}
                  className="flex items-center px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors shadow-md"
                >
                  <Edit className="h-4 w-4 mr-2" />
                  Chỉnh sửa
                </button>

                <button
                  onClick={handleDelete}
                  className="flex items-center px-4 py-2 text-red-600 hover:text-red-700 hover:bg-red-50 rounded-lg transition-colors"
                  disabled={deleteTournamentMutation.isLoading}
                >
                  <Trash2 className="h-4 w-4 mr-2" />
                  Xóa
                </button>
              </div>
            )}
          </div>

          {/* Progress Bar */}
          <div className="bg-gray-200 rounded-full h-2 mb-4">
            <div 
              className="bg-gradient-to-r from-blue-500 to-purple-600 h-2 rounded-full transition-all duration-500"
              style={{ width: `${getProgressPercentage()}%` }}
            ></div>
          </div>
          <div className="flex justify-between text-sm text-gray-600">
            <span>Tiến độ giải đấu: {getProgressPercentage()}%</span>
            <span>Cập nhật lần cuối: {formatDate(new Date())}</span>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Enhanced Tabs */}
        <div className="bg-white rounded-xl shadow-lg border border-gray-200 mb-8 overflow-hidden">
          <div className="border-b border-gray-200 bg-gray-50">
            <nav className="flex overflow-x-auto">
              {tabs.map((tab) => (
                <button
                  key={tab.id}
                  onClick={() => setActiveTab(tab.id)}
                  className={`flex-shrink-0 py-4 px-6 border-b-3 font-medium text-sm flex items-center space-x-3 transition-all duration-200 ${
                    activeTab === tab.id
                      ? 'border-blue-500 text-blue-600 bg-white'
                      : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300 hover:bg-white/50'
                  }`}
                >
                  <div className={`p-2 rounded-lg ${
                    activeTab === tab.id 
                      ? 'bg-blue-100 text-blue-600' 
                      : 'bg-gray-100 text-gray-500'
                  }`}>
                    <tab.icon className="h-4 w-4" />
                  </div>
                  <div className="text-left">
                    <div className="flex items-center space-x-2">
                      <span>{tab.name}</span>
                      {tab.count !== undefined && (
                        <span className={`px-2 py-0.5 rounded-full text-xs font-medium ${
                          activeTab === tab.id 
                            ? 'bg-blue-100 text-blue-800' 
                            : 'bg-gray-100 text-gray-600'
                        }`}>
                          {tab.count}
                        </span>
                      )}
                    </div>
                    <div className="text-xs text-gray-500 hidden sm:block">{tab.description}</div>
                  </div>
                </button>
              ))}
            </nav>
          </div>

          <div className="p-8">
            {/* Overview Tab */}
            {activeTab === 'overview' && (
              <div className="space-y-8">
                {/* Key Metrics */}
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
                  <div className="bg-gradient-to-r from-blue-500 to-blue-600 rounded-xl p-6 text-white shadow-lg transform hover:scale-105 transition-transform">
                    <div className="flex items-center justify-between">
                      <div>
                        <p className="text-blue-100 text-sm font-medium">Đội tham gia</p>
                        <p className="text-3xl font-bold">
                          {tournament.currentTeams}/{tournament.maxTeams}
                        </p>
                        <p className="text-blue-100 text-sm mt-1">
                          {Math.round((tournament.currentTeams / tournament.maxTeams) * 100)}% đã đăng ký
                        </p>
                      </div>
                      <Users className="h-12 w-12 text-blue-200" />
                    </div>
                  </div>

                  <div className="bg-gradient-to-r from-green-500 to-green-600 rounded-xl p-6 text-white shadow-lg transform hover:scale-105 transition-transform">
                    <div className="flex items-center justify-between">
                      <div>
                        <p className="text-green-100 text-sm font-medium">Trận đấu</p>
                        <p className="text-3xl font-bold">{matches?.length || 0}</p>
                        <p className="text-green-100 text-sm mt-1">
                          {matches?.filter(m => m.status === 'COMPLETED').length || 0} đã hoàn thành
                        </p>
                      </div>
                      <Calendar className="h-12 w-12 text-green-200" />
                    </div>
                  </div>

                  <div className="bg-gradient-to-r from-orange-500 to-orange-600 rounded-xl p-6 text-white shadow-lg transform hover:scale-105 transition-transform">
                    <div className="flex items-center justify-between">
                      <div>
                        <p className="text-orange-100 text-sm font-medium">Môn thể thao</p>
                        <p className="text-xl font-bold">{tournament.sportType}</p>
                        <p className="text-orange-100 text-sm mt-1">Hệ thống loại trực tiếp</p>
                      </div>
                      <Trophy className="h-12 w-12 text-orange-200" />
                    </div>
                  </div>

                  <div className="bg-gradient-to-r from-purple-500 to-purple-600 rounded-xl p-6 text-white shadow-lg transform hover:scale-105 transition-transform">
                    <div className="flex items-center justify-between">
                      <div>
                        <p className="text-purple-100 text-sm font-medium">Địa điểm</p>
                        <p className="text-lg font-bold truncate">{tournament.location}</p>
                        <p className="text-purple-100 text-sm mt-1">Tổ chức chuyên nghiệp</p>
                      </div>
                      <MapPin className="h-12 w-12 text-purple-200" />
                    </div>
                  </div>
                </div>

                {/* Tournament Status Timeline */}
                <div className="bg-white border border-gray-200 rounded-xl p-6 shadow-sm">
                  <h3 className="text-xl font-semibold text-gray-900 mb-6 flex items-center">
                    <Clock className="h-6 w-6 mr-2 text-blue-600" />
                    Tiến trình giải đấu
                  </h3>
                  <div className="relative">
                    <div className="absolute left-4 top-6 bottom-6 w-0.5 bg-gray-200"></div>
                    {[
                      { 
                        step: 'Đăng ký đội tham gia', 
                        completed: true, 
                        current: tournament.status === 'REGISTRATION',
                        date: formatDate(tournament.createdAt || new Date()),
                        description: 'Các đội đăng ký tham gia giải đấu'
                      },
                      { 
                        step: 'Bắt đầu giải đấu', 
                        completed: tournament.status !== 'REGISTRATION', 
                        current: tournament.status === 'ONGOING',
                        date: tournament.status !== 'REGISTRATION' ? formatDate(tournament.startDate) : null,
                        description: 'Giải đấu chính thức bắt đầu'
                      },
                      { 
                        step: 'Tạo sơ đồ thi đấu', 
                        completed: !!bracket, 
                        current: false,
                        date: bracket ? 'Đã tạo' : null,
                        description: 'Tạo bracket và lịch thi đấu'
                      },
                      { 
                        step: 'Thi đấu các vòng', 
                        completed: matches?.some(m => m.status === 'COMPLETED'), 
                        current: tournament.status === 'ONGOING',
                        date: matches?.some(m => m.status === 'COMPLETED') ? 'Đang diễn ra' : null,
                        description: 'Thực hiện các trận đấu theo lịch'
                      },
                      { 
                        step: 'Hoàn thành giải đấu', 
                        completed: tournament.status === 'COMPLETED', 
                        current: false,
                        date: tournament.status === 'COMPLETED' ? formatDate(tournament.endDate) : null,
                        description: 'Trao giải và kết thúc giải đấu'
                      }
                    ].map((item, index) => (
                      <div key={index} className="relative flex items-start mb-8 last:mb-0">
                        <div className={`w-8 h-8 rounded-full flex items-center justify-center z-10 ${
                          item.completed 
                            ? 'bg-green-500 text-white shadow-lg' 
                            : item.current 
                            ? 'bg-blue-500 text-white animate-pulse shadow-lg' 
                            : 'bg-gray-300 text-gray-600'
                        }`}>
                          {item.completed ? (
                            <CheckCircle2 className="h-4 w-4" />
                          ) : item.current ? (
                            <Zap className="h-4 w-4" />
                          ) : (
                            <span className="text-sm font-bold">{index + 1}</span>
                          )}
                        </div>
                        <div className="ml-6 flex-1">
                          <h4 className={`font-semibold text-lg ${
                            item.completed || item.current ? 'text-gray-900' : 'text-gray-500'
                          }`}>
                            {item.step}
                          </h4>
                          <p className="text-sm text-gray-600 mt-1">{item.description}</p>
                          {item.date && (
                            <p className="text-sm font-medium text-blue-600 mt-2">{item.date}</p>
                          )}
                        </div>
                      </div>
                    ))}
                  </div>
                </div>

                {/* Tournament Information */}
                <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                  <div className="bg-white border border-gray-200 rounded-xl p-6 shadow-sm">
                    <h3 className="text-xl font-semibold text-gray-900 mb-6 flex items-center">
                      <FileText className="h-6 w-6 mr-2 text-green-600" />
                      Thông tin giải đấu
                    </h3>
                    <div className="space-y-4">
                      <div>
                        <label className="text-sm font-medium text-gray-500">Mô tả</label>
                        <p className="text-gray-900 mt-1 leading-relaxed">{tournament.description || 'Chưa có mô tả chi tiết'}</p>
                      </div>
                      <div>
                        <label className="text-sm font-medium text-gray-500">Thể lệ thi đấu</label>
                        <p className="text-gray-900 mt-1 leading-relaxed">{tournament.rules || 'Áp dụng luật thi đấu tiêu chuẩn'}</p>
                      </div>
                      <div>
                        <label className="text-sm font-medium text-gray-500">Giải thưởng</label>
                        <div className="mt-1 p-3 bg-yellow-50 rounded-lg border border-yellow-200">
                          <p className="text-gray-900 leading-relaxed">{tournament.prizeInfo || 'Thông tin giải thưởng sẽ được cập nhật'}</p>
                        </div>
                      </div>
                    </div>
                  </div>

                  <div className="bg-white border border-gray-200 rounded-xl p-6 shadow-sm">
                    <h3 className="text-xl font-semibold text-gray-900 mb-6 flex items-center">
                      <Calendar className="h-6 w-6 mr-2 text-blue-600" />
                      Lịch trình chi tiết
                    </h3>
                    <div className="space-y-4">
                      <div className="flex items-center justify-between p-4 bg-red-50 rounded-lg border border-red-200">
                        <div>
                          <label className="text-sm font-medium text-red-600">Hạn đăng ký</label>
                          <p className="text-red-900 font-semibold text-lg">{formatDate(tournament.registrationDeadline)}</p>
                        </div>
                        <AlertCircle className="h-6 w-6 text-red-500" />
                      </div>
                      <div className="flex items-center justify-between p-4 bg-green-50 rounded-lg border border-green-200">
                        <div>
                          <label className="text-sm font-medium text-green-600">Ngày bắt đầu</label>
                          <p className="text-green-900 font-semibold text-lg">{formatDate(tournament.startDate)}</p>
                        </div>
                        <Play className="h-6 w-6 text-green-500" />
                      </div>
                      <div className="flex items-center justify-between p-4 bg-blue-50 rounded-lg border border-blue-200">
                        <div>
                          <label className="text-sm font-medium text-blue-600">Ngày kết thúc</label>
                          <p className="text-blue-900 font-semibold text-lg">{formatDate(tournament.endDate) || 'Chưa xác định'}</p>
                        </div>
                        <CheckSquare className="h-6 w-6 text-blue-500" />
                      </div>
                      <div className="p-4 bg-gray-50 rounded-lg">
                        <label className="text-sm font-medium text-gray-500">Thông tin liên hệ</label>
                        <p className="text-gray-900 mt-1 font-medium">{tournament.contactInfo}</p>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            )}

            {/* Teams Tab */}
            {activeTab === 'teams' && (
              <TeamRegistrationManager 
                tournament={tournament} 
                teams={teams}
                searchQuery={searchQuery}
                filterStatus={filterStatus}
                onTeamsUpdated={() => {
                  queryClient.invalidateQueries(['tournament-teams', id]);
                  queryClient.invalidateQueries(['tournament', id]);
                }}
              />
            )}

            {/* Bracket Tab */}
            {activeTab === 'bracket' && (
              <div className="space-y-6">
                {tournament.status === 'REGISTRATION' && (
                  <TournamentBracketGenerator 
                    tournament={tournament}
                    onBracketGenerated={() => {
                      queryClient.invalidateQueries(['tournament-bracket', id]);
                      queryClient.invalidateQueries(['tournament', id]);
                    }}
                  />
                )}

                {bracket && <TournamentBracketView bracket={bracket} tournament={tournament} />}

                {tournament.status === 'ONGOING' && (
                  <TournamentRoundManager 
                    tournament={tournament}
                    currentRound={bracket?.currentRound}
                    matches={matches}
                    onRoundAdvanced={() => {
                      queryClient.invalidateQueries(['tournament-bracket', id]);
                      queryClient.invalidateQueries(['tournament-matches', id]);
                      queryClient.invalidateQueries(['tournament', id]);
                    }}
                  />
                )}
              </div>
            )}

            {/* Matches Tab */}
            {activeTab === 'matches' && (
              <div className="space-y-6">
                <div className="text-center py-16 bg-white rounded-xl border border-gray-200">
                  <Calendar className="h-16 w-16 text-gray-400 mx-auto mb-4" />
                  <h4 className="text-xl font-semibold text-gray-900 mb-2">Chức năng đang phát triển</h4>
                  <p className="text-gray-600 mb-6">Quản lý trận đấu sẽ được cập nhật trong phiên bản tiếp theo</p>
                </div>
              </div>
            )}

            {/* Analytics Tab */}
            {activeTab === 'analytics' && (
              <TournamentAnalytics tournament={tournament} matches={matches} teams={teams} />
            )}

            {/* Notifications Tab */}
            {activeTab === 'notifications' && (
              <div className="space-y-6">
                <div className="text-center py-16 bg-white rounded-xl border border-gray-200">
                  <Bell className="h-16 w-16 text-gray-400 mx-auto mb-4" />
                  <h4 className="text-xl font-semibold text-gray-900 mb-2">Chức năng đang phát triển</h4>
                  <p className="text-gray-600 mb-6">Hệ thống thông báo sẽ được cập nhật trong phiên bản tiếp theo</p>
                </div>
              </div>
            )}

            {/* Media Tab */}
            {activeTab === 'media' && (
              <div className="space-y-6">
                <div className="text-center py-16 bg-white rounded-xl border border-gray-200">
                  <FileText className="h-16 w-16 text-gray-400 mx-auto mb-4" />
                  <h4 className="text-xl font-semibold text-gray-900 mb-2">Chức năng đang phát triển</h4>
                  <p className="text-gray-600 mb-6">Quản lý tài liệu sẽ được cập nhật trong phiên bản tiếp theo</p>
                </div>
              </div>
            )}

            {/* Settings Tab */}
            {activeTab === 'settings' && (
              <div className="space-y-6">
                <div className="text-center py-16 bg-white rounded-xl border border-gray-200">
                  <Settings className="h-16 w-16 text-gray-400 mx-auto mb-4" />
                  <h4 className="text-xl font-semibold text-gray-900 mb-2">Chức năng đang phát triển</h4>
                  <p className="text-gray-600 mb-6">Cài đặt nâng cao sẽ được cập nhật trong phiên bản tiếp theo</p>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Edit Modal */}
      {showEditModal && (
        <TournamentEditForm
          tournament={tournament}
          isOpen={showEditModal}
          onClose={() => setShowEditModal(false)}
          onSuccess={() => {
            queryClient.invalidateQueries(['tournament', id]);
            setShowEditModal(false);
          }}
        />
      )}
    </div>
  );
};

export default TournamentAdminDetailPage;
