import React, { useState } from 'react';
import { useQuery } from 'react-query';
import { AlertTriangle } from 'lucide-react';

import { Link } from 'react-router-dom';
import {
    Trophy,
    Users,
    Calendar,
    TrendingUp,
    Plus,
    Eye,
    Edit,
    Award,
    BookOpen, // Icon for News
    Info, // For general info/help
    Zap, // For quick actions
    ClipboardList // For registered teams list or match history
} from 'lucide-react';
import { useAuth } from '../context/AuthContext';
import { tournamentService, teamService, userService } from '../services'; // Import userService for user-specific data
import LoadingSpinner from '../components/LoadingSpinner';
import { formatDate, getStatusColor } from '../utils/helpers';
// import TeamCreateModal from '../components/team/TeamCreateModal'; // Nếu không còn chức năng tạo đội ở đây thì có thể xóa hẳn

const DashboardPage = () => {
    const { user } = useAuth();
    // const [isTeamCreateModalOpen, setIsTeamCreateModalOpen] = useState(false); // Xóa nếu không dùng

    // Lấy dữ liệu giải đấu
    // Đối với người dùng thông thường, chúng ta muốn thấy các giải đấu họ đã đăng ký hoặc các giải đấu công khai phổ biến
    // Đối với admin/organizer, có thể là các giải đấu họ tạo/quản lý
    const { data: tournaments, isLoading: tournamentsLoading, error: tournamentsError } = useQuery(
        ['dashboard-tournaments', user?.id], // Phụ thuộc vào user.id để tái fetch nếu user thay đổi
        async () => {
            if (!user) return []; // Không fetch nếu không có user

            // Nếu là ADMIN hoặc ORGANIZER, lấy các giải đấu mà họ có thể quản lý hoặc các giải đấu gần đây nhất
            if (user.role === 'ADMIN' || user.role === 'ORGANIZER') {
                const response = await tournamentService.getAllTournaments({ page: 0, size: 5, sortBy: 'createdAt', sortDirection: 'DESC' });
                return response.data?.content || [];
            } else {
                // Nếu là STUDENT hoặc người dùng thông thường, có thể lấy các giải đấu họ đã tham gia hoặc giải đấu đang hot
                // Hiện tại API getAllTournaments chưa có lọc theo người dùng tham gia, nên tạm lấy 5 giải đấu gần đây nhất.
                // Bạn cần thêm API cho việc lấy giải đấu của người dùng nếu muốn chính xác hơn.
                const response = await tournamentService.getAllTournaments({ page: 0, size: 5, sortBy: 'startDate', sortDirection: 'DESC' });
                return response.data?.content || [];
            }
        },
        { 
            staleTime: 5 * 60 * 1000,
            onError: (err) => {
                console.error('Lỗi khi tải giải đấu cho dashboard:', err);
                // toast.error('Không thể tải danh sách giải đấu.'); // Có thể thêm toast nếu cần thông báo cho người dùng
            },
            // Chỉ chạy query khi user đã được tải
            enabled: !!user, 
        }
    );

    // Lấy số liệu thống kê của người dùng
    const { data: userStats, isLoading: statsLoading, error: statsError } = useQuery(
        ['dashboard-user-stats', user?.id], // Phụ thuộc vào user.id để tái fetch
        async () => {
            if (!user) return {}; // Không fetch nếu không có user

            // **Đây là phần MOCK DATA, bạn cần thay thế bằng các API call thực tế:**
            // Ví dụ:
            // const totalTournaments = (await tournamentService.getTotalTournamentsCount()) || 0; // Cần API này
            // const registeredTeams = (await teamService.getUserTeamsCount(user.id)) || 0; // Cần API này
            // const matchesPlayed = (await userService.getUserMatchesPlayed(user.id)) || 0; // Cần API này
            // const winRate = (await userService.getUserWinRate(user.id)) || '0%'; // Cần API này

            // Để demo, tôi sẽ tiếp tục sử dụng logic giả định và chỉ lấy totalTournaments thật
            const tournamentsResponse = await tournamentService.getAllTournaments({ page: 0, size: 1 }); // Lấy 1 phần tử để có tổng số
            const totalTournamentsCount = tournamentsResponse?.data?.totalElements || 0;

            // Dữ liệu mock (Cần thay thế bằng dữ liệu thật từ API của bạn)
            const mockRegisteredTeams = 12; 
            const mockMatchesPlayed = 45; 
            const mockWinRate = '78%'; 

            return {
                totalTournaments: totalTournamentsCount,
                registeredTeams: mockRegisteredTeams,
                matchesPlayed: mockMatchesPlayed,
                winRate: mockWinRate,
                // ...thêm bất kỳ số liệu thống kê nào khác từ backend
            };
        },
        { 
            staleTime: 5 * 60 * 1000,
            onError: (err) => {
                console.error('Lỗi khi tải thống kê người dùng:', err);
            },
            enabled: !!user,
        }
    );

    const isOrganizerOrAdmin = user?.roles?.includes('ADMIN') || user?.roles?.includes('ORGANIZER');
    const userName = user?.name || user?.email || 'Người dùng'; // Tên mặc định nếu không có

    const stats = [
        {
            name: 'Tổng số giải đấu',
            value: statsLoading ? '...' : userStats?.totalTournaments || 0,
            icon: Trophy,
            color: 'text-orange-500',
            bgColor: 'bg-orange-100',
        },
        {
            name: 'Đội đã đăng ký', // Hoặc "Tổng số đội" nếu là Admin/Organizer
            value: statsLoading ? '...' : userStats?.registeredTeams || 0,
            icon: Users,
            color: 'text-green-500',
            bgColor: 'bg-green-100',
        },
        {
            name: 'Trận đấu đã chơi', // Hoặc "Tổng trận đấu" nếu là Admin/Organizer
            value: statsLoading ? '...' : userStats?.matchesPlayed || 0,
            icon: Calendar,
            color: 'text-purple-500',
            bgColor: 'bg-purple-100',
        },
        {
            name: 'Tỷ lệ thắng', // Chỉ hiển thị cho người chơi
            value: statsLoading ? '...' : userStats?.winRate || '0%',
            icon: TrendingUp,
            color: 'text-pink-500',
            bgColor: 'bg-pink-100',
        },
    ];

    return (
        <div className="min-h-screen bg-gray-50 font-sans">
            <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-10">
                {/* Welcome Header */}
                <div className="mb-10 text-center lg:text-left">
                    <h1 className="text-4xl font-extrabold text-gray-900 mb-2 leading-tight">
                        Chào mừng trở lại, <span className="text-primary-600">{userName}</span>!
                    </h1>
                    <p className="text-xl text-gray-600">
                        Nơi bắt đầu mọi hành trình thể thao của bạn.
                    </p>
                </div>

                {/* Stats Grid */}
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-12">
                    {stats.map((stat) => (
                        <div key={stat.name} className="bg-white rounded-xl shadow-lg p-6 flex items-center transform transition-transform hover:scale-105 hover:shadow-2xl">
                            <div className={`${stat.bgColor} p-4 rounded-xl flex-shrink-0`}>
                                <stat.icon className={`h-8 w-8 ${stat.color}`} />
                            </div>
                            <div className="ml-5">
                                <p className="text-base font-medium text-gray-600">{stat.name}</p>
                                <p className="text-3xl font-bold text-gray-900 mt-1">{stat.value}</p>
                            </div>
                        </div>
                    ))}
                </div>

                <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
                    {/* Recent Tournaments */}
                    <div className="bg-white rounded-xl shadow-lg p-8">
                        <div className="flex items-center justify-between mb-6">
                            <h2 className="text-2xl font-bold text-gray-900">
                                {isOrganizerOrAdmin ? 'Giải đấu của bạn' : 'Giải đấu gần đây'}
                            </h2>
                            <div className="flex space-x-3">
                                {isOrganizerOrAdmin && (
                                    <Link
                                        to="/admin/tournaments/new"
                                        className="inline-flex items-center px-4 py-2 border border-transparent text-sm leading-5 font-medium rounded-lg text-white bg-primary-600 hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 transition-colors duration-200"
                                    >
                                        <Plus className="h-4 w-4 mr-2" />
                                        Tạo mới
                                    </Link>
                                )}
                                <Link
                                    to="/tournaments"
                                    className="inline-flex items-center px-4 py-2 border border-gray-300 text-sm leading-5 font-medium rounded-lg text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 transition-colors duration-200"
                                >
                                    <Eye className="h-4 w-4 mr-2" />
                                    Xem tất cả
                                </Link>
                            </div>
                        </div>

                        {tournamentsLoading ? (
                            <div className="flex justify-center items-center py-10">
                                <LoadingSpinner />
                            </div>
                        ) : tournamentsError ? (
                            <div className="text-center py-10 text-red-600">
                                <AlertTriangle className="h-10 w-10 mx-auto mb-4" />
                                <p>Không thể tải giải đấu. Vui lòng thử lại.</p>
                            </div>
                        ) : (tournaments || []).length === 0 ? (
                            <div className="text-center py-10">
                                <Trophy className="h-16 w-16 text-gray-400 mx-auto mb-4" />
                                <p className="text-gray-600 text-lg">Không tìm thấy giải đấu nào.</p>
                                {isOrganizerOrAdmin && (
                                    <Link
                                        to="/admin/tournaments/new"
