import React, { useState, useCallback, useRef, useMemo } from 'react';
import { useForm } from 'react-hook-form';
import { useMutation, useQueryClient } from 'react-query';
import { X, Trophy, Eye, EyeOff } from 'lucide-react';
import { tournamentService } from '../../services';
import { 
  SPORT_TYPES, 
  SPORT_TYPE_LABELS,
  TOURNAMENT_STATUS,
  VALIDATION_RULES,
  QUERY_KEYS,
  STORAGE_KEYS
} from '../../utils/constants';
import { formatDateTimeForInput } from '../../utils/helpers';

const TournamentCreateFormAdvancedDebug = ({ isOpen, onClose, onSuccess }) => {
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState('');
  const [debugInfo, setDebugInfo] = useState([]);
  const [showDebug, setShowDebug] = useState(true);
  const [networkLogs, setNetworkLogs] = useState([]);
  const formRef = useRef(null);
  const queryClient = useQueryClient();

  const addDebugLog = useCallback((message, data = null) => {
    const timestamp = new Date().toLocaleTimeString();
    const logEntry = {
      id: Date.now(),
      timestamp,
      message,
      data: data ? JSON.stringify(data, null, 2) : null,
      type: 'debug'
    };

    console.log(`🐛 [DEBUG ${timestamp}] ${message}`, data || '');
    setDebugInfo(prev => [...prev.slice(-25), logEntry]);
  }, []);

  const addNetworkLog = useCallback((type, url, status, data = null) => {
    const timestamp = new Date().toLocaleTimeString();
    const logEntry = {
      id: Date.now(),
      timestamp,
      type,
      url,
      status,
      data: data ? JSON.stringify(data, null, 2) : null
    };

    console.log(`🌐 [NETWORK ${timestamp}] ${type} ${url} - ${status}`, data || '');
    setNetworkLogs(prev => [...prev.slice(-15), logEntry]);
  }, []);

  // Intercept all fetch requests to monitor API calls
  const originalFetch = useRef(window.fetch);

  React.useEffect(() => {
    window.fetch = async (...args) => {
      const [url, options] = args;
      addNetworkLog('REQUEST', url, 'PENDING', {
        method: options?.method || 'GET',
        headers: options?.headers,
        body: options?.body
      });

      try {
        const response = await originalFetch.current(...args);
        const clonedResponse = response.clone();

        let responseData = null;
        try {
          responseData = await clonedResponse.json();
        } catch (e) {
          responseData = await clonedResponse.text();
        }

        addNetworkLog('RESPONSE', url, response.status, {
          ok: response.ok,
          status: response.status,
          statusText: response.statusText,
          headers: Object.fromEntries(response.headers.entries()),
          data: responseData
        });

        return response;
      } catch (error) {
        addNetworkLog('ERROR', url, 'FAILED', {
          error: error.message,
          stack: error.stack
        });
        throw error;
      }
    };

    return () => {
      window.fetch = originalFetch.current;
    };
  }, [addNetworkLog]);

  const {
    register,
    handleSubmit,
    watch,
    reset,
    formState: { errors: formErrors }
  } = useForm({
    defaultValues: {
      name: '',
      description: '',
      sportType: 'FOOTBALL',
      maxTeams: 16,
      startDate: '',
      endDate: '',
      registrationDeadline: '',
      location: '',
      rules: '',
      prizeInfo: '',
      contactInfo: ''
    },
    mode: 'onChange'
  });

  const showError = useCallback((message) => {
    console.error('❌ ERROR:', message);
    setError(message);
    addDebugLog('ERROR OCCURRED', { error: message });

    setTimeout(() => {
      alert(`❌ ${message}`);
    }, 50);
  }, [addDebugLog]);

  const createTournamentMutation = useMutation(
    async (tournamentData) => {
      addDebugLog('=== MUTATION CYCLE START ===');
      addDebugLog('MUTATION STARTED', tournamentData);

      // Comprehensive auth check
      const token = localStorage.getItem(STORAGE_KEYS.ACCESS_TOKEN);
      const allStorageKeys = Object.keys(localStorage);
      const storageSnapshot = {};
      allStorageKeys.forEach(key => {
        storageSnapshot[key] = localStorage.getItem(key);
      });

      addDebugLog('STORAGE ANALYSIS', {
        targetKey: STORAGE_KEYS.ACCESS_TOKEN,
        hasToken: !!token,
        tokenLength: token ? token.length : 0,
        tokenPreview: token ? `${token.substring(0, 30)}...` : 'No token',
        allKeys: allStorageKeys,
        fullStorage: storageSnapshot
      });

      if (!token) {
        addDebugLog('AUTHENTICATION FAILED - NO TOKEN');
        throw new Error('No authentication token found');
      }

      // Token validation
      try {
        const tokenParts = token.split('.');
        if (tokenParts.length !== 3) {
          addDebugLog('TOKEN FORMAT ERROR', { parts: tokenParts.length });
          throw new Error('Invalid token format');
        }

        const payload = JSON.parse(atob(tokenParts[1]));
        const now = Math.floor(Date.now() / 1000);

        addDebugLog('TOKEN ANALYSIS', {
          isJWT: true,
          payload: payload,
          isExpired: payload.exp < now,
          expiresAt: new Date(payload.exp * 1000).toISOString(),
          currentTime: new Date(now * 1000).toISOString()
        });

        if (payload.exp < now) {
          addDebugLog('TOKEN EXPIRED');
          throw new Error('Token has expired');
        }
      } catch (tokenError) {
        addDebugLog('TOKEN VALIDATION ERROR', { error: tokenError.message });
      }

      // Format data for backend (array format)
      const formatArray = (date) => {
        const d = new Date(date);
        return [d.getFullYear(), d.getMonth() + 1, d.getDate(), 
               d.getHours(), d.getMinutes(), d.getSeconds()];
      };

      const dataWithFormattedDates = {
        ...tournamentData,
        startDate: formatArray(tournamentData.startDate),
        endDate: formatArray(tournamentData.endDate),
        registrationDeadline: formatArray(tournamentData.registrationDeadline)
      };

      addDebugLog('FORMATTED DATA FOR BACKEND', dataWithFormattedDates);

      try {
        addDebugLog('CALLING TOURNAMENT SERVICE...');
        addDebugLog('PRE-API CALL STORAGE CHECK', {
          tokenStillExists: !!localStorage.getItem(STORAGE_KEYS.ACCESS_TOKEN),
          storageKeys: Object.keys(localStorage)
        });

        const result = await tournamentService.createTournament(dataWithFormattedDates);

        addDebugLog('POST-API CALL STORAGE CHECK', {
          tokenStillExists: !!localStorage.getItem(STORAGE_KEYS.ACCESS_TOKEN),
          storageKeys: Object.keys(localStorage)
        });

        addDebugLog('SERVICE CALL SUCCESS', result);
        addDebugLog('=== MUTATION CYCLE SUCCESS ===');
        return result;
      } catch (apiError) {
        addDebugLog('SERVICE CALL ERROR', {
          message: apiError.message,
          status: apiError.response?.status,
          statusText: apiError.response?.statusText,
          data: apiError.response?.data,
          isAuthError: apiError.isAuthError,
          isServerError: apiError.isServerError,
          tokenAfterError: !!localStorage.getItem(STORAGE_KEYS.ACCESS_TOKEN)
        });
        addDebugLog('=== MUTATION CYCLE ERROR ===');
        throw apiError;
      }
    },
    {
      onSuccess: (response) => {
        addDebugLog('=== SUCCESS HANDLER START ===');
        addDebugLog('MUTATION SUCCESS CALLBACK', response);

        // Check what's in the response
        const tournamentId = response?.id || response?.data?.id || response?.tournament?.id;
        addDebugLog('RESPONSE ANALYSIS', {
          hasId: !!tournamentId,
          tournamentId: tournamentId,
          responseKeys: Object.keys(response || {}),
          fullResponse: response
        });

        // Check storage after success
        addDebugLog('POST-SUCCESS STORAGE CHECK', {
          tokenExists: !!localStorage.getItem(STORAGE_KEYS.ACCESS_TOKEN),
          allKeys: Object.keys(localStorage)
        });

        if (tournamentId) {
          addDebugLog('TOURNAMENT ID CONFIRMED', { 
            tournamentId: tournamentId,
            dataConfirmedSaved: true
          });

          alert(`✅ Tournament created successfully! ID: ${tournamentId}\n\nData saved to database!`);

          // Check for any pending redirects
          addDebugLog('CHECKING FOR REDIRECTS');

          // Manual refresh after delay to see if tournament appears
          setTimeout(() => {
            addDebugLog('INITIATING MANUAL REFRESH');
            addDebugLog('FINAL STORAGE CHECK BEFORE REFRESH', {
              token: !!localStorage.getItem(STORAGE_KEYS.ACCESS_TOKEN)
            });
            window.location.reload();
          }, 3000);
        } else {
          addDebugLog('WARNING: No tournament ID found in response');
          alert('⚠️ Response received but no tournament ID found');
        }

        addDebugLog('=== SUCCESS HANDLER END ===');
      },
      onError: (error) => {
        addDebugLog('=== ERROR HANDLER START ===');
        addDebugLog('MUTATION ERROR CALLBACK', {
          message: error.message,
          status: error.statusCode || error.response?.status,
          data: error.errorData || error.response?.data,
          isAuthError: error.isAuthError,
          isServerError: error.isServerError,
          isNetworkError: error.isNetworkError,
          tokenAfterError: !!localStorage.getItem(STORAGE_KEYS.ACCESS_TOKEN)
        });

        if (error.isAuthError || error.statusCode === 401) {
          addDebugLog('AUTH ERROR DETECTED - REDIRECT INCOMING');
          showError('Authentication error detected. You will be redirected to login.');
        } else if (error.isServerError) {
          showError(`Server error (${error.statusCode}): ${error.errorMessage}`);
        } else {
          showError(error.errorMessage || error.message || 'Unknown error occurred');
        }

        setIsSubmitting(false);
        addDebugLog('=== ERROR HANDLER END ===');
      }
    }
  );

  const watchStartDate = watch('startDate');

  const onSubmit = async (data) => {
    if (isSubmitting) return;

    try {
      addDebugLog('=== FORM SUBMISSION START ===');
      addDebugLog('FORM SUBMISSION STARTED', data);
      setIsSubmitting(true);
      setError('');

      // Basic validation
      if (!data.name?.trim() || !data.description?.trim() || 
          !data.location?.trim() || !data.contactInfo?.trim()) {
        showError('Please fill in all required fields');
        setIsSubmitting(false);
        return;
      }

      if (!data.startDate || !data.endDate || !data.registrationDeadline) {
        showError('Please select all dates');
        setIsSubmitting(false);
        return;
      }

      const startDate = new Date(data.startDate);
      const endDate = new Date(data.endDate);
      const regDeadline = new Date(data.registrationDeadline);
      const now = new Date();

      addDebugLog('DATE VALIDATION', {
        startDate: startDate.toISOString(),
        endDate: endDate.toISOString(),
        regDeadline: regDeadline.toISOString(),
        now: now.toISOString()
      });

      // Date validation
      const nowPlus1Min = new Date(now.getTime() + 60000);
      if (regDeadline <= nowPlus1Min) {
        showError('Registration deadline must be after current time');
        setIsSubmitting(false);
        return;
      }

      if (regDeadline >= startDate) {
        showError('Registration deadline must be before start date');
        setIsSubmitting(false);
        return;
      }

      if (startDate >= endDate) {
        showError('End date must be after start date');
        setIsSubmitting(false);
        return;
      }

      // Prepare tournament data
      const tournamentData = {
        name: data.name.trim(),
        description: data.description.trim(),
        sportType: data.sportType || 'FOOTBALL',
        maxTeams: parseInt(data.maxTeams) || 16,
        startDate: startDate,
        endDate: endDate,
        registrationDeadline: regDeadline,
        location: data.location.trim(),
        rules: data.rules?.trim() || '',
        prizeInfo: data.prizeInfo?.trim() || '',
        contactInfo: data.contactInfo.trim()
      };

      addDebugLog('TOURNAMENT DATA PREPARED', tournamentData);

      await createTournamentMutation.mutateAsync(tournamentData);

    } catch (error) {
      addDebugLog('FORM SUBMISSION ERROR', {
        message: error.message,
        stack: error.stack
      });
      setIsSubmitting(false);
      addDebugLog('=== FORM SUBMISSION ERROR END ===');
    }
  };

  const handleClose = useCallback(() => {
    addDebugLog('FORM CLOSING');
    setIsSubmitting(false);
    setError('');
    reset();

    setTimeout(() => {
      if (onClose && typeof onClose === 'function') {
        onClose();
      }
    }, 50);
  }, [reset, onClose, addDebugLog]);

  // Min dates calculation
  const now = useMemo(() => new Date(), []);
  const currentPlus10Min = useMemo(() => new Date(now.getTime() + 10 * 60000), [now]);
  const tomorrow = useMemo(() => {
    const date = new Date(now);
    date.setDate(now.getDate() + 1);
    return date;
  }, [now]);

  const minStartDate = useMemo(() => formatDateTimeForInput(tomorrow), [tomorrow]);
  const minEndDate = useMemo(() => {
    if (!watchStartDate) return minStartDate;
    try {
      const startDateObj = new Date(watchStartDate);
      const nextDay = new Date(startDateObj.getTime() + 24 * 60 * 60 * 1000);
      return formatDateTimeForInput(nextDay);
    } catch (error) {
      return minStartDate;
    }
  }, [watchStartDate, minStartDate]);

  const minRegDeadline = useMemo(() => formatDateTimeForInput(currentPlus10Min), [currentPlus10Min]);

  const maxRegDeadline = useMemo(() => {
    if (!watchStartDate) return '';
    try {
      const startDate = new Date(watchStartDate);
      const maxRegDate = new Date(startDate.getTime() - 24 * 60 * 60 * 1000);

      if (maxRegDate > currentPlus10Min) {
        return formatDateTimeForInput(maxRegDate);
      }
      return '';
    } catch (error) {
      return '';
    }
  }, [watchStartDate, currentPlus10Min]);

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-xl shadow-xl max-w-7xl w-full max-h-[90vh] overflow-hidden flex">
        {/* Main Form */}
        <div className="flex-1 flex flex-col">
          {/* Header */}
          <div className="flex items-center justify-between p-6 border-b border-gray-200">
            <div className="flex items-center space-x-3">
              <div className="bg-orange-100 p-2 rounded-lg">
                <Trophy className="h-6 w-6 text-orange-600" />
              </div>
              <div>
                <h2 className="text-xl font-bold text-gray-900">Advanced Debug Mode</h2>
                <p className="text-sm text-gray-600">Full request/response cycle monitoring</p>
              </div>
            </div>
            <div className="flex items-center space-x-2">
              <button
                onClick={() => setShowDebug(!showDebug)}
                className="text-gray-400 hover:text-gray-600 transition-colors"
                title="Toggle Debug Panel"
              >
                {showDebug ? <EyeOff className="h-5 w-5" /> : <Eye className="h-5 w-5" />}
              </button>
              <button
                onClick={handleClose}
                className="text-gray-400 hover:text-gray-600 transition-colors"
                disabled={isSubmitting}
              >
                <X className="h-6 w-6" />
              </button>
            </div>
          </div>

          {/* Error notification */}
          {error && (
            <div className="p-4 border-b bg-red-50 border-red-200 text-red-800">
              <div className="flex items-center">
                <span className="text-sm font-medium">❌ {error}</span>
              </div>
            </div>
          )}

          {/* Simple Form for Testing */}
          <form 
            ref={formRef}
            onSubmit={handleSubmit(onSubmit)} 
            className="flex-1 overflow-y-auto"
          >
            <div className="p-6 space-y-4">
              {/* Basic fields only for quick testing */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Tournament Name *</label>
                <input
                  type="text"
                  {...register('name', { required: 'Name is required' })}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md"
                  placeholder="Test Tournament"
                  disabled={isSubmitting}
                />
                {formErrors.name && <p className="text-red-600 text-sm mt-1">{formErrors.name.message}</p>}
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Description *</label>
                <textarea
                  {...register('description', { required: 'Description is required' })}
                  rows={2}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md"
                  placeholder="Test tournament description"
                  disabled={isSubmitting}
                />
                {formErrors.description && <p className="text-red-600 text-sm mt-1">{formErrors.description.message}</p>}
              </div>

              <div className="grid grid-cols-3 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Start Date *</label>
                  <input
                    type="datetime-local"
                    {...register('startDate', { required: 'Start date is required' })}
                    min={minStartDate}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md"
                    disabled={isSubmitting}
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">End Date *</label>
                  <input
                    type="datetime-local"
                    {...register('endDate', { required: 'End date is required' })}
                    min={minEndDate}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md"
                    disabled={isSubmitting}
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Registration Deadline *</label>
                  <input
                    type="datetime-local"
                    {...register('registrationDeadline', { required: 'Registration deadline is required' })}
                    min={minRegDeadline}
                    max={maxRegDeadline}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md"
                    disabled={isSubmitting}
                  />
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Location *</label>
                  <input
                    type="text"
                    {...register('location', { required: 'Location is required' })}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md"
                    placeholder="Test Stadium"
                    disabled={isSubmitting}
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Contact Info *</label>
                  <input
                    type="text"
                    {...register('contactInfo', { required: 'Contact info is required' })}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md"
                    placeholder="test@example.com"
                    disabled={isSubmitting}
                  />
                </div>
              </div>
            </div>

            {/* Footer */}
            <div className="flex items-center justify-end space-x-3 p-6 border-t border-gray-200 bg-gray-50">
              <button
                type="button"
                onClick={handleClose}
                className="px-4 py-2 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-md hover:bg-gray-50"
                disabled={isSubmitting}
              >
                Cancel
              </button>
              <button
                type="submit"
                className="px-4 py-2 text-sm font-medium text-white bg-blue-600 border border-transparent rounded-md hover:bg-blue-700 flex items-center"
                disabled={isSubmitting}
              >
                {isSubmitting ? (
                  <>
                    <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                    Creating...
                  </>
                ) : (
                  <>
                    <Trophy className="h-4 w-4 mr-2" />
                    Create Tournament (Debug)
                  </>
                )}
              </button>
            </div>
          </form>
        </div>

        {/* Advanced Debug Panel */}
        {showDebug && (
          <div className="w-1/2 border-l border-gray-200 bg-gray-50 flex flex-col">
            <div className="p-4 border-b border-gray-200">
              <h3 className="text-lg font-semibold text-gray-900">Advanced Debug Console</h3>
              <p className="text-sm text-gray-600">Request/Response cycle monitoring</p>
            </div>

            <div className="flex-1 overflow-hidden flex flex-col">
              {/* Debug Logs */}
              <div className="flex-1 overflow-y-auto p-4 space-y-2">
                <h4 className="font-medium text-gray-800 mb-2">Debug Logs:</h4>
                {debugInfo.length === 0 ? (
                  <p className="text-sm text-gray-500">Waiting for debug logs...</p>
                ) : (
                  debugInfo.map(log => (
                    <div key={log.id} className="bg-white rounded p-2 shadow-sm">
                      <div className="flex items-center justify-between mb-1">
                        <span className="text-xs font-mono text-gray-500">{log.timestamp}</span>
                      </div>
                      <div className="text-sm font-medium text-gray-800 mb-1">
                        {log.message}
                      </div>
                      {log.data && (
                        <pre className="text-xs text-gray-600 bg-gray-100 p-1 rounded overflow-x-auto max-h-32">
                          {log.data}
                        </pre>
                      )}
                    </div>
                  ))
                )}
              </div>

              {/* Network Logs */}
              <div className="border-t border-gray-200 p-4 max-h-64 overflow-y-auto">
                <h4 className="font-medium text-gray-800 mb-2">Network Logs:</h4>
                {networkLogs.length === 0 ? (
                  <p className="text-sm text-gray-500">No network activity yet...</p>
                ) : (
                  networkLogs.map(log => (
                    <div key={log.id} className="bg-white rounded p-2 shadow-sm mb-2">
                      <div className="flex items-center justify-between mb-1">
                        <span className="text-xs font-mono text-gray-500">{log.timestamp}</span>
                        <span className={`text-xs px-2 py-1 rounded ${
                          log.status === 'PENDING' ? 'bg-yellow-100 text-yellow-800' :
                          log.status >= 200 && log.status < 300 ? 'bg-green-100 text-green-800' :
                          log.status >= 400 ? 'bg-red-100 text-red-800' :
                          'bg-gray-100 text-gray-800'
                        }`}>
                          {log.status}
                        </span>
                      </div>
                      <div className="text-sm font-medium text-gray-800 mb-1">
                        {log.type} {log.url}
                      </div>
                      {log.data && (
                        <pre className="text-xs text-gray-600 bg-gray-100 p-1 rounded overflow-x-auto max-h-24">
                          {log.data}
                        </pre>
                      )}
                    </div>
                  ))
                )}
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default TournamentCreateFormAdvancedDebug;
