import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useQuery } from 'react-query';
import { Trophy, Users, Calendar, Target, ArrowRight, Play, Star, Clock, MapPin, User, Zap, Award, TrendingUp } from 'lucide-react';
import { tournamentServiceFixed as tournamentService } from '../services/tournamentServiceFixed';
import newsService from '../services/newsService';
import { dashboardService } from '../services';
import LoadingSpinner from '../components/LoadingSpinner';
import { formatDate, getStatusColor } from '../utils/helpers';

// Import NewsDetailModal
import NewsDetailModal from '../components/admin/NewsDetailModal';

const HomePage = () => {
  console.log('ðŸ  [HomePage] Component mounting at', new Date().toLocaleTimeString());

  const [selectedNewsArticle, setSelectedNewsArticle] = useState(null);
  const [isNewsDetailModalOpen, setIsNewsDetailModalOpen] = useState(false);

  useEffect(() => {
    console.log('ðŸ  [HomePage] useEffect running at', new Date().toLocaleTimeString());

    const currentPath = window.location.pathname;
    console.log('ðŸ“ [HomePage] Current path:', currentPath);

    const handleUrlChange = () => {
      console.log('âš ï¸ [HomePage] URL changed to:', window.location.pathname, 'at', new Date().toLocaleTimeString());
    };

    window.addEventListener('popstate', handleUrlChange);

    return () => {
      console.log('ðŸ  [HomePage] Component unmounting at', new Date().toLocaleTimeString());
      window.removeEventListener('popstate', handleUrlChange);
    };
  }, []);

  // Get dashboard statistics
  const { data: dashboardStats, isLoading: statsLoading } = useQuery(
      'dashboard-stats',
      () => dashboardService.getDashboardStats(),
      {
        staleTime: 2 * 60 * 1000,
        onSuccess: (data) => {
          console.log('ðŸ“Š [HomePage] Dashboard stats loaded at', new Date().toLocaleTimeString());
        },
        select: (response) => {
          console.log('ðŸ“Š [HomePage] Dashboard Stats Response:', response);
          return response?.data || null;
        },
        onError: (error) => {
          console.warn('âš ï¸ [HomePage] Dashboard stats API failed, using mock data:', error);
        }
      }
  );

  const { data: tournaments, isLoading: tournamentsLoading } = useQuery(
      ['tournaments', { page: 1, limit: 3 }],
      () => tournamentService.getAllTournaments({ page: 1, limit: 3 }),
      {
        staleTime: 5 * 60 * 1000,
        select: (response) => {
          console.log('ðŸ† [HomePage] Tournaments API Response:', response);

          if (response && Array.isArray(response.data)) {
            console.log('ðŸ† [HomePage] Found tournaments:', response.data.length);
            return response.data;
          }

          if (Array.isArray(response)) {
            return response;
          }
          if (response?.data && Array.isArray(response.data)) {
            return response.data;
          }
          if (response?.data?.content && Array.isArray(response.data.content)) {
            return response.data.content;
          }
          if (response?.data?.tournaments && Array.isArray(response.data.tournaments)) {
            return response.data.tournaments;
          }

          console.warn('âš ï¸ [HomePage] Tournaments data is not an array:', response);
          return [];
        }
      }
  );

  const { data: news, isLoading: newsLoading, error: newsError } = useQuery(
      'recent-news',
      () => newsService.getAllNews(),
      {
        staleTime: 5 * 60 * 1000,
        select: (data) => {
          let newsArray = [];

          if (Array.isArray(data)) {
            newsArray = data;
          } else if (data?.data && Array.isArray(data.data)) {
            newsArray = data.data;
          } else if (data?.content && Array.isArray(data.content)) {
            newsArray = data.content;
          } else if (data?.news && Array.isArray(data.news)) {
            newsArray = data.news;
          } else {
            if (data && typeof data === 'object') {
              const keys = Object.keys(data);
              for (const key of keys) {
                if (Array.isArray(data[key])) {
                  newsArray = data[key];
                  break;
                }
              }
            }
          }

          return newsArray.slice(0, 3);
        },
        onError: (error) => {
          console.error('Error loading news:', error);
        }
      }
  );

  // HÃ m má»Ÿ modal chi tiáº¿t tin tá»©c
  const openNewsDetailModal = (article) => {
    setSelectedNewsArticle(article);
    setIsNewsDetailModalOpen(true);
    document.body.style.overflow = 'hidden';
  };

  // HÃ m Ä‘Ã³ng modal chi tiáº¿t tin tá»©c
  const closeNewsDetailModal = () => {
    setIsNewsDetailModalOpen(false);
    setSelectedNewsArticle(null);
    document.body.style.overflow = 'unset';
  };

  // Xá»­ lÃ½ Escape key cho News Detail Modal
  useEffect(() => {
    const handleEscape = (e) => {
      if (e.key === 'Escape' && isNewsDetailModalOpen) {
        closeNewsDetailModal();
      }
    };

    document.addEventListener('keydown', handleEscape);
    return () => document.removeEventListener('keydown', handleEscape);
  }, [isNewsDetailModalOpen]);

  const stats = React.useMemo(() => {
    if (dashboardStats) {
      return [
        {
          icon: Trophy,
          label: 'Giáº£i Ä‘ang diá»…n ra',
          value: dashboardStats.activeTournaments?.toString() || '0',
          color: 'text-sports-orange'
        },
        {
          icon: Users,
          label: 'Äá»™i tham gia',
          value: dashboardStats.registeredTeams?.toString() || '0',
          color: 'text-sports-green'
        },
        {
          icon: Calendar,
          label: 'Tráº­n Ä‘Ã£ thi Ä‘áº¥u',
          value: dashboardStats.matchesPlayed?.toString() || '0',
          color: 'text-sports-purple'
        },
        {
          icon: Target,
          label: 'Tá»· lá»‡ thÃ nh cÃ´ng',
          value: dashboardStats.successRate ? `${Math.round(dashboardStats.successRate)}%` : '0%',
          color: 'text-sports-pink'
        },
      ];
    }

    return [
      { icon: Trophy, label: 'Giáº£i Ä‘ang diá»…n ra', value: '-', color: 'text-sports-orange' },
      { icon: Users, label: 'Äá»™i tham gia', value: '-', color: 'text-sports-green' },
      { icon: Calendar, label: 'Tráº­n Ä‘Ã£ thi Ä‘áº¥u', value: '-', color: 'text-sports-purple' },
      { icon: Target, label: 'Tá»· lá»‡ thÃ nh cÃ´ng', value: '-', color: 'text-sports-pink' },
    ];
  }, [dashboardStats]);

  const features = [
    {
      title: 'Quáº£n lÃ½ Ä‘a mÃ´n thá»ƒ thao',
      description: 'Táº¡o vÃ  quáº£n lÃ½ cÃ¡c hoáº¡t Ä‘á»™ng thá»ƒ thao Ä‘a dáº¡ng. Tá»« bÃ³ng Ä‘Ã¡, bÃ³ng rá»• Ä‘áº¿n cáº§u lÃ´ng, bÃ³ng chuyá»n.',
      icon: Trophy,
    },
    {
      title: 'ÄÄƒng kÃ½ váº­n Ä‘á»™ng viÃªn',
      description: 'Quy trÃ¬nh Ä‘Äƒng kÃ½ váº­n Ä‘á»™ng viÃªn vÃ  Ä‘á»™i thi Ä‘áº¥u Ä‘Æ¡n giáº£n vá»›i quáº£n lÃ½ thÃ´ng tin chi tiáº¿t.',
      icon: Users,
    },
    {
      title: 'Theo dÃµi káº¿t quáº£',
      description: 'Cáº­p nháº­t káº¿t quáº£ thi Ä‘áº¥u theo thá»i gian thá»±c cho táº¥t cáº£ cÃ¡c mÃ´n thá»ƒ thao.',
      icon: Play,
    },
    {
      title: 'Thá»‘ng kÃª & Xáº¿p háº¡ng',
      description: 'Thá»‘ng kÃª toÃ n diá»‡n vÃ  báº£ng xáº¿p háº¡ng cho cÃ¡c mÃ´n thá»ƒ thao vÃ  váº­n Ä‘á»™ng viÃªn.',
      icon: Target,
    },
  ];

  // Mock data cho cÃ¡c sá»± kiá»‡n thá»ƒ thao sáº¯p tá»›i
  const upcomingEvents = [
    {
      id: 1,
      homeTeam: 'Äáº¡i há»c BÃ¡ch Khoa',
      awayTeam: 'Äáº¡i há»c Quá»‘c Gia',
      sport: 'BÃ³ng Ä‘Ã¡',
      date: '2025-06-01',
      time: '14:00',
      venue: 'SÃ¢n váº­n Ä‘á»™ng Quá»‘c Gia'
    },
    {
      id: 2,
      homeTeam: 'Äáº¡i há»c Kinh Táº¿',
      awayTeam: 'Äáº¡i há»c Ngoáº¡i ThÆ°Æ¡ng',
      sport: 'BÃ³ng rá»•',
      date: '2025-06-02',
      time: '16:00',
      venue: 'NhÃ  thi Ä‘áº¥u ThÃ nh phá»‘'
    },
    {
      id: 3,
      homeTeam: 'Äáº¡i há»c Y',
      awayTeam: 'Äáº¡i há»c Luáº­t',
      sport: 'Cáº§u lÃ´ng',
      date: '2025-06-03',
      time: '09:00',
      venue: 'Cung thá»ƒ thao'
    }
  ];

  // Mock data cho káº¿t quáº£ má»›i nháº¥t
  const recentResults = [
    {
      id: 1,
      homeTeam: 'Äáº¡i há»c Y',
      awayTeam: 'Äáº¡i há»c Luáº­t',
      homeScore: 2,
      awayScore: 1,
      sport: 'BÃ³ng Ä‘Ã¡',
      date: '2025-05-28'
    },
    {
      id: 2,
      homeTeam: 'Äáº¡i há»c SÆ° Pháº¡m',
      awayTeam: 'Äáº¡i há»c CÃ´ng Nghá»‡',
      homeScore: 78,
      awayScore: 82,
      sport: 'BÃ³ng rá»•',
      date: '2025-05-27'
    },
    {
      id: 3,
      homeTeam: 'Äáº¡i há»c Ngoáº¡i ThÆ°Æ¡ng',
      awayTeam: 'Äáº¡i há»c Kinh Táº¿',
      homeScore: 21,
      awayScore: 15,
      sport: 'Cáº§u lÃ´ng',
      date: '2025-05-26'
    }
  ];

  return (
      <div className="min-h-screen">
        {/* NewsDetailModal */}
        <NewsDetailModal
            newsItem={selectedNewsArticle}
            show={isNewsDetailModalOpen}
            onClose={closeNewsDetailModal}
        />

        {/* Hero Section vá»›i áº£nh sÃ¢n thá»ƒ thao tá»•ng há»£p */}
        <section className="relative min-h-screen flex items-center overflow-hidden">
          {/* Background Image - Stadium with multiple sports */}
          <div 
            className="absolute inset-0 bg-cover bg-center bg-no-repeat transition-transform duration-20000 hover:scale-105"
            style={{
              backgroundImage: `url('https://images.unsplash.com/photo-1544737151406-6a997e3450fe?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2070&q=80')`
            }}
          >
            <div className="absolute inset-0 bg-gradient-to-r from-black/70 via-purple-900/30 to-black/70"></div>
          </div>

          {/* Floating particles */}
          <div className="particles">
            {[...Array(20)].map((_, i) => (
              <div
                key={i}
                className="particle"
                style={{
                  left: `${Math.random() * 100}%`,
                  width: `${Math.random() * 10 + 5}px`,
                  height: `${Math.random() * 10 + 5}px`,
                  animationDelay: `${Math.random() * 10}s`,
                  animationDuration: `${Math.random() * 10 + 10}s`
                }}
              />
            ))}
          </div>

          <div className="relative max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-20 z-10">
            <div className="text-center text-white">
              <h1 className="text-5xl md:text-7xl font-bold mb-6 animate-fade-in">
                Há»‡ thá»‘ng quáº£n lÃ½ thá»ƒ thao{' '}
                <span className="bg-gradient-to-r from-orange-500 via-red-500 to-pink-500 bg-clip-text text-transparent animate-pulse">
                  EduSports
                </span>
              </h1>
              <p className="text-xl md:text-2xl mb-8 text-gray-200 max-w-3xl mx-auto animate-slide-up">
                Há»‡ thá»‘ng quáº£n lÃ½ cÃ¡c hoáº¡t Ä‘á»™ng thá»ƒ thao tá»•ng há»£p cho cÃ¡c trÆ°á»ng Ä‘áº¡i há»c.
                Tá»• chá»©c, thi Ä‘áº¥u vÃ  tÃ´n vinh thÃ nh tÃ­ch thá»ƒ thao Ä‘a dáº¡ng.
              </p>
              <div className="flex flex-col sm:flex-row gap-4 justify-center animate-slide-up">
                <Link
                    to="/tournaments"
                    className="group bg-gradient-to-r from-orange-600 via-red-600 to-pink-600 hover:from-orange-700 hover:via-red-700 hover:to-pink-700 text-white font-bold py-4 px-8 rounded-lg transition-all duration-300 transform hover:scale-105 hover:shadow-2xl flex items-center justify-center space-x-2 animate-glow"
                >
                  <Trophy className="h-5 w-5 group-hover:animate-bounce-slow" />
                  <span>Xem cÃ¡c mÃ´n thá»ƒ thao</span>
                </Link>
                <Link
                    to="/register"
                    className="group glass-effect text-white hover:bg-white/20 font-bold py-4 px-8 rounded-lg transition-all duration-300 transform hover:scale-105 hover:shadow-2xl flex items-center justify-center space-x-2"
                >
                  <Users className="h-5 w-5 group-hover:animate-float" />
                  <span>Tham gia ngay</span>
                </Link>
              </div>
            </div>
          </div>
        </section>

        {/* Stats Section */}
        <section className="py-16 bg-gradient-to-br from-gray-50 to-blue-50">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            {statsLoading ? (
                <div className="text-center">
                  <LoadingSpinner />
                  <p className="mt-4 text-gray-600">Äang táº£i thá»‘ng kÃª...</p>
                </div>
            ) : (
                <div className="grid grid-cols-2 md:grid-cols-4 gap-8">
                  {stats.map((stat, index) => (
                      <div key={index} className="text-center group animate-scale-hover">
                        <div className="flex justify-center mb-4">
                          <div className={`p-4 rounded-full transform transition-all duration-300 group-hover:scale-110 ${
                            index === 0 ? 'sport-gradient' :
                            index === 1 ? 'sport-gradient-2' :
                            index === 2 ? 'sport-gradient-3' :
                            'bg-gradient-to-br from-purple-500 to-indigo-600'
                          }`}>
                            <stat.icon className="h-8 w-8 text-white group-hover:animate-bounce-slow" />
                          </div>
                        </div>
                        <div className="text-3xl font-bold text-gray-900 mb-2 group-hover:text-blue-600 transition-colors duration-300">
                          {stat.value}
                        </div>
                        <div className="text-gray-600 group-hover:text-gray-700 transition-colors duration-300">
                          {stat.label}
                        </div>
                      </div>
                  ))}
                </div>
