import React, { useState, useCallback, useRef, useEffect, useMemo } from 'react';
import { useForm } from 'react-hook-form';
import { useMutation, useQueryClient } from 'react-query';
import { X, Calendar, Users, MapPin, Trophy, FileText, Upload } from 'lucide-react';
import { tournamentService } from '../../services';
import { 
  SPORT_TYPES, 
  SPORT_TYPE_LABELS,
  TOURNAMENT_STATUS,
  VALIDATION_RULES,
  QUERY_KEYS
} from '../../utils/constants';
import { formatDateTimeForInput } from '../../utils/helpers';
import { formatDateForBackend } from '../../utils/dateUtils';

const TournamentCreateFormSafe = ({ isOpen, onClose, onSuccess }) => {
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [notification, setNotification] = useState(null);
  const formRef = useRef(null);
  const queryClient = useQueryClient();

  const {
    register,
    handleSubmit,
    watch,
    reset,
    formState: { errors, isValid }
  } = useForm({
    defaultValues: {
      name: '',
      description: '',
      sportType: 'FOOTBALL',
      maxTeams: 16,
      startDate: '',
      endDate: '',
      registrationDeadline: '',
      location: '',
      rules: '',
      prizeInfo: '',
      contactInfo: ''
    },
    mode: 'onChange'
  });

  // Native notification system - no DOM manipulation
  const showNotification = useCallback((type, message) => {
    console.log(`${type.toUpperCase()}: ${message}`);

    if (type === 'error') {
      setNotification({ type: 'error', message });
      // Also show native alert for important errors
      alert(`âŒ Lá»—i: ${message}`);
    } else if (type === 'success') {
      setNotification({ type: 'success', message });
      // Don't show alert for success, just console
    }

    // Clear notification after 5 seconds
    setTimeout(() => {
      setNotification(null);
    }, 5000);
  }, []);

  const createTournamentMutation = useMutation(
    (tournamentData) => {
      console.log('ğŸš€ Creating tournament with data:', tournamentData);
      return tournamentService.createTournament(tournamentData);
    },
    {
      onSuccess: (response) => {
        console.log('âœ… Tournament created successfully:', response);

        try {
          // Use setTimeout to prevent DOM manipulation conflicts
          setTimeout(() => {
            try {
              showNotification('success', 'Táº¡o giáº£i Ä‘áº¥u thÃ nh cÃ´ng!');

              // Safely invalidate queries
              setTimeout(() => {
                try {
                  queryClient.invalidateQueries(QUERY_KEYS.TOURNAMENTS);
                } catch (queryError) {
                  console.warn('Query invalidation error (non-critical):', queryError);
                }
              }, 100);

              // Safely call success callback
              setTimeout(() => {
                try {
                  if (onSuccess) {
                    onSuccess(response?.data || response);
                  }
                } catch (callbackError) {
                  console.warn('Success callback error (non-critical):', callbackError);
                }
              }, 150);

              // Safely close form
              setTimeout(() => {
                try {
                  handleClose();
                } catch (closeError) {
                  console.warn('Form close error (non-critical):', closeError);
                  // Force close anyway
                  if (onClose) {
                    onClose();
                  }
                }
              }, 200);

            } catch (innerError) {
              console.error('Error in success handler inner block:', innerError);
              // Still try to close form
              handleClose();
            }
          }, 50);
        } catch (outerError) {
          console.error('Error in success handler outer block:', outerError);
          // Fallback: just close the form
          handleClose();
        }
      },
      onError: (error) => {
        console.error('âŒ Tournament creation failed:', error);

        const errorMessage = error.response?.data?.message || 
                           error.response?.data?.error || 
                           error.message || 
                           'CÃ³ lá»—i xáº£y ra khi táº¡o giáº£i Ä‘áº¥u';

        showNotification('error', errorMessage);
        setIsSubmitting(false);
      }
    }
  );

  const watchStartDate = watch('startDate');

  const onSubmit = async (data) => {
    if (isSubmitting) return;

    try {
      console.log('ğŸ“ Form submitted with data:', data);
      setIsSubmitting(true);
      setNotification(null);

      // Validate required fields
      if (!data.name || !data.description || !data.location || !data.contactInfo) {
        showNotification('error', 'Vui lÃ²ng Ä‘iá»n Ä‘áº§y Ä‘á»§ thÃ´ng tin báº¯t buá»™c');
        setIsSubmitting(false);
        return;
      }

      // Validate dates
      if (!data.startDate || !data.endDate || !data.registrationDeadline) {
        showNotification('error', 'Vui lÃ²ng chá»n Ä‘áº§y Ä‘á»§ ngÃ y thÃ¡ng');
        setIsSubmitting(false);
        return;
      }

      const startDate = new Date(data.startDate);
      const endDate = new Date(data.endDate);
      const regDeadline = new Date(data.registrationDeadline);
      const now = new Date();

      // Debug log Ä‘á»ƒ kiá»ƒm tra thá»i gian
      console.log('ğŸ• Time validation:', {
        now: now.toISOString(),
        regDeadline: regDeadline.toISOString(),
        startDate: startDate.toISOString(),
        endDate: endDate.toISOString()
      });

      // Kiá»ƒm tra háº¡n Ä‘Äƒng kÃ½ pháº£i sau thá»i Ä‘iá»ƒm hiá»‡n táº¡i (cho thÃªm 1 phÃºt buffer)
      const nowPlus1Min = new Date(now.getTime() + 60000);
      if (regDeadline <= nowPlus1Min) {
        showNotification('error', `Háº¡n Ä‘Äƒng kÃ½ pháº£i sau thá»i Ä‘iá»ƒm hiá»‡n táº¡i. Hiá»‡n táº¡i: ${now.toLocaleString('vi-VN')}`);
        setIsSubmitting(false);
        return;
      }

      if (regDeadline >= startDate) {
        showNotification('error', `Háº¡n Ä‘Äƒng kÃ½ (${regDeadline.toLocaleString('vi-VN')}) pháº£i trÆ°á»›c ngÃ y báº¯t Ä‘áº§u (${startDate.toLocaleString('vi-VN')})`);
        setIsSubmitting(false);
        return;
      }

      if (startDate >= endDate) {
        showNotification('error', `NgÃ y káº¿t thÃºc (${endDate.toLocaleString('vi-VN')}) pháº£i sau ngÃ y báº¯t Ä‘áº§u (${startDate.toLocaleString('vi-VN')})`);
        setIsSubmitting(false);
        return;
      }

      // ULTRA-SAFE tournament data preparation
      try {
        const tournamentData = {
          name: data.name.trim(),
          description: data.description.trim(),
          sportType: data.sportType || 'FOOTBALL',
          maxTeams: parseInt(data.maxTeams) || 16,
          // CRITICAL: Use multiple date format attempts
          startDate: formatDateForBackend(startDate),
          endDate: formatDateForBackend(endDate),
          registrationDeadline: formatDateForBackend(regDeadline),
          location: data.location.trim(),
          rules: data.rules?.trim() || '',
          prizeInfo: data.prizeInfo?.trim() || '',
          contactInfo: data.contactInfo.trim()
        };

        // Validate that all dates were formatted successfully
        if (!tournamentData.startDate || !tournamentData.endDate || !tournamentData.registrationDeadline) {
          showNotification('error', 'Lá»—i Ä‘á»‹nh dáº¡ng ngÃ y thÃ¡ng. Vui lÃ²ng kiá»ƒm tra láº¡i.');
          setIsSubmitting(false);
          return;
        }

        console.log('ğŸ¯ FINAL TOURNAMENT DATA (with enhanced date format):');
        console.log(JSON.stringify(tournamentData, null, 2));

        // ULTRA-SAFE mutation call with multiple error protection
        try {
          await createTournamentMutation.mutateAsync(tournamentData);
        } catch (mutationError) {
          console.error('ğŸ’¥ Mutation error caught:', mutationError);

          // If it's a date format error, try alternative format
          if (mutationError.message && mutationError.message.includes('LocalDateTime')) {
          console.log('ğŸ”„ Trying alternative date formats...');

          // Try multiple alternative formats
