// src/services/newsService.js
import axios from 'axios';

const API_BASE_URL = 'http://localhost:8080/api/v1/news'; // Đảm bảo URL này khớp với backend của bạn

const newsService = {
  // Get all news articles
  getAllNews: async () => {
    try {
      const response = await axios.get(API_BASE_URL);
      return response.data; // NewsController trả về List<News> trực tiếp
    } catch (error) {
      console.error("Error fetching all news:", error);
      throw error;
    }
  },

  // Get news by ID
  getNewsById: async (id) => {
    try {
      const response = await axios.get(`${API_BASE_URL}/${id}`);
      return response.data; // NewsController trả về Optional<News>, Axios sẽ tự Unwrap
    } catch (error) {
      console.error(`Error fetching news with ID ${id}:`, error);
      throw error;
    }
  },

  // Create a new news article
  createNews: async (newsData) => {
    // newsData nên có cấu trúc { name, content, shortDescription }
    try {
      const response = await axios.post(API_BASE_URL, newsData);
      return response.data;
    } catch (error) {
      console.error("Error creating news:", error);
      throw error;
    }
  },

  // Update a news article
  updateNews: async (id, newsData) => {
    // newsData nên có cấu trúc { name, content, shortDescription }
    try {
      const response = await axios.put(`${API_BASE_URL}/${id}`, newsData);
      return response.data;
    } catch (error) {
      console.error(`Error updating news with ID ${id}:`, error);
      throw error;
    }
  },

  // Delete a news article
  deleteNews: async (id) => {
    try {
      const response = await axios.delete(`${API_BASE_URL}/${id}`);
      return response.data; // Backend trả về Map<String, String>
    } catch (error) {
      console.error(`Error deleting news with ID ${id}:`, error);
      throw error;
    }
  },

  // Upload attachments for a news article
  uploadNewsAttachments: async (newsId, files) => {
    try {
      const formData = new FormData();
      files.forEach(file => {
        formData.append('files', file); // 'files' phải khớp với @RequestParam("files")
      });

      const response = await axios.post(`${API_BASE_URL}/uploads/${newsId}`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      return response.data; // Backend trả về ResponseObject
    } catch (error) {
      console.error(`Error uploading attachments for news ID ${newsId}:`, error);
      throw error;
    }
  },

  // Get image by name (for displaying attachments)
  getImageUrl: (imageName) => {
    return `${API_BASE_URL}/image/${imageName}`;
  }
};

export default newsService;
