import React, { useState, Suspense } from 'react';
import { useQuery, useMutation, useQueryClient } from 'react-query'; // Giữ lại useQuery và useQueryClient
import {
  Users,
  Trophy,
  Calendar,
  FileText,
  Settings,
  Plus,
  Edit,
  Trash2,
  Eye,
  Search,
  Filter,
  UserCheck,
  UserX,
  Shield,
  Crown,
  AlertTriangle,
  BarChart3,
  Bug
} from 'lucide-react';
import { useAuth } from '../context/AuthContext';
// Không cần import các service cụ thể nếu chỉ đọc từ cache.
// Tuy nhiên, bạn vẫn cần chúng ở những nơi khác để fill cache.
// Ví dụ, nếu UserManagement gọi userService.getAllUsers() thì dữ liệu mới có trong cache.
// import { tournamentService, newsService } from '../services'; // Có thể bỏ nếu không dùng trực tiếp
import LoadingSpinner from '../components/LoadingSpinner';
import { formatDate, getStatusColor } from '../utils/helpers';
import toast from 'react-hot-toast';

// --- Sử dụng React.lazy để import các component động ---
const UserManagement = React.lazy(() => import('../components/admin/UserManagement'));
const TournamentManagement = React.lazy(() => import('../components/admin/TournamentManagement'));
const AdminTournamentDashboard = React.lazy(() => import('../components/admin/AdminTournamentDashboard'));
const MatchManagement = React.lazy(() => import('../components/admin/MatchManagement'));
const NewsManagement = React.lazy(() => import('../components/admin/NewsManagement'));
// const NewsDebugPanel = React.lazy(() => import('../components/debug/NewsDebugPanel')); // DISABLED FOR PRODUCTION

const AdminPanel = () => {
  const [activeTab, setActiveTab] = useState('dashboard');
  const { user } = useAuth();
  const queryClient = useQueryClient(); // Khởi tạo queryClient để truy cập cache

  const isAdmin = user?.role === 'ADMIN';
  const isOrganizer = user?.role === 'ORGANIZER';

  // --- Thay đổi ở đây: Lấy dữ liệu từ cache của React Query ---
  // Giả định các query keys tương ứng là 'users', 'tournaments', 'teams', 'matches', 'news'
  const usersData = queryClient.getQueryData('users');
  const tournamentsData = queryClient.getQueryData('tournaments');
  const teamsData = queryClient.getQueryData('teams');
  const matchesData = queryClient.getQueryData('matches');
  const newsData = queryClient.getQueryData('news');

  // Tính toán các giá trị thống kê từ dữ liệu trong cache
  const totalUsers = usersData ? usersData.length : 0;
  const totalTournaments = tournamentsData ? tournamentsData.length : 0;
  const totalTeams = teamsData ? teamsData.length : 0;
  const totalMatches = matchesData ? matchesData.length : 0;
  // Giả định newsData có trường 'status' và bạn muốn đếm các tin tức có status là 'PUBLISHED'
  const publishedNews = newsData ? newsData.filter(n => n.status === 'PUBLISHED').length : 0;


  // Tạo một object `adminStats` từ các giá trị tính toán
  // Không cần `isLoading` cho adminStats riêng vì chúng ta lấy từ cache
  const adminStats = {
      totalUsers: totalUsers,
      totalTournaments: totalTournaments,
      totalTeams: totalTeams,
      totalMatches: totalMatches,
      publishedNews: publishedNews,
      // Có thể thêm các thống kê khác nếu dữ liệu tương ứng có trong cache
      // Ví dụ: pendingApprovals: teamsData?.filter(t => t.status === 'PENDING').length || 0,
      systemHealth: 99.2 // Giá trị mock nếu không có nguồn dữ liệu real-time
  };

  // Điều kiện kiểm tra quyền truy cập (không thay đổi)
  if (!isAdmin && !isOrganizer) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <AlertTriangle className="h-16 w-16 text-red-500 mx-auto mb-4" />
          <h2 className="text-2xl font-bold text-gray-900 mb-2">Access Denied</h2>
          <p className="text-gray-600">You don't have permission to access this page.</p>
        </div>
      </div>
    );
  }

  // --- Thay đổi ở đây: Logic loading cho AdminPanel ---
  // Chúng ta không có `statsLoading` từ `useQuery` nữa.
  // Thay vào đó, bạn có thể cân nhắc hiển thị loading ban đầu
  // hoặc khi một tab cụ thể đang tải component của nó.
  // Ở đây, tôi sẽ bỏ qua loading ban đầu cho dashboard stats,
  // giả định dữ liệu trong cache là đủ nhanh.
  // Nếu bạn muốn một loading indicator cho toàn bộ panel ban đầu,
  // bạn có thể đặt một `useState` và quản lý nó thủ công.
  // Hoặc bạn có thể sử dụng `usersData === undefined || tournamentsData === undefined ...`
  // làm điều kiện loading ban đầu nếu bạn muốn chờ dữ liệu cache có sẵn.
  // Tuy nhiên, vì mục đích của phương pháp 2 là không cần fetch lại,
  // nên việc hiển thị spinner chỉ khi component chính (AdminPanel) đang tải là đủ.
  // Loading spinner cho component con (UserManagement, TournamentManagement) đã được xử lý bởi <Suspense>.

  const tabs = [
    {
      id: 'dashboard',
      name: 'Tournament Dashboard',
      icon: BarChart3,
      color: 'text-indigo-600',
      bgColor: 'bg-indigo-100',
      description: 'Overview and analytics of all tournaments',
      component: AdminTournamentDashboard
    },
    {
      id: 'users',
      name: 'User Management',
      icon: Users,
      color: 'text-blue-600',
      bgColor: 'bg-blue-100',
      description: 'Manage user accounts and permissions',
      component: UserManagement
    },
    {
      id: 'tournaments',
      name: 'Tournament Management',
      icon: Trophy,
      color: 'text-orange-600',
      bgColor: 'bg-orange-100',
      description: 'Create and manage tournaments',
      component: TournamentManagement
    },
    // { // Uncomment nếu bạn muốn thêm lại tab này
    //   id: 'matches',
    //   name: 'Match Management',
    //   icon: Calendar,
    //   color: 'text-green-600',
    //   bgColor: 'bg-green-100',
    //   description: 'Schedule and manage matches',
    //   component: MatchManagement
    // },
    {
      id: 'news',
      name: 'News Management',
      icon: FileText,
      color: 'text-purple-600',
      bgColor: 'bg-purple-100',
      description: 'Create and publish news articles',
      component: NewsManagement
    },
    /*
    {
      id: 'debug',
      name: 'News Debug',
      icon: Bug,
      color: 'text-red-600',
      bgColor: 'bg-red-100',
      description: 'Debug and test news API functionality',
      component: NewsDebugPanel
    }
    */
  ];

  const stats = [
    {
      name: 'Total Users',
      value: adminStats.totalUsers, // Lấy từ object adminStats đã tính toán
      icon: Users,
      color: 'text-blue-600',
      bgColor: 'bg-blue-100',
      change: '+12%', // Giữ nguyên mock change
      changeType: 'increase'
    },
    {
      name: 'Total Teams Registered',
      value: adminStats.totalTeams, // Lấy từ object adminStats đã tính toán
      icon: Users, // Có thể đổi icon nếu muốn phân biệt rõ hơn
      color: 'text-blue-600',
      bgColor: 'bg-blue-100',
      change: '+8%',
      changeType: 'increase'
    },
    {
      name: 'Total Tournaments',
      value: adminStats.totalTournaments, // Lấy từ object adminStats đã tính toán
      icon: Trophy,
      color: 'text-orange-600',
      bgColor: 'bg-orange-100',
      change: '+8%',
      changeType: 'increase'
    },
    {
      name: 'Total Matches',
      value: adminStats.totalMatches, // Lấy từ object adminStats đã tính toán
      icon: Calendar,
      color: 'text-green-600',
      bgColor: 'bg-green-100',
      change: '+5%',
      changeType: 'increase'
    },
    {
      name: 'Published News',
      value: adminStats.publishedNews, // Lấy từ object adminStats đã tính toán
      icon: FileText,
      color: 'text-purple-600',
      bgColor: 'bg-purple-100',
      change: '+15%',
      changeType: 'increase'
    }
  ];

  const CurrentTabComponent = tabs.find(tab => tab.id === activeTab)?.component;

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
          <div className="flex items-center justify-between">
            <div>
              <div className="flex items-center space-x-3">
                <div className="bg-gradient-to-r from-red-500 to-orange-500 p-3 rounded-lg">
                  <Shield className="h-8 w-8 text-white" />
                </div>
                <div>
                  <h1 className="text-3xl font-bold text-gray-900">Admin Panel</h1>
                  <p className="text-gray-600">
                    {isAdmin ? 'System Administrator' : 'Tournament Organizer'} Dashboard
                  </p>
                </div>
              </div>
            </div>
            <div className="flex items-center space-x-3">
              <div className="bg-green-100 text-green-800 px-3 py-1 rounded-full text-sm font-medium">
                System Healthy
              </div>
              <div className="flex items-center space-x-2">
                <Crown className="h-5 w-5 text-yellow-500" />
                <span className="text-sm font-medium text-gray-700">{user?.name}</span>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Stats Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          {stats.map((stat, index) => (
            <div key={index} className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-gray-600 mb-1">{stat.name}</p>
                  <p className="text-3xl font-bold text-gray-900">{stat.value}</p>
                  <div className="flex items-center mt-2">
                    <span className={`text-sm font-medium ${
                      stat.changeType === 'increase' ? 'text-green-600' : 'text-red-600'
                    }`}>
                      {stat.change}
                    </span>
                    <span className="text-sm text-gray-500 ml-1">vs last month</span>
                  </div>
                </div>
                <div className={`${stat.bgColor} p-3 rounded-lg`}>
                  <stat.icon className={`h-6 w-6 ${stat.color}`} />
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Quick Actions */}
        <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6 mb-8">
          <h2 className="text-xl font-bold text-gray-900 mb-4">Quick Actions</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {tabs.map((tab) => (
              <button
                key={tab.id}
                onClick={() => setActiveTab(tab.id)}
                className={`p-4 rounded-lg border-2 transition-all duration-200 text-left ${
                  activeTab === tab.id
                    ? 'border-primary-500 bg-primary-50'
                    : 'border-gray-200 hover:border-gray-300 hover:bg-gray-50'
                }`}
              >
                <div className="flex items-center space-x-3 mb-2">
                  <div className={`${tab.bgColor} p-2 rounded-lg`}>
                    <tab.icon className={`h-5 w-5 ${tab.color}`} />
                  </div>
                  <h3 className="font-semibold text-gray-900">{tab.name}</h3>
                </div>
                <p className="text-sm text-gray-600">{tab.description}</p>
              </button>
            ))}
          </div>
        </div>

        {/* Tab Content */}
        <div className="bg-white rounded-xl shadow-sm border border-gray-200">
          <div className="border-b border-gray-200">
            <nav className="-mb-px flex space-x-8 px-6">
              {tabs.map((tab) => (
                <button
                  key={tab.id}
                  onClick={() => setActiveTab(tab.id)}
                  className={`py-4 px-1 border-b-2 font-medium text-sm flex items-center space-x-2 transition-colors ${
                    activeTab === tab.id
                      ? 'border-primary-500 text-primary-600'
                      : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                  }`}
                >
                  <tab.icon className="h-4 w-4" />
                  <span>{tab.name}</span>
                </button>
              ))}
            </nav>
          </div>

          <div className="p-6">
            <Suspense fallback={<LoadingSpinner text="Loading admin module..." />}>
              {CurrentTabComponent && <CurrentTabComponent />}
            </Suspense>
          </div>
        </div>

        {/* System Health */}
        <div className="mt-8 bg-white rounded-xl shadow-sm border border-gray-200 p-6">
          <h2 className="text-xl font-bold text-gray-900 mb-4">System Status</h2>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <div className="flex items-center space-x-3">
              <div className="w-3 h-3 bg-green-500 rounded-full"></div>
              <div>
                <p className="font-medium text-gray-900">API Status</p>
                <p className="text-sm text-gray-600">All systems operational</p>
              </div>
            </div>
            <div className="flex items-center space-x-3">
              <div className="w-3 h-3 bg-green-500 rounded-full"></div>
              <div>
                <p className="font-medium text-gray-900">Database</p>
                <p className="text-sm text-gray-600">Response time: 23ms</p>
              </div>
            </div>
            <div className="flex items-center space-x-3">
              <div className="w-3 h-3 bg-yellow-500 rounded-full"></div>
              <div>
                <p className="font-medium text-gray-900">Background Jobs</p>
                <p className="text-sm text-gray-600">2 pending tasks</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminPanel;
