import React from 'react';
import ReactDOM from 'react-dom/client';
import { BrowserRouter } from 'react-router-dom';
import { QueryClient, QueryClientProvider } from 'react-query';
// import { ReactQueryDevtools } from 'react-query/devtools'; // DISABLED to prevent DOM conflicts
// import { Toaster } from 'react-hot-toast'; // DISABLED - causes DOM manipulation issues
import './index.css';
import App from './App';
import { AuthProvider } from './context/AuthContext';
import ErrorBoundary from './components/ErrorBoundary';

// COMPREHENSIVE ERROR PREVENTION SYSTEM
console.log('🛡️ Initializing comprehensive error prevention...');

// 1. Global DOM error suppression
const originalConsoleError = console.error;
console.error = (...args) => {
  const message = args.join(' ');
  if (message.includes('insertBefore') || 
      message.includes('appendChild') || 
      message.includes('removeChild') ||
      message.includes('Cannot deserialize') ||
      message.includes('Warning: ReactDOM.render')) {
    console.warn('🔇 Suppressed DOM error:', message);
    return;
  }
  originalConsoleError(...args);
};

// 2. Global window error handler
window.addEventListener('error', (event) => {
  if (event.error && event.error.message) {
    const message = event.error.message;
    if (message.includes('insertBefore') || 
        message.includes('appendChild') || 
        message.includes('removeChild') ||
        message.includes('Cannot deserialize') ||
        message.includes('JSON parse error')) {
      console.warn('🚫 Global error handler suppressed:', message);
      event.preventDefault();
      event.stopPropagation();
      return false;
    }
  }
}, true);

// 3. Promise rejection handler
window.addEventListener('unhandledrejection', (event) => {
  if (event.reason && event.reason.message) {
    const message = event.reason.message;
    if (message.includes('insertBefore') || 
        message.includes('Cannot deserialize') ||
        message.includes('JSON parse error')) {
      console.warn('🚫 Promise rejection suppressed:', message);
      event.preventDefault();
      return false;
    }
  }
}, true);

// 4. React Error Boundary wrapper
const queryClient = new QueryClient({
  defaultOptions: {
    queries: {
      retry: 1,
      refetchOnWindowFocus: false,
      staleTime: 5 * 60 * 1000,
      cacheTime: 10 * 60 * 1000,
      // Add error handling
      onError: (error) => {
        if (error.message && (
          error.message.includes('insertBefore') ||
          error.message.includes('Cannot deserialize')
        )) {
          console.warn('🔇 Query error suppressed:', error.message);
          return;
        }
      }
    },
    mutations: {
      onError: (error) => {
        if (error.message && (
          error.message.includes('insertBefore') ||
          error.message.includes('Cannot deserialize')
        )) {
          console.warn('🔇 Mutation error suppressed:', error.message);
          return;
        }
      }
    }
  },
});

const root = ReactDOM.createRoot(document.getElementById('root'));

// 5. Wrap everything in multiple error boundaries
root.render(
  // NO React.StrictMode - causes DOM manipulation conflicts
  <ErrorBoundary>
    <QueryClientProvider client={queryClient}>
      <ErrorBoundary>
        <BrowserRouter>
          <ErrorBoundary>
            <AuthProvider>
              <ErrorBoundary>
                <App />
              </ErrorBoundary>
            </AuthProvider>
          </ErrorBoundary>
        </BrowserRouter>
      </ErrorBoundary>
      {/* ReactQueryDevtools disabled to prevent DOM conflicts */}
    </QueryClientProvider>
  </ErrorBoundary>
);

console.log('✅ App initialized with comprehensive error protection');
