import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useQuery, useMutation, useQueryClient } from 'react-query';
import toast from 'react-hot-toast';
import {
  Save,
  RotateCcw, // Hoặc RefreshCw, Reset
  List, // Cho "Danh sách mùa giải"
  Share2, // Cho "Di chuyển mùa giải" hoặc tương tự
  Upload, // Cho chọn ảnh
  Calendar, // Cho icon lịch
} from 'lucide-react';

import LoadingSpinner from '../LoadingSpinner';
import { tournamentServiceFixed as tournamentService } from '../../services/tournamentServiceFixed';
import { QUERY_KEYS } from '../../utils/constants'; // Đảm bảo QUERY_KEYS có TOURNAMENTS
import { getSportTypeLabel } from '../../utils/helpers'; // Nếu bạn có helper này

// Giả sử có một ảnh đại diện mặc định cho logo và background
const DEFAULT_LOGO_PLACEHOLDER = 'https://via.placeholder.com/150x150?text=Logo+Giải+Đấu';
const DEFAULT_BACKGROUND_PLACEHOLDER = 'https://via.placeholder.com/400x200?text=Ảnh+Nền+Giải+Đấu';

const AdminTournamentDetailPage = () => {
  const { id } = useParams(); // Lấy ID giải đấu từ URL
  const navigate = useNavigate();
  const queryClient = useQueryClient();

  // State cho form data
  const [formData, setFormData] = useState({
    name: '',
    startDate: '',
    endDate: '',
    maxTeams: 0,
    websiteColor: '#B81E1F', // Màu mặc định từ ảnh
    tournamentFormat: '', // Thể thức thi đấu
    sportType: '', // Môn đấu
    tournamentType: '', // Loại giải đấu (Đơn đội/Đội)
    winPoints: 3,
    drawPoints: 1,
    losePoints: 0,
    onlineRegistration: true,
    thirdPlaceMatch: true,
    playersPerTeam: 0,
    coachesPerTeam: 0,
    logo: DEFAULT_LOGO_PLACEHOLDER, // URL ảnh logo
    background: DEFAULT_BACKGROUND_PLACEHOLDER, // URL ảnh nền
    location: '', // Thêm trường location nếu có
    description: '', // Thêm trường description nếu có
  });

  // Query để lấy dữ liệu giải đấu
  const { data: tournamentData, isLoading, isError, error } = useQuery(
    [QUERY_KEYS.TOURNAMENTS, id],
    () => tournamentService.getTournamentById(id),
    {
      enabled: !!id, // Chỉ fetch khi có ID
      onSuccess: (data) => {
        // Cập nhật formData khi dữ liệu được fetch thành công
        setFormData({
          name: data.name || '',
          startDate: data.startDate ? new Date(data.startDate).toISOString().split('T')[0] : '', // Format YYYY-MM-DD
          endDate: data.endDate ? new Date(data.endDate).toISOString().split('T')[0] : '',
          maxTeams: data.maxTeams || 0,
          websiteColor: data.websiteColor || '#B81E1F',
          tournamentFormat: data.tournamentFormat || '',
          sportType: data.sportType || '',
          tournamentType: data.tournamentType || 'TEAM', // Mặc định là đội
          winPoints: data.winPoints !== undefined ? data.winPoints : 3,
          drawPoints: data.drawPoints !== undefined ? data.drawPoints : 1,
          losePoints: data.losePoints !== undefined ? data.losePoints : 0,
          onlineRegistration: data.onlineRegistration !== undefined ? data.onlineRegistration : true,
          thirdPlaceMatch: data.thirdPlaceMatch !== undefined ? data.thirdPlaceMatch : true,
          playersPerTeam: data.playersPerTeam || 0,
          coachesPerTeam: data.coachesPerTeam || 0,
          logo: data.logo || DEFAULT_LOGO_PLACEHOLDER,
          background: data.background || DEFAULT_BACKGROUND_PLACEHOLDER,
          location: data.location || '',
          description: data.description || '',
        });
      },
      onError: (err) => {
        toast.error('Lỗi khi tải dữ liệu giải đấu!');
        console.error('Error fetching tournament:', err);
      }
    }
  );

  // Mutation để cập nhật giải đấu
  const updateTournamentMutation = useMutation(
    (updatedData) => tournamentService.updateTournament(id, updatedData),
    {
      onSuccess: () => {
        toast.success('Cập nhật giải đấu thành công!');
        queryClient.invalidateQueries([QUERY_KEYS.TOURNAMENTS, id]); // Invalidates cache cho giải đấu này
        queryClient.invalidateQueries(QUERY_KEYS.TOURNAMENTS); // Invalidates cache cho danh sách
      },
      onError: (err) => {
        toast.error(err.response?.data?.message || 'Cập nhật giải đấu thất bại!');
        console.error('Error updating tournament:', err);
      }
    }
  );

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  const handleFileChange = (e, fieldName) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setFormData((prev) => ({
          ...prev,
          [fieldName]: reader.result, // Lưu base64 hoặc URL tạm thời
        }));
      };
      reader.readAsDataURL(file);
      // TODO: Implement actual file upload to your backend storage service
      // You might want to call a separate service function here to upload the file
      // and get the actual URL back, then update formData with the real URL.
      toast('Chức năng tải ảnh cần được tích hợp API thực tế!', { icon: '⚠️' });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Tạo bản sao của formData để tránh mutation trực tiếp
    const dataToSubmit = { ...formData };

    // Chuyển đổi các URL base64 tạm thời thành null nếu không phải là URL thật,
    // hoặc bạn có thể gửi chúng lên server và để server xử lý
    if (dataToSubmit.logo.startsWith('data:') && dataToSubmit.logo !== DEFAULT_LOGO_PLACEHOLDER) {
      // Logic để upload file logo thực tế và lấy URL
      // Hiện tại, bỏ qua để không gửi base64 nếu không phải ảnh thật
      delete dataToSubmit.logo;
    }
    if (dataToSubmit.background.startsWith('data:') && dataToSubmit.background !== DEFAULT_BACKGROUND_PLACEHOLDER) {
      // Logic để upload file background thực tế và lấy URL
      delete dataToSubmit.background;
    }

    // Convert numbers
    dataToSubmit.maxTeams = Number(dataToSubmit.maxTeams);
    dataToSubmit.winPoints = Number(dataToSubmit.winPoints);
    dataToSubmit.drawPoints = Number(dataToSubmit.drawPoints);
    dataToSubmit.losePoints = Number(dataToSubmit.losePoints);
    dataToSubmit.playersPerTeam = Number(dataToSubmit.playersPerTeam);
    dataToSubmit.coachesPerTeam = Number(dataToSubmit.coachesPerTeam);

    updateTournamentMutation.mutate(dataToSubmit);
  };

  const handleReset = () => {
    // Đặt lại form data về trạng thái ban đầu từ tournamentData
    if (tournamentData) {
      setFormData({
        name: tournamentData.name || '',
        startDate: tournamentData.startDate ? new Date(tournamentData.startDate).toISOString().split('T')[0] : '',
        endDate: tournamentData.endDate ? new Date(tournamentData.endDate).toISOString().split('T')[0] : '',
        maxTeams: tournamentData.maxTeams || 0,
        websiteColor: tournamentData.websiteColor || '#B81E1F',
        tournamentFormat: tournamentData.tournamentFormat || '',
        sportType: tournamentData.sportType || '',
        tournamentType: tournamentData.tournamentType || 'TEAM',
        winPoints: tournamentData.winPoints !== undefined ? tournamentData.winPoints : 3,
        drawPoints: tournamentData.drawPoints !== undefined ? tournamentData.drawPoints : 1,
        losePoints: tournamentData.losePoints !== undefined ? tournamentData.losePoints : 0,
        onlineRegistration: tournamentData.onlineRegistration !== undefined ? tournamentData.onlineRegistration : true,
        thirdPlaceMatch: tournamentData.thirdPlaceMatch !== undefined ? tournamentData.thirdPlaceMatch : true,
        playersPerTeam: tournamentData.playersPerTeam || 0,
        coachesPerTeam: tournamentData.coachesPerTeam || 0,
        logo: tournamentData.logo || DEFAULT_LOGO_PLACEHOLDER,
        background: tournamentData.background || DEFAULT_BACKGROUND_PLACEHOLDER,
        location: tournamentData.location || '',
        description: tournamentData.description || '',
      });
      toast.info('Form đã được đặt lại!');
    }
  };

  if (isLoading) {
    return (
      <div className="flex justify-center items-center h-screen-75">
        <LoadingSpinner text="Đang tải cấu hình giải đấu..." />
      </div>
    );
  }

  if (isError) {
    return (
      <div className="text-center py-8">
        <h3 className="text-xl font-semibold text-red-600">Lỗi:</h3>
        <p className="text-gray-700">Không thể tải dữ liệu giải đấu. {error.message}</p>
        <button
          onClick={() => queryClient.invalidateQueries([QUERY_KEYS.TOURNAMENTS, id])}
          className="mt-4 btn-primary"
        >
          Thử lại
        </button>
      </div>
    );
  }

  if (!tournamentData) {
    return (
      <div className="text-center py-8">
        <h3 className="text-xl font-semibold text-gray-700">Không tìm thấy giải đấu</h3>
        <p className="text-gray-500">ID giải đấu không hợp lệ hoặc giải đấu không tồn tại.</p>
        <button onClick={() => navigate('/admin/tournaments')} className="mt-4 btn-primary">
          Quay lại danh sách giải đấu
        </button>
      </div>
    );
  }

  return (
    // Đây là phần nội dung chính, nằm trong Layout của bạn
    <div className="p-6 bg-gray-100 min-h-screen-75">
      <div className="max-w-7xl mx-auto bg-white rounded-lg shadow-md overflow-hidden">
        {/* Header của form cấu hình */}
        <div className="bg-gradient-to-r from-purple-700 to-blue-600 text-white p-4 flex items-center justify-between">
          <h2 className="text-xl font-bold">Cấu hình mùa giải: {formData.name || 'Mùa giải mới'}</h2>
          <div className="flex space-x-2">
            <button
              onClick={handleSubmit}
              className="px-4 py-2 bg-green-500 hover:bg-green-600 rounded-md flex items-center text-sm font-medium transition-colors"
              disabled={updateTournamentMutation.isLoading}
            >
              <Save className="h-4 w-4 mr-2" /> Lưu
            </button>
            <button
              onClick={handleReset}
              className="px-4 py-2 bg-gray-500 hover:bg-gray-600 rounded-md flex items-center text-sm font-medium transition-colors"
              disabled={updateTournamentMutation.isLoading}
            >
              <RotateCcw className="h-4 w-4 mr-2" /> Nhập lại
            </button>
            <button
              onClick={() => navigate('/admin/tournaments')}
              className="px-4 py-2 bg-blue-500 hover:bg-blue-600 rounded-md flex items-center text-sm font-medium transition-colors"
            >
              <List className="h-4 w-4 mr-2" /> Danh sách mùa giải
            </button>
            {/* Nếu có chức năng "Di chuyển mùa giải" */}
            {/* <button
              onClick={() => { /* logic di chuyển mùa giải */ /*}}
              className="px-4 py-2 bg-red-500 hover:bg-red-600 rounded-md flex items-center text-sm font-medium transition-colors"
            >
              <Share2 className="h-4 w-4 mr-2" /> Di chuyển mùa giải
            </button> */}
          </div>
        </div>

        {/* Nội dung form */}
        <div className="p-6 grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Cột trái: Thông tin cơ bản và cài đặt */}
          <div className="lg:col-span-2 space-y-6">
            {/* Thông tin chung */}
            <div className="bg-white border border-gray-200 rounded-lg p-4 shadow-sm">
              <h3 className="text-lg font-semibold text-gray-800 mb-4">Cấu hình giải đấu</h3>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <label htmlFor="name" className="input-label">Tên mùa giải</label>
                  <input
                    type="text"
                    id="name"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    className="input-field"
                    placeholder="Nhập tên giải đấu"
                  />
                </div>
                <div>
                  <label htmlFor="maxTeams" className="input-label">Số đội tham dự tối đa</label>
                  <input
                    type="number"
                    id="maxTeams"
                    name="maxTeams"
                    value={formData.maxTeams}
                    onChange={handleChange}
                    className="input-field"
                    min="0"
                  />
                </div>
                <div className="relative">
                  <label htmlFor="startDate" className="input-label">Thời gian bắt đầu</label>
                  <input
                    type="date"
                    id="startDate"
                    name="startDate"
                    value={formData.startDate}
                    onChange={handleChange}
                    className="input-field pr-8"
                  />
                  <Calendar className="absolute right-3 top-1/2 mt-2 transform -translate-y-1/2 text-gray-400 h-5 w-5" />
                </div>
                <div className="relative">
                  <label htmlFor="endDate" className="input-label">Thời gian kết thúc</label>
                  <input
                    type="date"
                    id="endDate"
                    name="endDate"
                    value={formData.endDate}
                    onChange={handleChange}
                    className="input-field pr-8"
                  />
                  <Calendar className="absolute right-3 top-1/2 mt-2 transform -translate-y-1/2 text-gray-400 h-5 w-5" />
                </div>
                <div>
                  <label htmlFor="websiteColor" className="input-label">Màu Website</label>
                  <div className="flex items-center space-x-2">
                    <input
                      type="color"
                      id="websiteColor"
                      name="websiteColor"
                      value={formData.websiteColor}
                      onChange={handleChange}
                      className="h-10 w-10 p-0 border border-gray-300 rounded-md cursor-pointer"
                      title="Chọn màu"
                    />
                    <span className="text-gray-700">{formData.websiteColor}</span>
                  </div>
                </div>
                <div>
                  <label htmlFor="location" className="input-label">Địa điểm</label>
                  <input
                    type="text"
                    id="location"
                    name="location"
                    value={formData.location}
                    onChange={handleChange}
                    className="input-field"
                    placeholder="Ví dụ: Sân vận động ABC"
                  />
                </div>
                <div>
                  <label htmlFor="sportType" className="input-label">Môn đấu</label>
                  <select
                    id="sportType"
                    name="sportType"
                    value={formData.sportType}
                    onChange={handleChange}
                    className="input-field"
                  >
                    <option value="">Chọn môn đấu</option>
                    <option value="FOOTBALL">Bóng đá</option>
                    <option value="BASKETBALL">Bóng rổ</option>
                    <option value="VOLLEYBALL">Bóng chuyền</option>
                    <option value="BADMINTON">Cầu lông</option>
                    {/* Thêm các loại môn đấu khác nếu có */}
                  </select>
                </div>
                <div>
                  <label htmlFor="tournamentType" className="input-label">Loại giải đấu</label>
                  <select
                    id="tournamentType"
                    name="tournamentType"
                    value={formData.tournamentType}
                    onChange={handleChange}
                    className="input-field"
                  >
                    <option value="TEAM">Đồng đội</option>
                    <option value="INDIVIDUAL">Cá nhân</option>
                  </select>
                </div>
              </div>
              <div className="mt-4">
                <label htmlFor="description" className="input-label">Mô tả giải đấu</label>
                <textarea
                  id="description"
                  name="description"
                  value={formData.description}
                  onChange={handleChange}
                  rows="3"
                  className="input-field"
                  placeholder="Mô tả chi tiết về giải đấu..."
                ></textarea>
              </div>
            </div>

            {/* Cài đặt điểm */}
            <div className="bg-white border border-gray-200 rounded-lg p-4 shadow-sm">
              <h3 className="text-lg font-semibold text-gray-800 mb-4">Cài đặt điểm</h3>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <div>
                  <label htmlFor="winPoints" className="input-label">Điểm thắng</label>
                  <input
                    type="number"
                    id="winPoints"
                    name="winPoints"
                    value={formData.winPoints}
                    onChange={handleChange}
                    className="input-field"
                  />
                </div>
                <div>
                  <label htmlFor="drawPoints" className="input-label">Điểm hòa</label>
                  <input
                    type="number"
                    id="drawPoints"
                    name="drawPoints"
                    value={formData.drawPoints}
                    onChange={handleChange}
                    className="input-field"
                  />
                </div>
                <div>
                  <label htmlFor="losePoints" className="input-label">Điểm thua</label>
                  <input
                    type="number"
                    id="losePoints"
                    name="losePoints"
                    value={formData.losePoints}
                    onChange={handleChange}
                    className="input-field"
                  />
                </div>
              </div>
            </div>

            {/* Cài đặt đăng ký & thi đấu */}
            <div className="bg-white border border-gray-200 rounded-lg p-4 shadow-sm">
              <h3 className="text-lg font-semibold text-gray-800 mb-4">Cài đặt đăng ký & thi đấu</h3>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div className="flex items-center justify-between py-2">
                  <label htmlFor="onlineRegistration" className="input-label cursor-pointer mb-0">Đăng ký trực tuyến</label>
                  <input
                    type="checkbox"
                    id="onlineRegistration"
                    name="onlineRegistration"
                    checked={formData.onlineRegistration}
                    onChange={handleChange}
                    className="toggle-switch"
                  />
                </div>
                <div className="flex items-center justify-between py-2">
                  <label htmlFor="thirdPlaceMatch" className="input-label cursor-pointer mb-0">Tranh giải 3</label>
                  <input
                    type="checkbox"
                    id="thirdPlaceMatch"
                    name="thirdPlaceMatch"
                    checked={formData.thirdPlaceMatch}
                    onChange={handleChange}
                    className="toggle-switch"
                  />
                </div>
                <div>
                  <label htmlFor="playersPerTeam" className="input-label">Số cầu thủ 1 đội (đăng ký)</label>
                  <input
                    type="number"
                    id="playersPerTeam"
                    name="playersPerTeam"
                    value={formData.playersPerTeam}
                    onChange={handleChange}
                    className="input-field"
                    min="0"
                  />
                </div>
                <div>
                  <label htmlFor="coachesPerTeam" className="input-label">Số thành viên ban huấn luyện</label>
                  <input
                    type="number"
                    id="coachesPerTeam"
                    name="coachesPerTeam"
                    value={formData.coachesPerTeam}
                    onChange={handleChange}
                    className="input-field"
                    min="0"
                  />
                </div>
              </div>
            </div>
          </div>

          {/* Cột phải: Logo và Background */}
          <div className="lg:col-span-1 space-y-6">
            <div className="bg-white border border-gray-200 rounded-lg p-4 shadow-sm text-center">
              <h3 className="text-lg font-semibold text-gray-800 mb-4">Logo</h3>
              <img
                src={formData.logo}
                alt="Logo Giải Đấu"
                className="w-32 h-32 object-contain mx-auto mb-4 rounded-md border border-gray-200"
              />
              <input
                type="file"
                id="logo-upload"
                accept="image/*"
                onChange={(e) => handleFileChange(e, 'logo')}
                className="hidden"
              />
              <label
                htmlFor="logo-upload"
                className="inline-flex items-center px-4 py-2 bg-blue-600 text-white text-sm font-medium rounded-md cursor-pointer hover:bg-blue-700 transition-colors"
              >
                <Upload className="h-4 w-4 mr-2" /> Chọn ảnh
              </label>
            </div>

            <div className="bg-white border border-gray-200 rounded-lg p-4 shadow-sm text-center">
              <h3 className="text-lg font-semibold text-gray-800 mb-4">Background</h3>
              <img
                src={formData.background}
                alt="Ảnh Nền Giải Đấu"
                className="w-full h-40 object-cover mx-auto mb-4 rounded-md border border-gray-200"
              />
              <input
                type="file"
                id="background-upload"
                accept="image/*"
                onChange={(e) => handleFileChange(e, 'background')}
                className="hidden"
              />
              <label
                htmlFor="background-upload"
                className="inline-flex items-center px-4 py-2 bg-blue-600 text-white text-sm font-medium rounded-md cursor-pointer hover:bg-blue-700 transition-colors"
              >
                <Upload className="h-4 w-4 mr-2" /> Chọn ảnh
              </label>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminTournamentDetailPage;
