import React from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import { Toaster } from 'react-hot-toast';
import { useAuth } from './context/AuthContext';

// Layout Components
import Layout from './components/Layout'; // Layout cho ng∆∞·ªùi d√πng th∆∞·ªùng
import AdminLayout from './components/AdminLayout'; // Layout cho trang admin
import LoadingSpinner from './components/LoadingSpinner';


// Public Pages
import HomePage from './pages/HomePage';
import LoginPage from './pages/LoginPage';
import RegisterPage from './pages/RegisterPage';
import TournamentsPage from './pages/TournamentsPage';
import TournamentDetailPage from './pages/TournamentDetailPage';
import NewsPage from './pages/NewsPage';
import NewsDetailPage from './pages/NewsDetailPage';

import DashboardPage from './pages/DashboardPage';
import ProfilePage from './pages/ProfilePage';
import AdminPanel from './pages/AdminPanel'; 

// Import c√°c component qu·∫£n l√Ω kh√°c s·∫Ω l√† route ri√™ng
import TournamentManagement from './components/admin/TournamentManagement';
import UserManagement from './components/admin/UserManagement'; // Th√™m n·∫øu ch∆∞a c√≥
import NewsManagement from './components/admin/NewsManagement';   // Th√™m n·∫øu ch∆∞a c√≥
import AdminTournamentDetailPage from './components/admin/AdminTournamentDetailPage'; // ƒê·∫£m b·∫£o ƒë∆∞·ªùng d·∫´n ƒë√∫ng


import HelpCenter from './pages/HelpCenter';
import ContactUs from './pages/ContactUs';
import PrivacyPolicy from './pages/PrivacyPolicy';
import TermsOfService from './pages/TermsOfService';
import AboutUs from './pages/AboutUs';


// Protected Route Component
const ProtectedRoute = ({ children }) => {
  const { isAuthenticated, isLoading } = useAuth();

  if (isLoading) {
    return <LoadingSpinner />;
  }

  if (!isAuthenticated) {
    return <Navigate to="/login" replace />;
  }

  return children;
};

// Admin Route Component
const AdminRoute = ({ children }) => {
  const { user, isAuthenticated, isLoading } = useAuth();

  if (isLoading) {
    return <LoadingSpinner />;
  }

  if (!isAuthenticated) {
    return <Navigate to="/login" replace />;
  }

  // Debug log ƒë·ªÉ ki·ªÉm tra vai tr√≤
  console.log('üîí [AdminRoute] User role:', user?.role);
  if (user?.role !== 'ADMIN' && user?.role !== 'ORGANIZER') {
    console.log('‚ùå [AdminRoute] User not authorized. Redirecting to /dashboard');
    return <Navigate to="/dashboard" replace />;
  }

  return children;
};

function App() {
  const { isLoading, isAuthenticated, user } = useAuth();

  // Simple debug logs
  console.log('üé® [App] Render state:', {
    isLoading,
    isAuthenticated,
    userRole: user?.role,
    currentPath: window.location.pathname,
    timestamp: new Date().toLocaleTimeString()
  });

  if (isLoading) {
    console.log('‚è≥ [App] Showing loading spinner');
    return <LoadingSpinner />;
  }

  console.log('‚úÖ [App] Rendering routes');

  return (
    <>
      <Routes>
        {/* Public Routes - s·ª≠ d·ª•ng Layout chung cho ng∆∞·ªùi d√πng */}
        <Route path="/" element={<Layout />}>
          <Route index element={<HomePage />} />
          <Route path="tournaments" element={<TournamentsPage />} />
          <Route path="tournaments/:id" element={<TournamentDetailPage />} />
          <Route path="news" element={<NewsPage />} />
          <Route path="news/:id" element={<NewsDetailPage />} />

          {/* C√°c route cho trang tƒ©nh */}
          <Route path="help-center" element={<HelpCenter />} />
          <Route path="contact-us" element={<ContactUs />} />
          <Route path="privacy-policy" element={<PrivacyPolicy />} />
          <Route path="terms-of-service" element={<TermsOfService />} />
          <Route path="about" element={<AboutUs />} />
        </Route>

        <Route path="/login" element={<LoginPage />} />
        <Route path="/register" element={<RegisterPage />} />

        <Route
          path="/dashboard"
          element={
            <ProtectedRoute>
              <Layout />
            </ProtectedRoute>
          }
        >
          <Route index element={<DashboardPage />} />
          <Route path="profile" element={<ProfilePage />} />
        </Route>

        <Route
          path="/admin"
          element={
            <AdminRoute>
              <AdminLayout />
            </AdminRoute>
          }
        >
          {/* Admin Dashboard: /admin */}
          <Route index element={<AdminPanel />} />
          {/* Qu·∫£n l√Ω gi·∫£i ƒë·∫•u: /admin/tournaments */}
          <Route path="tournaments" element={<TournamentManagement />} />
          {/* Chi ti·∫øt gi·∫£i ƒë·∫•u Admin: /admin/tournaments/:id */}
          <Route path="tournaments/:id" element={<AdminTournamentDetailPage />} />
          {/* Qu·∫£n l√Ω t√†i kho·∫£n: /admin/users */}
          <Route path="users" element={<UserManagement />} />
          {/* Qu·∫£n l√Ω tin t·ª©c: /admin/news */}
          <Route path="news" element={<NewsManagement />} />
          {/* Th√™m c√°c route admin kh√°c n·∫øu c√≥ */}

        </Route>

        {/* Catch all route - Chuy·ªÉn h∆∞·ªõng v·ªÅ trang ch·ªß n·∫øu kh√¥ng t√¨m th·∫•y route */}
        <Route path="*" element={<Navigate to="/" replace />} />
      </Routes>

      {/* Toast Notifications */}
      <Toaster
        position="top-right"
        toastOptions={{
          duration: 4000,
          style: {
            background: '#363636',
            color: '#fff',
          },
          success: {
            duration: 6000,
            iconTheme: {
              primary: '#10B981',
              secondary: '#fff',
            },
          },
          error: {
            duration: 6000,
            iconTheme: {
              primary: '#EF4444',
              secondary: '#fff',
            },
          },
        }}
      />
    </>
  );
}

export default App;
