import React from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import { Toaster } from 'react-hot-toast';
import { useAuth } from './context/AuthContext';

// Layout Components
import Layout from './components/Layout';
import LoadingSpinner from './components/LoadingSpinner';


// Public Pages
import HomePage from './pages/HomePage';
import LoginPage from './pages/LoginPage';
import RegisterPage from './pages/RegisterPage';
import TournamentsPage from './pages/TournamentsPage';
import TournamentDetailPage from './pages/TournamentDetailPage';
import NewsPage from './pages/NewsPage';
import NewsDetailPage from './pages/NewsDetailPage';

// Protected Pages
import DashboardPage from './pages/DashboardPage';
import ProfilePage from './pages/ProfilePage';
import AdminPanel from './pages/AdminPanel';
import TournamentAdminDetailPage from './pages/TournamentAdminDetailPage';

// New Management Pages
import TournamentResultsPage from './pages/TournamentResultsPage';
import RoundManagementPage from './pages/RoundManagementPage';
import TeamManagementPage from './pages/TeamManagementPage';
import TournamentViewerPage from './pages/TournamentViewerPage';
import DebugPage from './pages/DebugPage';

import HelpCenter from './pages/HelpCenter';
import ContactUs from './pages/ContactUs';
import PrivacyPolicy from './pages/PrivacyPolicy';
import TermsOfService from './pages/TermsOfService';
import AboutUs from './pages/AboutUs'; // ƒê·∫£m b·∫£o ƒë√£ import


// Protected Route Component
const ProtectedRoute = ({ children }) => {
  const { isAuthenticated, isLoading } = useAuth();

  if (isLoading) {
    return <LoadingSpinner />;
  }

  if (!isAuthenticated) {
    return <Navigate to="/login" replace />;
  }

  return children;
};

// Admin Route Component
const AdminRoute = ({ children }) => {
  const { user, isAuthenticated, isLoading } = useAuth();

  if (isLoading) {
    return <LoadingSpinner />;
  }

  if (!isAuthenticated) {
    return <Navigate to="/login" replace />;
  }

  if (user?.role !== 'ADMIN' && user?.role !== 'ORGANIZER') {
    return <Navigate to="/dashboard" replace />;
  }

  return children;
};

function App() {
  const { isLoading, isAuthenticated, user } = useAuth();

  // Simple debug logs
  console.log('üé® [App] Render state:', {
    isLoading,
    isAuthenticated,
    userRole: user?.role,
    currentPath: window.location.pathname,
    timestamp: new Date().toLocaleTimeString()
  });

  if (isLoading) {
    console.log('‚è≥ [App] Showing loading spinner');
    return <LoadingSpinner />;
  }

  console.log('‚úÖ [App] Rendering routes');

  return (
    <>
      <Routes>
        {/* Public Routes */}
        <Route path="/" element={<Layout />}>
          <Route index element={<HomePage />} />
          <Route path="tournaments" element={<TournamentsPage />} />
          <Route path="tournaments/:id" element={<TournamentDetailPage />} />
          <Route path="news" element={<NewsPage />} />
          <Route path="news/:id" element={<NewsDetailPage />} />

          {/* C√°c route cho trang tƒ©nh */}
          <Route path="help-center" element={<HelpCenter />} />
          <Route path="contact-us" element={<ContactUs />} />
          <Route path="privacy-policy" element={<PrivacyPolicy />} />
          <Route path="terms-of-service" element={<TermsOfService />} />
          <Route path="about" element={<AboutUs />} />
        </Route>

        {/* Auth Routes - Simple without any wrappers */}
        <Route path="/login" element={<LoginPage />} />
        <Route path="/register" element={<RegisterPage />} />

        {/* Protected Routes */}
        <Route 
          path="/dashboard" 
          element={
            <ProtectedRoute>
              <Layout />
            </ProtectedRoute>
          }
        >
          <Route index element={<DashboardPage />} />
          <Route path="profile" element={<ProfilePage />} />
        </Route>

        {/* Admin Routes */}
        <Route 
          path="/admin" 
          element={
            <AdminRoute>
              <Layout />
            </AdminRoute>
          }
        >
          <Route index element={<AdminPanel />} />
          <Route path="tournaments" element={<AdminPanel />} />
          <Route path="results" element={<TournamentResultsPage />} />
          <Route path="rounds" element={<RoundManagementPage />} />
          <Route path="teams" element={<TeamManagementPage />} />
          <Route path="viewer" element={<TournamentViewerPage />} />
        </Route>

        {/* Admin Tournament Detail - Separate Layout */}
        <Route 
          path="/admin/tournaments/:id" 
          element={
            <AdminRoute>
              <TournamentAdminDetailPage />
            </AdminRoute>
          }
        />

        {/* Debug route - only in development */}
        {process.env.NODE_ENV === 'development' && (
          <Route path="/debug" element={<DebugPage />} />
        )}

        {/* Catch all route */}
        <Route path="*" element={<Navigate to="/" replace />} />
      </Routes>

      {/* Toast Notifications */}
      <Toaster
        position="top-right"
        toastOptions={{
          duration: 4000,
          style: {
            background: '#363636',
            color: '#fff',
          },
          success: {
            duration: 6000,
            iconTheme: {
              primary: '#10B981',
              secondary: '#fff',
            },
          },
          error: {
            duration: 6000,
            iconTheme: {
              primary: '#EF4444',
              secondary: '#fff',
            },
          },
        }}
      />
    </>
  );
}

export default App;
