import React from 'react';
import ReactDOM from 'react-dom/client';
import { BrowserRouter } from 'react-router-dom';
import { QueryClient, QueryClientProvider } from 'react-query';
import './index.css';
import App from './App';
import { AuthProvider } from './context/AuthContext';

// ============================================
// SIMPLE & CLEAN APPROACH - FIX ROOT CAUSE
// ============================================
console.log('üîß Initializing SIMPLE DOM structure fix...');

// Disable React Error Overlay ONLY
if (process.env.NODE_ENV === 'development') {
  // Simple error overlay disable
  window.__REACT_ERROR_OVERLAY_GLOBAL_HOOK__ = {
    isMounted: () => false,
    onBuildError: () => {},
    onBuildOk: () => {},
    onRuntimeError: () => {},
  };
}

// Simple error suppression for console
const originalConsoleError = console.error;
console.error = (...args) => {
  const message = args.join(' ');
  if (message.includes('insertBefore') || message.includes('NotFoundError')) {
    console.debug('üîß [SUPPRESSED] DOM error:', message);
    return;
  }
  originalConsoleError(...args);
};

// Simple Query Client
const queryClient = new QueryClient({
  defaultOptions: {
    queries: {
      retry: 1,
      refetchOnWindowFocus: false,
      staleTime: 5 * 60 * 1000,
      cacheTime: 10 * 60 * 1000,
    },
    mutations: {
      retry: 1,
    }
  },
});

// Simple Error Boundary for critical errors only
class SimpleErrorBoundary extends React.Component {
  constructor(props) {
    super(props);
    this.state = { hasError: false };
  }

  static getDerivedStateFromError(error) {
    // Only catch real application errors, not DOM errors
    if (error && error.message && 
        (error.message.includes('insertBefore') || 
         error.message.includes('commitPlacement') ||
         error.message.includes('NotFoundError'))) {
      console.debug('üîß [IGNORED] DOM manipulation error');
      return { hasError: false }; // Don't show error UI for DOM errors
    }
    return { hasError: true };
  }

  componentDidCatch(error, errorInfo) {
    // Only log real application errors
    if (!(error.message.includes('insertBefore') || 
          error.message.includes('commitPlacement') ||
          error.message.includes('NotFoundError'))) {
      console.error('‚ùå Application Error:', error, errorInfo);
    }
  }

  render() {
    if (this.state.hasError) {
      return (
        <div className="min-h-screen flex items-center justify-center bg-gray-50 p-4">
          <div className="text-center">
            <h3 className="text-lg font-medium text-gray-900 mb-4">
              ƒê√£ x·∫£y ra l·ªói ·ª©ng d·ª•ng
            </h3>
            <button
              onClick={() => window.location.reload()}
              className="bg-blue-600 text-white px-4 py-2 rounded-md text-sm"
            >
              T·∫£i l·∫°i trang
            </button>
          </div>
        </div>
      );
    }

    return this.props.children;
  }
}

// Create root
const root = ReactDOM.createRoot(document.getElementById('root'));

// SIMPLE RENDER - NO NESTED ERROR BOUNDARIES
root.render(
  <SimpleErrorBoundary>
    <QueryClientProvider client={queryClient}>
      <BrowserRouter>
        <AuthProvider>
          <App />
        </AuthProvider>
      </BrowserRouter>
    </QueryClientProvider>
  </SimpleErrorBoundary>
);

console.log('üîß Simple DOM structure initialized');
console.log('üîß Tournament creation should work without DOM errors');
