import React, { useState, useCallback, useRef, useMemo } from 'react';
import { useForm } from 'react-hook-form';
import { useMutation, useQueryClient } from 'react-query';
import { X, Trophy } from 'lucide-react';
import { tournamentService } from '../../services';
import { 
  SPORT_TYPES, 
  SPORT_TYPE_LABELS,
  TOURNAMENT_STATUS,
  VALIDATION_RULES,
  QUERY_KEYS
} from '../../utils/constants';
import { formatDateTimeForInput } from '../../utils/helpers';

// ULTRA-SIMPLE DATE FORMATTERS FOR JAVA LocalDateTime
const formatDateForJava = (date) => {
  if (!date) return null;

  try {
    const d = new Date(date);
    if (isNaN(d.getTime())) return null;

    const year = d.getFullYear();
    const month = String(d.getMonth() + 1).padStart(2, '0');
    const day = String(d.getDate()).padStart(2, '0');
    const hours = String(d.getHours()).padStart(2, '0');
    const minutes = String(d.getMinutes()).padStart(2, '0');
    const seconds = String(d.getSeconds()).padStart(2, '0');

    // Simple format without milliseconds: YYYY-MM-DDTHH:mm:ss
    return `${year}-${month}-${day}T${hours}:${minutes}:${seconds}`;
  } catch (error) {
    console.error('Date formatting error:', error);
    return null;
  }
};

const TournamentCreateFormFinalFix = ({ isOpen, onClose, onSuccess }) => {
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState('');
  const formRef = useRef(null);
  const queryClient = useQueryClient();

  const {
    register,
    handleSubmit,
    watch,
    reset,
    formState: { errors: formErrors }
  } = useForm({
    defaultValues: {
      name: '',
      description: '',
      sportType: 'FOOTBALL',
      maxTeams: 16,
      startDate: '',
      endDate: '',
      registrationDeadline: '',
      location: '',
      rules: '',
      prizeInfo: '',
      contactInfo: ''
    },
    mode: 'onChange'
  });

  // ULTRA-SAFE notification - no DOM manipulation at all
  const showError = useCallback((message) => {
    console.error('‚ùå ERROR:', message);
    setError(message);

    // Use setTimeout to avoid any DOM conflict
    setTimeout(() => {
      try {
        alert(`‚ùå ${message}`);
      } catch (e) {
        console.error('Alert error:', e);
      }
    }, 200);
  }, []);

  // ULTRA-SAFE success handler
  const handleSuccessComplete = useCallback((response) => {
    console.log('‚úÖ SUCCESS: Tournament created, starting safe cleanup...');

    try {
      // Step 1: Show success message immediately
      console.log('üì¢ Step 1: Success notification');

      // Step 2: Start safe async cleanup
      setTimeout(() => {
        try {
          console.log('üßπ Step 2: Safe form cleanup');
          setIsSubmitting(false);
          setError('');

          // Step 3: Safe form reset
          setTimeout(() => {
            try {
              console.log('üîÑ Step 3: Form reset');
              reset();

              // Step 4: Safe query invalidation (non-blocking)
              setTimeout(() => {
                try {
                  console.log('üîÑ Step 4: Query invalidation (async)');
                  queryClient.invalidateQueries(QUERY_KEYS.TOURNAMENTS).catch((err) => {
                    console.warn('Query invalidation warning (non-critical):', err);
                  });
                } catch (e) {
                  console.warn('Query invalidation error (non-critical):', e);
                }
              }, 300);

              // Step 5: Safe callback execution
              setTimeout(() => {
                try {
                  console.log('üìû Step 5: Success callback');
                  if (onSuccess && typeof onSuccess === 'function') {
                    onSuccess(response?.data || response);
                  }
                } catch (e) {
                  console.warn('Success callback error (non-critical):', e);
                }
              }, 400);

              // Step 6: Safe form close
              setTimeout(() => {
                try {
                  console.log('üö™ Step 6: Close form');
                  if (onClose && typeof onClose === 'function') {
                    onClose();
                  }
                } catch (e) {
                  console.warn('Close callback error (non-critical):', e);
                  // Force close by setting window location if needed
                  try {
                    window.location.reload();
                  } catch (reloadError) {
                    console.warn('Reload fallback failed:', reloadError);
                  }
                }
              }, 500);

            } catch (resetError) {
              console.warn('Form reset error:', resetError);
              // Force close
              if (onClose) onClose();
            }
          }, 200);

        } catch (cleanupError) {
          console.warn('Cleanup error:', cleanupError);
          // Force close
          if (onClose) onClose();
        }
      }, 100);

    } catch (mainError) {
      console.error('Main success handler error:', mainError);
      // Emergency close
      setIsSubmitting(false);
      if (onClose) onClose();
    }
  }, [queryClient, onSuccess, onClose, reset]);

  const createTournamentMutation = useMutation(
    async (tournamentData) => {
      console.log('üöÄ Creating tournament with data:', tournamentData);

      // MULTIPLE FORMAT ATTEMPTS for Java LocalDateTime compatibility
      const formatAttempts = [
        // Format 1: Simple without milliseconds (most likely to work)
        {
          name: 'Simple ISO',
          formatter: (date) => {
            const d = new Date(date);
            const year = d.getFullYear();
            const month = String(d.getMonth() + 1).padStart(2, '0');
            const day = String(d.getDate()).padStart(2, '0');
            const hours = String(d.getHours()).padStart(2, '0');
            const minutes = String(d.getMinutes()).padStart(2, '0');
            const seconds = String(d.getSeconds()).padStart(2, '0');
            return `${year}-${month}-${day}T${hours}:${minutes}:${seconds}`;
          }
        },
        // Format 2: Space separated (fallback)
        {
          name: 'Space separated',
          formatter: (date) => {
            const d = new Date(date);
            const year = d.getFullYear();
            const month = String(d.getMonth() + 1).padStart(2, '0');
            const day = String(d.getDate()).padStart(2, '0');
            const hours = String(d.getHours()).padStart(2, '0');
            const minutes = String(d.getMinutes()).padStart(2, '0');
            const seconds = String(d.getSeconds()).padStart(2, '0');
            return `${year}-${month}-${day} ${hours}:${minutes}:${seconds}`;
          }
        },
        // Format 3: ISO slice (another common format)
        {
          name: 'ISO slice',
          formatter: (date) => {
            return new Date(date).toISOString().slice(0, 19);
          }
        },
        // Format 4: Array format (for specific Java configurations)
        {
          name: 'Array format',
          formatter: (date) => {
            const d = new Date(date);
            return [d.getFullYear(), d.getMonth() + 1, d.getDate(), 
                   d.getHours(), d.getMinutes(), d.getSeconds()];
          }
        }
      ];

      let lastError = null;

      for (let i = 0; i < formatAttempts.length; i++) {
        const attempt = formatAttempts[i];

        try {
          console.log(`üîÑ Trying format ${i + 1}: ${attempt.name}`);

          const dataWithFormattedDates = {
            ...tournamentData,
            startDate: attempt.formatter(tournamentData.startDate),
            endDate: attempt.formatter(tournamentData.endDate),
            registrationDeadline: attempt.formatter(tournamentData.registrationDeadline)
          };

          console.log(`üì§ Sending data with format ${i + 1}:`, {
            startDate: dataWithFormattedDates.startDate,
            endDate: dataWithFormattedDates.endDate,
            registrationDeadline: dataWithFormattedDates.registrationDeadline
          });

          const result = await tournamentService.createTournament(dataWithFormattedDates);
          console.log(`‚úÖ SUCCESS with format ${i + 1}: ${attempt.name}!`);
          return result;

        } catch (error) {
          console.log(`‚ùå Format ${i + 1} (${attempt.name}) failed:`, error.message);
          lastError = error;

