import React, { useState } from 'react';
import { useMutation, useQueryClient } from 'react-query';
import { tournamentService } from '../../services';

// ULTRA SIMPLE Tournament Form - NO complex hooks, NO complex structure
const TournamentCreateFormUltraSimple = ({ isOpen, onClose, onSuccess }) => {
  // Simple state only
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    sportType: 'FOOTBALL',
    maxTeams: 16,
    startDate: '',
    endDate: '',
    registrationDeadline: '',
    location: '',
    rules: '',
    prizeInfo: '',
    contactInfo: ''
  });

  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);
  const [tournamentId, setTournamentId] = useState(null);

  const queryClient = useQueryClient();

  // Simple mutation
  const createMutation = useMutation(tournamentService.createTournament, {
    onSuccess: (response) => {
      console.log('✅ Tournament created:', response);
      setTournamentId(response?.id || 'Unknown');
      setSuccess(true);
      setIsSubmitting(false);
    },
    onError: (error) => {
      console.error('❌ Creation failed:', error);
      setError(error.message || 'Failed to create tournament');
      setIsSubmitting(false);
    }
  });

  // Simple handlers
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (isSubmitting) return;

    setIsSubmitting(true);
    setError('');

    try {
      // Basic validation
      if (!formData.name || !formData.description || !formData.location || !formData.contactInfo) {
        setError('Please fill all required fields');
        setIsSubmitting(false);
        return;
      }

      if (!formData.startDate || !formData.endDate || !formData.registrationDeadline) {
        setError('Please select all dates');
        setIsSubmitting(false);
        return;
      }

      // Format dates for backend
      const formatArray = (dateString) => {
        const d = new Date(dateString);
        return [d.getFullYear(), d.getMonth() + 1, d.getDate(), 
               d.getHours(), d.getMinutes(), d.getSeconds()];
      };

      const dataToSend = {
        name: formData.name.trim(),
        description: formData.description.trim(),
        sportType: formData.sportType,
        maxTeams: parseInt(formData.maxTeams),
        startDate: formatArray(formData.startDate),
        endDate: formatArray(formData.endDate),
        registrationDeadline: formatArray(formData.registrationDeadline),
        location: formData.location.trim(),
        rules: '',
        prizeInfo: '',
        contactInfo: formData.contactInfo.trim()
      };

      console.log('📤 Sending data:', dataToSend);

      await createMutation.mutateAsync(dataToSend);

    } catch (err) {
      console.error('💥 Submit error:', err);
      setError(err.message || 'Submission failed');
      setIsSubmitting(false);
    }
  };

  const handleClose = () => {
    setFormData({
      name: '',
      description: '',
      sportType: 'FOOTBALL',
      maxTeams: 16,
      startDate: '',
      endDate: '',
      registrationDeadline: '',
      location: '',
      contactInfo: ''
    });
    setError('');
    setSuccess(false);
    setTournamentId(null);
    setIsSubmitting(false);

    if (onClose) onClose();
  };

  const handleSuccessConfirm = () => {
    queryClient.invalidateQueries('tournaments').catch(() => {});

    if (onSuccess) {
      onSuccess({ id: tournamentId });
    }

    handleClose();
  };

  if (!isOpen) return null;

  // Success state
  if (success) {
    return (
      <div style={{
        position: 'fixed',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        backgroundColor: 'rgba(0,0,0,0.5)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        zIndex: 1000,
        padding: '20px'
      }}>
        <div style={{
          backgroundColor: 'white',
          borderRadius: '8px',
          padding: '24px',
          maxWidth: '400px',
          width: '100%',
          textAlign: 'center'
        }}>
          <div style={{ color: '#10b981', fontSize: '24px', marginBottom: '16px' }}>
            ✅
          </div>
          <h3 style={{ color: '#1f2937', marginBottom: '8px', fontSize: '18px' }}>
            Tournament Created Successfully!
          </h3>
          <p style={{ color: '#6b7280', marginBottom: '16px', fontSize: '14px' }}>
            Tournament ID: #{tournamentId}
          </p>
          <button
            onClick={handleSuccessConfirm}
            style={{
              backgroundColor: '#10b981',
              color: 'white',
              border: 'none',
              borderRadius: '6px',
              padding: '8px 16px',
              fontSize: '14px',
              cursor: 'pointer'
            }}
          >
            Back to Admin Panel
          </button>
        </div>
      </div>
    );
  }

  // Main form
  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      backgroundColor: 'rgba(0,0,0,0.5)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      zIndex: 1000,
      padding: '20px',
      overflow: 'auto'
    }}>
      <div style={{
        backgroundColor: 'white',
        borderRadius: '8px',
        maxWidth: '600px',
        width: '100%',
        maxHeight: '90vh',
        overflow: 'auto'
      }}>
        {/* Header */}
        <div style={{
          padding: '20px',
          borderBottom: '1px solid #e5e7eb',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center'
        }}>
          <h2 style={{ margin: 0, color: '#1f2937', fontSize: '20px' }}>
            Create Tournament
          </h2>
          <button
            onClick={handleClose}
            disabled={isSubmitting}
            style={{
              background: 'none',
              border: 'none',
              fontSize: '20px',
              cursor: 'pointer',
              color: '#6b7280'
            }}
          >
            ×
          </button>
        </div>

        {/* Error */}
        {error && (
          <div style={{
            padding: '16px 20px',
            backgroundColor: '#fef2f2',
            borderBottom: '1px solid #fecaca',
            color: '#dc2626',
            fontSize: '14px'
          }}>
            ❌ {error}
          </div>
        )}

        {/* Form */}
        <form onSubmit={handleSubmit} style={{ padding: '20px' }}>
          <div style={{ marginBottom: '16px' }}>
            <label style={{ display: 'block', marginBottom: '4px', fontSize: '14px', fontWeight: '500' }}>
              Tournament Name *
            </label>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleInputChange}
              disabled={isSubmitting}
              required
              style={{
                width: '100%',
                padding: '8px 12px',
                border: '1px solid #d1d5db',
                borderRadius: '6px',
                fontSize: '14px',
                boxSizing: 'border-box'
              }}
              placeholder="Enter tournament name"
            />
          </div>

          <div style={{ marginBottom: '16px' }}>
            <label style={{ display: 'block', marginBottom: '4px', fontSize: '14px', fontWeight: '500' }}>
              Description *
            </label>
            <textarea
              name="description"
              value={formData.description}
              onChange={handleInputChange}
              disabled={isSubmitting}
              required
              rows={3}
              style={{
                width: '100%',
                padding: '8px 12px',
                border: '1px solid #d1d5db',
                borderRadius: '6px',
                fontSize: '14px',
                boxSizing: 'border-box',
                resize: 'vertical'
              }}
              placeholder="Describe the tournament"
            />
          </div>

          <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '16px', marginBottom: '16px' }}>
            <div>
              <label style={{ display: 'block', marginBottom: '4px', fontSize: '14px', fontWeight: '500' }}>
                Sport Type *
              </label>
              <select
                name="sportType"
                value={formData.sportType}
                onChange={handleInputChange}
                disabled={isSubmitting}
                style={{
                  width: '100%',
                  padding: '8px 12px',
                  border: '1px solid #d1d5db',
                  borderRadius: '6px',
                  fontSize: '14px',
                  boxSizing: 'border-box'
                }}
              >
                <option value="FOOTBALL">Football</option>
                <option value="BASKETBALL">Basketball</option>
                <option value="VOLLEYBALL">Volleyball</option>
                <option value="BADMINTON">Badminton</option>
                <option value="TENNIS">Tennis</option>
                <option value="PING_PONG">Ping Pong</option>
                <option value="GENERAL">General</option>
              </select>
            </div>

            <div>
              <label style={{ display: 'block', marginBottom: '4px', fontSize: '14px', fontWeight: '500' }}>
                Max Teams *
              </label>
              <select
                name="maxTeams"
                value={formData.maxTeams}
                onChange={handleInputChange}
                disabled={isSubmitting}
                style={{
                  width: '100%',
                  padding: '8px 12px',
                  border: '1px solid #d1d5db',
                  borderRadius: '6px',
                  fontSize: '14px',
                  boxSizing: 'border-box'
                }}
              >
                <option value={4}>4 teams</option>
                <option value={8}>8 teams</option>
                <option value={16}>16 teams</option>
                <option value={32}>32 teams</option>
                <option value={64}>64 teams</option>
              </select>
            </div>
          </div>

          <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr 1fr', gap: '16px', marginBottom: '16px' }}>
            <div>
              <label style={{ display: 'block', marginBottom: '4px', fontSize: '14px', fontWeight: '500' }}>
                Start Date *
              </label>
              <input
                type="datetime-local"
                name="startDate"
                value={formData.startDate}
                onChange={handleInputChange}
                disabled={isSubmitting}
                required
                style={{
                  width: '100%',
                  padding: '8px 12px',
                  border: '1px solid #d1d5db',
                  borderRadius: '6px',
                  fontSize: '14px',
                  boxSizing: 'border-box'
                }}
              />
            </div>

            <div>
              <label style={{ display: 'block', marginBottom: '4px', fontSize: '14px', fontWeight: '500' }}>
                End Date *
              </label>
              <input
                type="datetime-local"
                name="endDate"
                value={formData.endDate}
                onChange={handleInputChange}
                disabled={isSubmitting}
                required
                style={{
                  width: '100%',
                  padding: '8px 12px',
                  border: '1px solid #d1d5db',
                  borderRadius: '6px',
                  fontSize: '14px',
                  boxSizing: 'border-box'
                }}
              />
            </div>

            <div>
              <label style={{ display: 'block', marginBottom: '4px', fontSize: '14px', fontWeight: '500' }}>
                Registration Deadline *
              </label>
              <input
                type="datetime-local"
                name="registrationDeadline"
                value={formData.registrationDeadline}
                onChange={handleInputChange}
                disabled={isSubmitting}
                required
                style={{
                  width: '100%',
                  padding: '8px 12px',
                  border: '1px solid #d1d5db',
                  borderRadius: '6px',
                  fontSize: '14px',
                  boxSizing: 'border-box'
                }}
              />
            </div>
          </div>

          <div style={{ marginBottom: '16px' }}>
            <label style={{ display: 'block', marginBottom: '4px', fontSize: '14px', fontWeight: '500' }}>
              Location *
            </label>
            <input
              type="text"
              name="location"
              value={formData.location}
              onChange={handleInputChange}
              disabled={isSubmitting}
              required
              style={{
                width: '100%',
                padding: '8px 12px',
                border: '1px solid #d1d5db',
                borderRadius: '6px',
                fontSize: '14px',
                boxSizing: 'border-box'
              }}
              placeholder="Tournament location"
            />
          </div>

          <div style={{ marginBottom: '24px' }}>
            <label style={{ display: 'block', marginBottom: '4px', fontSize: '14px', fontWeight: '500' }}>
              Contact Info *
            </label>
            <input
              type="text"
              name="contactInfo"
              value={formData.contactInfo}
              onChange={handleInputChange}
              disabled={isSubmitting}
              required
              style={{
                width: '100%',
                padding: '8px 12px',
                border: '1px solid #d1d5db',
                borderRadius: '6px',
                fontSize: '14px',
                boxSizing: 'border-box'
              }}
              placeholder="Email or phone number"
            />
          </div>

          {/* Footer */}
          <div style={{ display: 'flex', justifyContent: 'flex-end', gap: '12px' }}>
            <button
              type="button"
              onClick={handleClose}
              disabled={isSubmitting}
              style={{
                padding: '8px 16px',
                border: '1px solid #d1d5db',
                borderRadius: '6px',
                backgroundColor: 'white',
                color: '#374151',
                fontSize: '14px',
                cursor: 'pointer'
              }}
            >
              Cancel
            </button>
            <button
              type="submit"
              disabled={isSubmitting}
              style={{
                padding: '8px 16px',
                border: 'none',
                borderRadius: '6px',
                backgroundColor: isSubmitting ? '#9ca3af' : '#3b82f6',
                color: 'white',
                fontSize: '14px',
                cursor: isSubmitting ? 'not-allowed' : 'pointer'
              }}
            >
              {isSubmitting ? 'Creating...' : 'Create Tournament'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default TournamentCreateFormUltraSimple;
