import React, { useState, useEffect } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { useQuery, useMutation, useQueryClient } from 'react-query';
import {
  Calendar,
  Users,
  MapPin,
  Trophy,
  ArrowLeft,
  Clock,
  Target,
  Award,
  Settings, // Icon cho tab Thiáº¿t láº­p & Äiá»u khiá»ƒn
  Play,
  UserPlus,
  Info,
  ListChecks,
  Save,         // Icon cho nÃºt LÆ°u
  RotateCcw,    // Icon cho nÃºt Nháº­p láº¡i
  List,         // Icon cho Danh sÃ¡ch mÃ¹a giáº£i
  Upload,       // Icon cho chá»n áº£nh
  Share2        // Cho chá»©c nÄƒng di chuyá»ƒn mÃ¹a giáº£i (náº¿u cÃ³)
} from 'lucide-react';
import { tournamentService, teamService, matchService } from '../../services';
import { useAuth } from '../../context/AuthContext';
import LoadingSpinner from '../LoadingSpinner';
import TournamentBracketGenerator from '../tournament/TournamentBracketGenerator';
import RoundManager from '../tournament/RoundManager';
import TournamentBracketView from '../tournament/TournamentBracketView';
import MatchResultsManager from '../tournament/MatchResultsManager';
import TeamRegistrationModal from '../tournament/TeamRegistrationModal';
import TournamentWorkflowGuide from '../tournament/TournamentWorkflowGuide';
import { formatDate, formatDateTime, getStatusColor, getTournamentStatusLabel } from '../../utils/helpers';
import toast from 'react-hot-toast';
import { QUERY_KEYS } from '../../utils/constants'; // Äáº£m báº£o QUERY_KEYS cÃ³ TOURNAMENTS


// Giáº£ sá»­ cÃ³ má»™t áº£nh Ä‘áº¡i diá»‡n máº·c Ä‘á»‹nh cho logo vÃ  background
const DEFAULT_LOGO_PLACEHOLDER = 'https://via.placeholder.com/150x150?text=Logo+Giáº£i+Äáº¥u';
const DEFAULT_BACKGROUND_PLACEHOLDER = 'https://via.placeholder.com/400x200?text=áº¢nh+Ná»n+Giáº£i+Äáº¥u';


const AdminTournamentDetailPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { user } = useAuth();
  const queryClient = useQueryClient();
  const [activeTab, setActiveTab] = useState('overview'); // Tab máº·c Ä‘á»‹nh lÃ  'overview'
  const [showRegistrationModal, setShowRegistrationModal] = useState(false);
  const [showWorkflowGuide, setShowWorkflowGuide] = useState(false);

  // State cho form data cá»§a pháº§n cáº¥u hÃ¬nh giáº£i Ä‘áº¥u
  const [configFormData, setConfigFormData] = useState({
    name: '',
    startDate: '',
    endDate: '',
    maxTeams: 0,
    websiteColor: '#B81E1F',
    tournamentFormat: '',
    sportType: '',
    tournamentType: '',
    winPoints: 3,
    drawPoints: 1,
    losePoints: 0,
    onlineRegistration: true,
    thirdPlaceMatch: true,
    playersPerTeam: 0,
    coachesPerTeam: 0,
    logo: DEFAULT_LOGO_PLACEHOLDER,
    background: DEFAULT_BACKGROUND_PLACEHOLDER,
    location: '',
    description: '',
    rules: '', // ThÃªm trÆ°á»ng rules
    type: '', // ThÃªm trÆ°á»ng type (loáº¡i hÃ¬nh giáº£i Ä‘áº¥u)
    organizer: '', // ThÃªm trÆ°á»ng organizer
  });

  // Chá»‰ cho phÃ©p admin vÃ  organizer truy cáº­p trang nÃ y
  const isAdmin = user?.role === 'ADMIN' || user?.role === 'ORGANIZER';

  // Äiá»u hÆ°á»›ng náº¿u khÃ´ng pháº£i admin/organizer
  useEffect(() => {
    if (!isAdmin) {
      toast.error('Báº¡n khÃ´ng cÃ³ quyá»n truy cáº­p trang quáº£n lÃ½ giáº£i Ä‘áº¥u nÃ y.');
      navigate('/dashboard'); // Chuyá»ƒn hÆ°á»›ng vá» trang dashboard hoáº·c trang khÃ¡c
    }
  }, [isAdmin, navigate]);

  // Query Ä‘á»ƒ láº¥y dá»¯ liá»‡u giáº£i Ä‘áº¥u
  const { data: tournament, isLoading: tournamentLoading, refetch: refetchTournament } = useQuery(
    ['tournament', id],
    () => tournamentService.getTournamentById(id),
    {
      staleTime: 1000,
      enabled: !!id,
      onSuccess: (data) => {
        // Cáº­p nháº­t configFormData khi dá»¯ liá»‡u Ä‘Æ°á»£c fetch thÃ nh cÃ´ng
        setConfigFormData({
          name: data.name || '',
          startDate: data.startDate ? new Date(data.startDate).toISOString().split('T')[0] : '',
          endDate: data.endDate ? new Date(data.endDate).toISOString().split('T')[0] : '',
          maxTeams: data.maxTeams || 0,
          websiteColor: data.websiteColor || '#B81E1F',
          tournamentFormat: data.tournamentFormat || '',
          sportType: data.sportType || '',
          tournamentType: data.tournamentType || 'TEAM',
          winPoints: data.winPoints !== undefined ? data.winPoints : 3,
          drawPoints: data.drawPoints !== undefined ? data.drawPoints : 1,
          losePoints: data.losePoints !== undefined ? data.losePoints : 0,
          onlineRegistration: data.onlineRegistration !== undefined ? data.onlineRegistration : true,
          thirdPlaceMatch: data.thirdPlaceMatch !== undefined ? data.thirdPlaceMatch : true,
          playersPerTeam: data.playersPerTeam || 0,
          coachesPerTeam: data.coachesPerTeam || 0,
          logo: data.logo || DEFAULT_LOGO_PLACEHOLDER,
          background: data.background || DEFAULT_BACKGROUND_PLACEHOLDER,
          location: data.location || '',
          description: data.description || '',
          rules: data.rules || '',
          type: data.type || 'TiÃªu chuáº©n', // GiÃ¡ trá»‹ máº·c Ä‘á»‹nh
          organizer: data.organizer || 'EduSports', // GiÃ¡ trá»‹ máº·c Ä‘á»‹nh
        });
      },
      onError: (err) => {
        toast.error('Lá»—i khi táº£i dá»¯ liá»‡u giáº£i Ä‘áº¥u!');
        console.error('Error fetching tournament:', err);
      }
    }
  );

  // Mutation Ä‘á»ƒ cáº­p nháº­t giáº£i Ä‘áº¥u
  const updateTournamentMutation = useMutation(
    (updatedData) => tournamentService.updateTournament(id, updatedData),
    {
      onSuccess: () => {
        toast.success('Cáº­p nháº­t giáº£i Ä‘áº¥u thÃ nh cÃ´ng!');
        queryClient.invalidateQueries(['tournament', id]); // Invalidates cache cho giáº£i Ä‘áº¥u nÃ y
        queryClient.invalidateQueries(QUERY_KEYS.TOURNAMENTS); // Invalidates cache cho danh sÃ¡ch
        refetchTournament(); // Táº£i láº¡i dá»¯ liá»‡u sau khi cáº­p nháº­t
      },
      onError: (err) => {
        toast.error(err.response?.data?.message || 'Cáº­p nháº­t giáº£i Ä‘áº¥u tháº¥t báº¡i!');
        console.error('Error updating tournament:', err);
      }
    }
  );


  const { data: teams, isLoading: teamsLoading, refetch: refetchTeams } = useQuery(
    ['tournament-teams', id],
    () => teamService.getTeamsByTournament(id),
    {
      staleTime: 1000,
      enabled: !!id,
      onSuccess: (data) => {
        // console.log('ðŸ‘¥ [AdminTournamentDetailPage] Dá»¯ liá»‡u Ä‘á»™i táº£i thÃ nh cÃ´ng:', data);
      },
      onError: (error) => {
        console.error('âŒ [AdminTournamentDetailPage] Táº£i dá»¯ liá»‡u Ä‘á»™i tháº¥t báº¡i:', error);
        toast.error('Lá»—i khi táº£i danh sÃ¡ch Ä‘á»™i.');
      }
    }
  );

  const { data: matches, isLoading: matchesLoading, refetch: refetchMatches } = useQuery(
    ['tournament-matches', id],
    () => matchService.getMatchesByTournament(id),
    {
      staleTime: 1000,
      enabled: !!id
    }
  );

  const { data: currentRoundData, isLoading: currentRoundLoading, error: currentRoundError } = useQuery(
    ['tournament-current-round', id],
    () => {
      // console.log('ðŸš€ [getCurrentRound] API call triggered for tournament:', id);
      return tournamentService.getCurrentRound(id);
    },
    {
      staleTime: 1000,
      enabled: !!id,
      onSuccess: (data) => {
        // console.log('âœ… [getCurrentRound] API success:', data);
      },
      onError: (error) => {
        console.error('âŒ [getCurrentRound] API error:', error);
        toast.error('Lá»—i khi láº¥y thÃ´ng tin vÃ²ng hiá»‡n táº¡i.');
      }
    }
  );

  const { data: bracket, isLoading: bracketLoading, refetch: refetchBracket } = useQuery(
    ['tournament-bracket', id],
    () => matchService.getTournamentBracket(id),
    {
      staleTime: 1000,
      enabled: !!id && (tournament?.data?.status === 'READY' || tournament?.data?.status === 'ONGOING' || tournament?.data?.status === 'COMPLETED')
    }
  );

  // --- Logic xá»­ lÃ½ form cáº¥u hÃ¬nh ---
  const handleConfigFormChange = (e) => {
    const { name, value, type, checked } = e.target;
    setConfigFormData((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  const handleFileChange = (e, fieldName) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setConfigFormData((prev) => ({
          ...prev,
          [fieldName]: reader.result, // LÆ°u base64 hoáº·c URL táº¡m thá»i
        }));
      };
      reader.readAsDataURL(file);
      toast('Chá»©c nÄƒng táº£i áº£nh cáº§n Ä‘Æ°á»£c tÃ­ch há»£p API thá»±c táº¿!', { icon: 'âš ï¸' });
    }
  };

  const handleConfigFormSubmit = (e) => {
    e.preventDefault();
    const dataToSubmit = { ...configFormData };

    // Convert numbers
    dataToSubmit.maxTeams = Number(dataToSubmit.maxTeams);
    dataToSubmit.winPoints = Number(dataToSubmit.winPoints);
    dataToSubmit.drawPoints = Number(dataToSubmit.drawPoints);
    dataToSubmit.losePoints = Number(dataToSubmit.losePoints);
    dataToSubmit.playersPerTeam = Number(dataToSubmit.playersPerTeam);
    dataToSubmit.coachesPerTeam = Number(dataToSubmit.coachesPerTeam);

    // Xá»­ lÃ½ cÃ¡c URL base64 táº¡m thá»i (náº¿u cÃ³)
    // Náº¿u báº¡n cÃ³ logic upload file riÃªng, hÃ£y gá»i nÃ³ á»Ÿ Ä‘Ã¢y
    if (dataToSubmit.logo.startsWith('data:') && dataToSubmit.logo !== DEFAULT_LOGO_PLACEHOLDER) {
      // Thá»±c hiá»‡n upload logo lÃªn server vÃ  láº¥y URL tháº­t
      // VÃ­ dá»¥: uploadFileService.upload(dataToSubmit.logo).then(url => dataToSubmit.logo = url);
      delete dataToSubmit.logo; // XÃ³a táº¡m thá»i Ä‘á»ƒ khÃ´ng gá»­i base64 náº¿u khÃ´ng pháº£i logic upload thá»±c táº¿
    }
    if (dataToSubmit.background.startsWith('data:') && dataToSubmit.background !== DEFAULT_BACKGROUND_PLACEHOLDER) {
      // Thá»±c hiá»‡n upload background lÃªn server vÃ  láº¥y URL tháº­t
      delete dataToSubmit.background; // XÃ³a táº¡m thá»i
    }

    updateTournamentMutation.mutate(dataToSubmit);
  };

  const handleConfigFormReset = () => {
    if (tournament?.data) {
      setConfigFormData({
        name: tournament.data.name || '',
        startDate: tournament.data.startDate ? new Date(tournament.data.startDate).toISOString().split('T')[0] : '',
        endDate: tournament.data.endDate ? new Date(tournament.data.endDate).toISOString().split('T')[0] : '',
        maxTeams: tournament.data.maxTeams || 0,
        websiteColor: tournament.data.websiteColor || '#B81E1F',
        tournamentFormat: tournament.data.tournamentFormat || '',
        sportType: tournament.data.sportType || '',
        tournamentType: tournament.data.tournamentType || 'TEAM',
        winPoints: tournament.data.winPoints !== undefined ? tournament.data.winPoints : 3,
        drawPoints: tournament.data.drawPoints !== undefined ? tournament.data.drawPoints : 1,
        losePoints: tournament.data.losePoints !== undefined ? tournament.data.losePoints : 0,
        onlineRegistration: tournament.data.onlineRegistration !== undefined ? tournament.data.onlineRegistration : true,
        thirdPlaceMatch: tournament.data.thirdPlaceMatch !== undefined ? tournament.data.thirdPlaceMatch : true,
        playersPerTeam: tournament.data.playersPerTeam || 0,
        coachesPerTeam: tournament.data.coachesPerTeam || 0,
        logo: tournament.data.logo || DEFAULT_LOGO_PLACEHOLDER,
        background: tournament.data.background || DEFAULT_BACKGROUND_PLACEHOLDER,
        location: tournament.data.location || '',
        description: tournament.data.description || '',
        rules: tournament.data.rules || '',
        type: tournament.data.type || 'TiÃªu chuáº©n',
        organizer: tournament.data.organizer || 'EduSports',
      });
      toast.info('Form cáº¥u hÃ¬nh Ä‘Ã£ Ä‘Æ°á»£c Ä‘áº·t láº¡i!');
    }
  };
  // --- Káº¿t thÃºc Logic xá»­ lÃ½ form cáº¥u hÃ¬nh ---


  const handleBracketGenerated = (bracketData) => {
    refetchTournament();
    refetchBracket();
    refetchMatches();
    toast.success('ÄÃ£ táº¡o cÃ¢y thi Ä‘áº¥u thÃ nh cÃ´ng!');
  };

  const handleRoundAdvanced = (roundData) => {
    refetchMatches();
    refetchBracket();
    refetchTournament();
    queryClient.invalidateQueries(['tournament-current-round', id]);
    toast.success('ÄÃ£ chuyá»ƒn sang vÃ²ng tiáº¿p theo!');
  };

  const handleMatchResultUpdated = () => {
    refetchMatches();
    refetchBracket();
    refetchTournament();
    queryClient.invalidateQueries(['tournament-current-round', id]);
    toast.success('Káº¿t quáº£ tráº­n Ä‘áº¥u Ä‘Ã£ Ä‘Æ°á»£c cáº­p nháº­t!');
  };


  if (tournamentLoading || !isAdmin) {
    return <LoadingSpinner text="Äang táº£i chi tiáº¿t giáº£i Ä‘áº¥u..." />;
  }

  if (!tournament?.data) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <h2 className="text-2xl font-bold text-gray-900 mb-2">KhÃ´ng tÃ¬m tháº¥y giáº£i Ä‘áº¥u</h2>
          <Link to="/admin/tournaments" className="text-primary-600 hover:text-primary-700">
            â† Quay láº¡i danh sÃ¡ch giáº£i Ä‘áº¥u
          </Link>
        </div>
      </div>
    );
  }

  const tournamentData = tournament.data; // Dá»¯ liá»‡u giáº£i Ä‘áº¥u gá»‘c

  // Safe extraction of matches data
  const matchesData = matches?.data || matches || {};

  // Logic tÃ­nh toÃ¡n currentRound (giá»¯ nguyÃªn tá»« code cÅ© cá»§a báº¡n)
  let currentRound = 1;
  if (currentRoundData?.data?.data?.currentRound) {
    currentRound = currentRoundData.data.data.currentRound;
  } else if (currentRoundData?.data?.currentRound) {
    currentRound = currentRoundData.data.currentRound;
  } else if (tournamentData.currentRound) {
    currentRound = tournamentData.currentRound;
  } else if (bracket?.data?.currentRound) {
    currentRound = bracket.data.currentRound;
  } else if (matchesData?.matches?.length > 0) {
    const rounds = matchesData.matches.map(m => m.round || 1);
    const maxRound = Math.max(...rounds);

    const incompleteRounds = [];
    for (let round = 1; round <= maxRound; round++) {
      const roundMatches = matchesData.matches.filter(m => (m.round || 1) === round);
      const completed = roundMatches.filter(m => m.status === 'COMPLETED').length;
      if (completed < roundMatches.length) {
        incompleteRounds.push(round);
      }
    }

    if (incompleteRounds.length > 0) {
      currentRound = Math.min(...incompleteRounds);
    } else {
      currentRound = maxRound + 1;
    }
  }

  const matchesList = matchesData?.matches || [];
  const roundMatches = matchesList.filter(match => match.round === currentRound) || [];


  const tabs = [
    { id: 'overview', name: 'Tá»•ng quan', icon: Info },
    { id: 'teams', name: 'Äá»™i thi Ä‘áº¥u', icon: Users },
    { id: 'matches', name: 'Lá»‹ch thi Ä‘áº¥u', icon: Play }, // Äá»•i tÃªn hiá»ƒn thá»‹ cho rÃµ rÃ ng
    { id: 'bracket', name: 'CÃ¢y thi Ä‘áº¥u', icon: Target },
    { id: 'match-results', name: 'Káº¿t quáº£ thi Ä‘áº¥u', icon: Award }, // Äá»•i tÃªn hiá»ƒn thá»‹
    { id: 'round-management', name: 'Quáº£n lÃ½ vÃ²ng Ä‘áº¥u', icon: Clock },
    { id: 'management', name: 'Cáº¥u hÃ¬nh giáº£i Ä‘áº¥u', icon: Settings } // Tab má»›i cho form cáº¥u hÃ¬nh
  ];

