import React, { useState } from 'react';
import { testConnection } from '../../services/apiClient';
import { tournamentService } from '../../services';
// import toast from 'react-hot-toast'; // DISABLED - causes DOM manipulation issues

const TournamentDebugPanel = () => {
  const [isLoading, setIsLoading] = useState(false);
  const [testResults, setTestResults] = useState([]);

  const addTestResult = (test, success, data, error) => {
    const result = {
      test,
      success,
      data,
      error: error?.message || error,
      timestamp: new Date().toLocaleTimeString()
    };
    setTestResults(prev => [result, ...prev.slice(0, 9)]); // Keep last 10 results
  };

  const testApiConnection = async () => {
    setIsLoading(true);
    try {
      const response = await testConnection();
      addTestResult('API Connection', true, response);
      console.log('✅ API connection successful!');
    } catch (error) {
      addTestResult('API Connection', false, null, error);
      console.error('❌ API connection failed!', error);
    } finally {
      setIsLoading(false);
    }
  };

  const testCreateTournament = async () => {
    setIsLoading(true);
    const testData = {
      name: 'Test Tournament ' + Date.now(),
      description: 'This is a test tournament',
      sportType: 'FOOTBALL',
      maxTeams: 8,
      startDate: new Date(Date.now() + 24 * 60 * 60 * 1000).toISOString(),
      endDate: new Date(Date.now() + 7 * 24 * 60 * 60 * 1000).toISOString(),
      registrationDeadline: new Date(Date.now() + 12 * 60 * 60 * 1000).toISOString(),
      location: 'Test Location',
      contactInfo: 'test@example.com'
    };

    try {
      const response = await tournamentService.createTournament(testData);
      addTestResult('Create Tournament', true, response);
      console.log('✅ Tournament creation test successful!');
    } catch (error) {
      addTestResult('Create Tournament', false, null, error);
      console.error('❌ Tournament creation test failed!', error);
    } finally {
      setIsLoading(false);
    }
  };

  const testGetTournaments = async () => {
    setIsLoading(true);
    try {
      const response = await tournamentService.getAllTournaments({ page: 1, limit: 5 });
      addTestResult('Get Tournaments', true, response);
      console.log('✅ Get tournaments test successful!');
    } catch (error) {
      addTestResult('Get Tournaments', false, null, error);
      console.error('❌ Get tournaments test failed!', error);
    } finally {
      setIsLoading(false);
    }
  };

  const checkAuthToken = () => {
    const token = localStorage.getItem('accessToken');
    addTestResult('Auth Token Check', !!token, token ? 'Token exists' : 'No token found');
  };

  return (
    <div className="fixed bottom-4 right-4 bg-white border-2 border-blue-500 rounded-lg shadow-lg p-4 max-w-md z-50">
      <div className="flex items-center justify-between mb-3">
        <h3 className="text-lg font-bold text-blue-600">🔧 Tournament Debug Panel</h3>
        <button
          onClick={() => setTestResults([])}
          className="text-xs text-gray-500 hover:text-red-500"
        >
          Clear
        </button>
      </div>

      <div className="space-y-2 mb-4">
        <button
          onClick={checkAuthToken}
          className="w-full text-left px-3 py-2 text-sm bg-gray-100 hover:bg-gray-200 rounded"
          disabled={isLoading}
        >
          Check Auth Token
        </button>
        <button
          onClick={testApiConnection}
          className="w-full text-left px-3 py-2 text-sm bg-blue-100 hover:bg-blue-200 rounded"
          disabled={isLoading}
        >
          Test API Connection
        </button>
        <button
          onClick={testGetTournaments}
          className="w-full text-left px-3 py-2 text-sm bg-green-100 hover:bg-green-200 rounded"
          disabled={isLoading}
        >
          Test Get Tournaments
        </button>
        <button
          onClick={testCreateTournament}
          className="w-full text-left px-3 py-2 text-sm bg-orange-100 hover:bg-orange-200 rounded"
          disabled={isLoading}
        >
          Test Create Tournament
        </button>
      </div>

      {isLoading && (
        <div className="text-center py-2">
          <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-blue-500 mx-auto"></div>
          <span className="text-xs text-gray-500">Testing...</span>
        </div>
      )}

      <div className="max-h-40 overflow-y-auto">
        <h4 className="text-sm font-semibold mb-2">Test Results:</h4>
        {testResults.length === 0 ? (
          <p className="text-xs text-gray-500">No tests run yet</p>
        ) : (
          testResults.map((result, index) => (
            <div
              key={index}
              className={`text-xs p-2 mb-1 rounded ${
                result.success 
                  ? 'bg-green-50 border border-green-200' 
                  : 'bg-red-50 border border-red-200'
              }`}
            >
              <div className="flex justify-between items-center">
                <span className="font-medium">
                  {result.success ? '✅' : '❌'} {result.test}
                </span>
                <span className="text-gray-400">{result.timestamp}</span>
              </div>
              {result.error && (
                <div className="text-red-600 mt-1">
                  Error: {result.error}
                </div>
              )}
              {result.data && typeof result.data === 'string' && (
                <div className="text-gray-600 mt-1 truncate">
                  {result.data}
                </div>
              )}
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default TournamentDebugPanel;
