import React, { useState, useCallback, useRef, useEffect } from 'react';
import { useForm } from 'react-hook-form';
import { useMutation, useQueryClient } from 'react-query';
import { X, Calendar, Users, MapPin, Trophy, FileText, Upload } from 'lucide-react';
import { tournamentService } from '../../services';
import { 
  SPORT_TYPES, 
  SPORT_TYPE_LABELS,
  TOURNAMENT_STATUS,
  VALIDATION_RULES,
  QUERY_KEYS
} from '../../utils/constants';
import { formatDateTimeForInput } from '../../utils/helpers';
import toast from 'react-hot-toast';

const TournamentCreateFormFixed = ({ isOpen, onClose, onSuccess }) => {
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [isClosing, setIsClosing] = useState(false);
  const formRef = useRef(null);
  const queryClient = useQueryClient();

  const {
    register,
    handleSubmit,
    watch,
    reset,
    formState: { errors, isValid }
  } = useForm({
    defaultValues: {
      name: '',
      description: '',
      sportType: 'FOOTBALL',
      maxTeams: 16,
      startDate: '',
      endDate: '',
      registrationDeadline: '',
      location: '',
      rules: '',
      prizeInfo: '',
      contactInfo: ''
    },
    mode: 'onChange'
  });

  // Cleanup effect
  useEffect(() => {
    return () => {
      // Cleanup when component unmounts
      setIsSubmitting(false);
      setIsClosing(false);
    };
  }, []);

  // Safe notification function that avoids DOM manipulation conflicts
  const showNotification = useCallback((type, message) => {
    // Use requestAnimationFrame to ensure DOM is stable
    requestAnimationFrame(() => {
      try {
        const toastId = `tournament-${type}-${Date.now()}`;

        if (type === 'success') {
          toast.success(message, { 
            id: toastId,
            duration: 3000,
            position: 'top-center' 
          });
        } else if (type === 'error') {
          toast.error(message, { 
            id: toastId,
            duration: 4000,
            position: 'top-center' 
          });
        }
      } catch (error) {
        console.error('Toast notification error:', error);
        // Fallback notification
        console.log(`${type.toUpperCase()}: ${message}`);
      }
    });
  }, []);

  const createTournamentMutation = useMutation(
    (tournamentData) => {
      console.log('ğŸš€ Creating tournament with data:', tournamentData);
      return tournamentService.createTournament(tournamentData);
    },
    {
      onSuccess: (response) => {
        console.log('âœ… Tournament created successfully:', response);

        // Use setTimeout to avoid DOM manipulation conflicts
        setTimeout(() => {
          showNotification('success', 'Táº¡o giáº£i Ä‘áº¥u thÃ nh cÃ´ng!');

          // Invalidate queries after notification
          setTimeout(() => {
            queryClient.invalidateQueries(QUERY_KEYS.TOURNAMENTS);

            if (onSuccess) {
              onSuccess(response?.data || response);
            }

            handleClose();
          }, 100);
        }, 50);
      },
      onError: (error) => {
        console.error('âŒ Tournament creation failed:', error);

        const errorMessage = error.response?.data?.message || 
                           error.response?.data?.error || 
                           error.message || 
                           'CÃ³ lá»—i xáº£y ra khi táº¡o giáº£i Ä‘áº¥u';

        setTimeout(() => {
          showNotification('error', errorMessage);
          setIsSubmitting(false);
        }, 50);
      }
    }
  );

  const watchStartDate = watch('startDate');

  const onSubmit = async (data) => {
    if (isSubmitting || isClosing) return;

    try {
      console.log('ğŸ“ Form submitted with data:', data);
      setIsSubmitting(true);

      // Validate required fields
      if (!data.name || !data.description || !data.location || !data.contactInfo) {
        showNotification('error', 'Vui lÃ²ng Ä‘iá»n Ä‘áº§y Ä‘á»§ thÃ´ng tin báº¯t buá»™c');
        setIsSubmitting(false);
        return;
      }

      // Validate dates
      if (!data.startDate || !data.endDate || !data.registrationDeadline) {
        showNotification('error', 'Vui lÃ²ng chá»n Ä‘áº§y Ä‘á»§ ngÃ y thÃ¡ng');
        setIsSubmitting(false);
        return;
      }

      const startDate = new Date(data.startDate);
      const endDate = new Date(data.endDate);
      const regDeadline = new Date(data.registrationDeadline);
      const now = new Date();

      // Debug log Ä‘á»ƒ kiá»ƒm tra thá»i gian
      console.log('ğŸ• Time validation:', {
        now: now.toISOString(),
        regDeadline: regDeadline.toISOString(),
        startDate: startDate.toISOString(),
        endDate: endDate.toISOString()
      });

      // Kiá»ƒm tra háº¡n Ä‘Äƒng kÃ½ pháº£i sau thá»i Ä‘iá»ƒm hiá»‡n táº¡i (cho thÃªm 1 phÃºt buffer)
      const nowPlus1Min = new Date(now.getTime() + 60000); // ThÃªm 1 phÃºt buffer
      if (regDeadline <= nowPlus1Min) {
        showNotification('error', `Háº¡n Ä‘Äƒng kÃ½ pháº£i sau thá»i Ä‘iá»ƒm hiá»‡n táº¡i. Hiá»‡n táº¡i: ${now.toLocaleString('vi-VN')}`);
        setIsSubmitting(false);
        return;
      }

      if (regDeadline >= startDate) {
        showNotification('error', `Háº¡n Ä‘Äƒng kÃ½ (${regDeadline.toLocaleString('vi-VN')}) pháº£i trÆ°á»›c ngÃ y báº¯t Ä‘áº§u (${startDate.toLocaleString('vi-VN')})`);
        setIsSubmitting(false);
        return;
      }

      if (startDate >= endDate) {
        showNotification('error', `NgÃ y káº¿t thÃºc (${endDate.toLocaleString('vi-VN')}) pháº£i sau ngÃ y báº¯t Ä‘áº§u (${startDate.toLocaleString('vi-VN')})`);
        setIsSubmitting(false);
        return;
      }

      // Prepare tournament data
      const tournamentData = {
        name: data.name.trim(),
        description: data.description.trim(),
        sportType: data.sportType || 'FOOTBALL',
        maxTeams: parseInt(data.maxTeams) || 16,
        startDate: startDate.toISOString(),
        endDate: endDate.toISOString(),
        registrationDeadline: regDeadline.toISOString(),
        location: data.location.trim(),
        rules: data.rules?.trim() || '',
        prizeInfo: data.prizeInfo?.trim() || '',
        contactInfo: data.contactInfo.trim()
      };

      console.log('ğŸ¯ Final tournament data:', tournamentData);

      // Use setTimeout to ensure form state is stable before API call
      setTimeout(() => {
        createTournamentMutation.mutate(tournamentData);
      }, 10);

    } catch (error) {
      console.error('ğŸ’¥ Tournament creation error:', error);
      setIsSubmitting(false);
    }
  };

  const handleClose = useCallback(() => {
    if (isClosing) return;

    setIsClosing(true);
    setIsSubmitting(false);

    // Use setTimeout to ensure state changes are processed
    setTimeout(() => {
      try {
        reset();
        if (onClose) {
          onClose();
        }
      } catch (error) {
        console.error('Error in handleClose:', error);
      } finally {
        setIsClosing(false);
      }
    }, 10);
  }, [reset, onClose, isClosing]);

  // Generate min dates vá»›i buffer thá»i gian
  const now = new Date();
  const currentPlus10Min = new Date(now.getTime() + 10 * 60000); // ThÃªm 10 phÃºt tá»« hiá»‡n táº¡i
  const tomorrow = new Date(now);
  tomorrow.setDate(now.getDate() + 1);

  const minStartDate = formatDateTimeForInput(tomorrow);
  const minEndDate = watchStartDate ? 
    formatDateTimeForInput(new Date(new Date(watchStartDate).getTime() + 24 * 60 * 60 * 1000)) : 
    minStartDate;

  // Háº¡n Ä‘Äƒng kÃ½: tá»‘i thiá»ƒu 10 phÃºt tá»« hiá»‡n táº¡i
  const minRegDeadline = formatDateTimeForInput(currentPlus10Min);

  // Háº¡n Ä‘Äƒng kÃ½ tá»‘i Ä‘a: náº¿u cÃ³ startDate thÃ¬ pháº£i trÆ°á»›c 1 ngÃ y, náº¿u khÃ´ng thÃ¬ khÃ´ng giá»›i háº¡n
  let maxRegDeadline = '';
  if (watchStartDate) {
    const startDate = new Date(watchStartDate);
    const maxRegDate = new Date(startDate.getTime() - 24 * 60 * 60 * 1000); // 1 ngÃ y trÆ°á»›c start date

    // Chá»‰ set max náº¿u nÃ³ sau min deadline
    if (maxRegDate > currentPlus10Min) {
      maxRegDeadline = formatDateTimeForInput(maxRegDate);
    }
    // Náº¿u start date quÃ¡ gáº§n hiá»‡n táº¡i, khÃ´ng set max (Ä‘á»ƒ user tá»± chá»n há»£p lÃ½)
  }

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-xl shadow-xl max-w-4xl w-full max-h-[90vh] overflow-hidden">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-gray-200">
          <div className="flex items-center space-x-3">
            <div className="bg-orange-100 p-2 rounded-lg">
              <Trophy className="h-6 w-6 text-orange-600" />
            </div>
            <div>
              <h2 className="text-xl font-bold text-gray-900">Táº¡o Giáº£i Äáº¥u Má»›i</h2>
              <p className="text-sm text-gray-600">Äiá»n thÃ´ng tin Ä‘á»ƒ táº¡o giáº£i Ä‘áº¥u</p>
            </div>
          </div>
          <button
            onClick={handleClose}
            className="text-gray-400 hover:text-gray-600 transition-colors"
            disabled={isSubmitting || isClosing}
          >
            <X className="h-6 w-6" />
          </button>
        </div>

        {/* Form */}
        <form 
          ref={formRef}
          onSubmit={handleSubmit(onSubmit)} 
          className="overflow-y-auto max-h-[calc(90vh-140px)]"
        >
          <div className="p-6 space-y-6">
            {/* Basic Information */}
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              {/* Tournament Name */}
              <div className="lg:col-span-2">
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  TÃªn giáº£i Ä‘áº¥u *
                </label>
                <input
                  type="text"
                  {...register('name', {
                    required: 'TÃªn giáº£i Ä‘áº¥u lÃ  báº¯t buá»™c',
                    minLength: { value: 3, message: 'TÃªn giáº£i Ä‘áº¥u pháº£i cÃ³ Ã­t nháº¥t 3 kÃ½ tá»±' },
                    maxLength: { value: 100, message: 'TÃªn giáº£i Ä‘áº¥u khÃ´ng Ä‘Æ°á»£c vÆ°á»£t quÃ¡ 100 kÃ½ tá»±' }
                  })}
                  className={`w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent ${errors.name ? 'border-red-500' : ''}`}
                  placeholder="Nháº­p tÃªn giáº£i Ä‘áº¥u"
                  disabled={isSubmitting || isClosing}
                />
                {errors.name && (
                  <p className="mt-1 text-sm text-red-600">{errors.name.message}</p>
                )}
              </div>

              {/* Sport Type */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  MÃ´n thá»ƒ thao *
                </label>
                <select
                  {...register('sportType', { required: 'Vui lÃ²ng chá»n mÃ´n thá»ƒ thao' })}
                  className={`w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent ${errors.sportType ? 'border-red-500' : ''}`}
                  disabled={isSubmitting || isClosing}
                >
                  <option value="FOOTBALL">BÃ³ng Ä‘Ã¡</option>
                  <option value="BASKETBALL">BÃ³ng rá»•</option>
                  <option value="VOLLEYBALL">BÃ³ng chuyá»n</option>
                  <option value="BADMINTON">Cáº§u lÃ´ng</option>
                  <option value="TENNIS">Quáº§n vá»£t</option>
                  <option value="PING_PONG">BÃ³ng bÃ n</option>
                  <option value="GENERAL">Tá»•ng há»£p</option>
                </select>
                {errors.sportType && (
                  <p className="mt-1 text-sm text-red-600">{errors.sportType.message}</p>
                )}
              </div>

              {/* Max Teams */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Sá»‘ Ä‘á»™i tá»‘i Ä‘a *
                </label>
                <select
                  {...register('maxTeams', { required: 'Vui lÃ²ng chá»n sá»‘ Ä‘á»™i tá»‘i Ä‘a' })}
                  className={`w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent ${errors.maxTeams ? 'border-red-500' : ''}`}
