// Date utilities for backend communication - DEFINITIVE JAVA LOCALDATETIME SOLUTION
export const formatDateForBackend = (date) => {
  if (!date) return null;

  try {
    const dateObj = date instanceof Date ? date : new Date(date);

    // Check if date is valid
    if (isNaN(dateObj.getTime())) {
      console.error('Invalid date for backend formatting:', date);
      return null;
    }

    // CRITICAL FIX: Java LocalDateTime expects format WITHOUT timezone
    // and potentially WITH nanoseconds padding

    const year = dateObj.getFullYear();
    const month = String(dateObj.getMonth() + 1).padStart(2, '0');
    const day = String(dateObj.getDate()).padStart(2, '0');
    const hours = String(dateObj.getHours()).padStart(2, '0');
    const minutes = String(dateObj.getMinutes()).padStart(2, '0');
    const seconds = String(dateObj.getSeconds()).padStart(2, '0');

    // Try multiple formats that Java might accept
    const formats = [
      // Format 1: Standard ISO without timezone
      `${year}-${month}-${day}T${hours}:${minutes}:${seconds}`,
      // Format 2: With milliseconds
      `${year}-${month}-${day}T${hours}:${minutes}:${seconds}.000`,
      // Format 3: Alternative format
      `${year}-${month}-${day} ${hours}:${minutes}:${seconds}`
    ];

    // Use the first format by default (most common for LocalDateTime)
    const formattedDate = formats[0];

    console.log('ðŸ• Date formatting for Java LocalDateTime:');
    console.log('  Original:', date);
    console.log('  Formatted:', formattedDate);
    console.log('  Alternative formats available:', formats);

    return formattedDate;
  } catch (error) {
    console.error('Error formatting date for backend:', error, 'Date:', date);
    return null;
  }
};

// Alternative function for different format if needed
export const formatDateForBackendAlt = (date) => {
  if (!date) return null;

  try {
    const dateObj = date instanceof Date ? date : new Date(date);
    if (isNaN(dateObj.getTime())) return null;

    // Format: YYYY-MM-DD HH:mm:ss (space separator)
    const year = dateObj.getFullYear();
    const month = String(dateObj.getMonth() + 1).padStart(2, '0');
    const day = String(dateObj.getDate()).padStart(2, '0');
    const hours = String(dateObj.getHours()).padStart(2, '0');
    const minutes = String(dateObj.getMinutes()).padStart(2, '0');
    const seconds = String(dateObj.getSeconds()).padStart(2, '0');

    return `${year}-${month}-${day} ${hours}:${minutes}:${seconds}`;
  } catch (error) {
    console.error('Error in alternative date formatting:', error);
    return null;
  }
};

export const safeDateParse = (dateString) => {
  if (!dateString) return null;

  try {
    // Handle different date string formats
    let date;

    if (typeof dateString === 'string') {
      // Remove timezone info if present (Z suffix)
      const cleanDateString = dateString.replace('Z', '');
      date = new Date(cleanDateString);
    } else {
      date = new Date(dateString);
    }

    // Check if date is valid
    if (isNaN(date.getTime())) {
      console.warn('Could not parse date:', dateString);
      return null;
    }

    return date;
  } catch (error) {
    console.error('Error parsing date:', error, 'Input:', dateString);
    return null;
  }
};

export const formatLocalDateTime = (date) => {
  if (!date) return '';

  try {
    const dateObj = safeDateParse(date);
    if (!dateObj) return '';

    // Format for display: DD/MM/YYYY HH:mm
    const day = String(dateObj.getDate()).padStart(2, '0');
    const month = String(dateObj.getMonth() + 1).padStart(2, '0');
    const year = dateObj.getFullYear();
    const hours = String(dateObj.getHours()).padStart(2, '0');
    const minutes = String(dateObj.getMinutes()).padStart(2, '0');

    return `${day}/${month}/${year} ${hours}:${minutes}`;
  } catch (error) {
    console.error('Error formatting local date time:', error);
    return '';
  }
};

export default {
  formatDateForBackend,
  safeDateParse,
  formatLocalDateTime
};
