                      <div className="text-right space-y-4">
                        <div>
                          <span className={`px-3 py-1 rounded-full text-sm font-medium ${
                            match.status === 'PENDING' ? 'bg-gray-100 text-gray-800' :
                            match.status === 'ONGOING' ? 'bg-blue-100 text-blue-800' :
                            'bg-green-100 text-green-800'
                          }`}>
                            {match.status === 'PENDING' ? 'Chưa bắt đầu' :
                             match.status === 'ONGOING' ? 'Đang diễn ra' :
                             'Đã kết thúc'}
                          </span>
                        </div>

                        <div className="flex space-x-2">
                          {match.status === 'PENDING' && (
                            <button className="bg-green-600 text-white px-3 py-1 rounded text-sm hover:bg-green-700">
                              <Play className="h-4 w-4 inline mr-1" />
                              Bắt đầu
                            </button>
                          )}

                          {match.status === 'ONGOING' && (
                            <>
                              <button className="bg-blue-600 text-white px-3 py-1 rounded text-sm hover:bg-blue-700">
                                <Edit className="h-4 w-4 inline mr-1" />
                                Cập nhật tỉ số
                              </button>
                              <button className="bg-red-600 text-white px-3 py-1 rounded text-sm hover:bg-red-700">
                                <StopCircle className="h-4 w-4 inline mr-1" />
                                Kết thúc
                              </button>
                            </>
                          )}

                          {match.status === 'COMPLETED' && (
                            <button className="bg-gray-600 text-white px-3 py-1 rounded text-sm hover:bg-gray-700">
                              <Eye className="h-4 w-4 inline mr-1" />
                              Xem chi tiết
                            </button>
                          )}
                        </div>
                      </div>
                    </div>
                  </div>
                ))}
              </div>

              {/* Round Control */}
              {mockMatches.filter(m => m.round === currentRound && m.status === 'COMPLETED').length === mockMatches.filter(m => m.round === currentRound).length && (
                <div className="mt-8 text-center p-6 bg-green-50 rounded-lg">
                  <h3 className="text-lg font-semibold text-green-800 mb-4">Vòng {currentRound} đã hoàn thành!</h3>
                  <button 
                    onClick={handleAdvanceRound}
                    className="bg-green-600 text-white px-6 py-3 rounded-lg hover:bg-green-700 flex items-center mx-auto"
                  >
                    <SkipForward className="h-5 w-5 mr-2" />
                    Chuyển sang Vòng {currentRound + 1}
                  </button>
                </div>
              )}
            </div>
          </div>
        )}

        {/* Bracket Tab */}
        {activeTab === 'bracket' && (
          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center justify-between mb-6">
              <h2 className="text-xl font-bold text-gray-900 flex items-center">
                <Target className="h-6 w-6 mr-2 text-purple-600" />
                Sơ đồ thi đấu
              </h2>
              <div className="flex space-x-3">
                <button className="bg-purple-600 text-white px-4 py-2 rounded-lg hover:bg-purple-700 flex items-center text-sm">
                  <Download className="h-4 w-4 mr-2" />
                  Xuất PDF
                </button>
              </div>
            </div>

            {tournament.status === 'REGISTRATION' && (
              <TournamentBracketGenerator 
                tournament={tournament}
                onBracketGenerated={() => {
                  queryClient.invalidateQueries(['tournament-bracket', id]);
                  queryClient.invalidateQueries(['tournament', id]);
                }}
              />
            )}

            {bracket && <TournamentBracketView bracket={bracket} tournament={tournament} />}
          </div>
        )}

        {/* Results Tab */}
        {activeTab === 'results' && (
          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center justify-between mb-6">
              <h2 className="text-xl font-bold text-gray-900 flex items-center">
                <Medal className="h-6 w-6 mr-2 text-yellow-600" />
                Kết quả & Xếp hạng
              </h2>
              <div className="flex space-x-3">
                <button className="bg-yellow-600 text-white px-4 py-2 rounded-lg hover:bg-yellow-700 flex items-center text-sm">
                  <Download className="h-4 w-4 mr-2" />
                  Xuất báo cáo
                </button>
              </div>
            </div>

            <div className="text-center py-16">
              <Medal className="h-16 w-16 text-gray-400 mx-auto mb-4" />
              <h4 className="text-lg font-semibold text-gray-900 mb-2">Kết quả sẽ hiển thị khi giải đấu hoàn thành</h4>
              <p className="text-gray-600">Tiếp tục quản lý các trận đấu để xem kết quả cuối cùng</p>
            </div>
          </div>
        )}

        {/* Settings Tab */}
        {activeTab === 'settings' && (
          <div className="bg-white rounded-lg shadow p-6">
            <h2 className="text-xl font-bold text-gray-900 flex items-center mb-6">
              <Settings className="h-6 w-6 mr-2 text-gray-600" />
              Cài đặt giải đấu
            </h2>

            <div className="space-y-6">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Tên giải đấu</label>
                  <input
                    type="text"
                    value={tournament.name || ''}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Số đội tham dự</label>
                  <input
                    type="number"
                    value={tournament.maxTeams || ''}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  />
                </div>
              </div>

              <div className="flex space-x-4">
                <button className="bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700">
                  Lưu thay đổi
                </button>
                <button className="bg-gray-600 text-white px-6 py-2 rounded-lg hover:bg-gray-700">
                  Hủy
                </button>
              </div>
            </div>
          </div>
        )}
      </div>

      {/* Edit Modal */}
      {showEditModal && (
        <TournamentEditForm
          tournament={tournament}
          isOpen={showEditModal}
          onClose={() => setShowEditModal(false)}
          onSuccess={() => {
            queryClient.invalidateQueries(['tournament', id]);
            setShowEditModal(false);
          }}
        />
      )}
    </div>
  );
};

export default TournamentAdminDetailPage;
