import React, { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from 'react-query'; // Import useQueryClient và useMutation
import {
  Trophy,
  Users,
  Calendar,
  BarChart3,
  Play,
  Pause,
  CheckCircle,
  Clock,
  TrendingUp,
  Target,
  Award,
  AlertTriangle,
  Plus,
  Search,
  Filter,
  RefreshCw,
  Eye,
  Settings,
  MoreHorizontal,
  Edit, // Thêm icon Edit
  Trash2, // Thêm icon Trash2
  MessageSquare // Thêm icon MessageSquare cho Message
} from 'lucide-react';
import { tournamentService, teamService, matchService } from '../../services';
import { formatDate, formatDateTime, getStatusColor } from '../../utils/helpers';
import LoadingSpinner from '../LoadingSpinner';
import toast from 'react-hot-toast'; // Import toast để hiển thị thông báo

const AdminTournamentDashboard = () => {
  const queryClient = useQueryClient(); // Khởi tạo queryClient

  const [searchQuery, setSearchQuery] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');

  // Fetch tournaments data
  const { data: tournaments, isLoading: tournamentsLoading, refetch: refetchTournaments } = useQuery(
    ['admin-tournaments'], // Không phụ thuộc vào selectedPeriod nữa
    () => tournamentService.getAllTournaments(),
    { staleTime: 5 * 60 * 1000 }
  );

  // Mutation for deleting a tournament
  const deleteTournamentMutation = useMutation(
    (tournamentId) => tournamentService.deleteTournament(tournamentId),
    {
      onSuccess: () => {
        queryClient.invalidateQueries('admin-tournaments'); // Vô hiệu hóa cache để refetch dữ liệu mới
        toast.success('Giải đấu đã được xóa thành công!');
      },
      onError: (error) => {
        toast.error(`Lỗi khi xóa giải đấu: ${error.message || 'Lỗi không xác định'}`);
      }
    }
  );

  const handleDeleteTournament = async (tournamentId) => {
    if (window.confirm('Bạn có chắc chắn muốn xóa giải đấu này? Thao tác này không thể hoàn tác.')) {
      await deleteTournamentMutation.mutateAsync(tournamentId);
    }
  };

  // Safe extraction of tournaments data
  let tournamentsList = [];
  try {
    if (Array.isArray(tournaments?.data)) {
      tournamentsList = tournaments.data;
    } else if (Array.isArray(tournaments?.data?.tournaments)) {
      tournamentsList = tournaments.data.tournaments;
    } else if (Array.isArray(tournaments)) { // Trường hợp API trả về trực tiếp mảng
      tournamentsList = tournaments;
    } else {
      console.log('⚠️ [AdminTournamentDashboard] Unexpected tournaments data structure:', tournaments);
      tournamentsList = [];
    }
  } catch (err) {
    console.error('🚨 [AdminTournamentDashboard] Error processing tournaments data:', err);
    tournamentsList = [];
  }

  // Calculate dashboard statistics with safe operations
  const stats = {
    total: Array.isArray(tournamentsList) ? tournamentsList.length : 0,
    ongoing: Array.isArray(tournamentsList) ? tournamentsList.filter(t => t?.status === 'ONGOING').length : 0,
    registration: Array.isArray(tournamentsList) ? tournamentsList.filter(t => t?.status === 'REGISTRATION').length : 0,
    ready: Array.isArray(tournamentsList) ? tournamentsList.filter(t => t?.status === 'READY').length : 0,
    completed: Array.isArray(tournamentsList) ? tournamentsList.filter(t => t?.status === 'COMPLETED').length : 0,
    totalTeams: Array.isArray(tournamentsList) ? tournamentsList.reduce((acc, t) => acc + (t?.currentTeams || 0), 0) : 0,
    avgTeamsPerTournament: Array.isArray(tournamentsList) && tournamentsList.length > 0 ?
      Math.round(tournamentsList.reduce((acc, t) => acc + (t?.currentTeams || 0), 0) / tournamentsList.length) : 0,
    // Thêm các thống kê khác nếu cần
  };

  // Filter tournaments with safe operations
  const filteredTournaments = Array.isArray(tournamentsList) ? tournamentsList.filter(tournament => {
    if (!tournament) return false;

    const matchesSearch = !searchQuery ||
      tournament.name?.toLowerCase().includes(searchQuery.toLowerCase()) ||
      tournament.description?.toLowerCase().includes(searchQuery.toLowerCase());

    const matchesStatus = statusFilter === 'all' || tournament.status === statusFilter;

    return matchesSearch && matchesStatus;
  }) : [];

  const getStatusIcon = (status) => {
    switch (status) {
      case 'ONGOING':
        return <Play className="h-4 w-4 text-blue-600" />;
      case 'COMPLETED':
        return <CheckCircle className="h-4 w-4 text-green-600" />;
      case 'REGISTRATION':
        return <Users className="h-4 w-4 text-orange-600" />;
      case 'READY':
        return <Clock className="h-4 w-4 text-purple-600" />;
      default:
        return <AlertTriangle className="h-4 w-4 text-gray-600" />;
    }
  };

  if (tournamentsLoading) {
    return <LoadingSpinner />;
  }

  return (
    <div className="bg-gray-50 p-6 rounded-lg shadow-inner min-h-[calc(100vh-160px)]"> {/* Thêm padding và shadow */}
      <div className="max-w-full mx-auto space-y-8"> {/* Dùng max-w-full */}

        {/* Top Section: Tournament Status, Recent Activity, Performance */}
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Tournament Status Breakdown */}
          <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
            <h3 className="text-lg font-semibold text-gray-900 mb-4 flex items-center">
              <BarChart3 className="h-5 w-5 mr-2 text-indigo-600" />
              Tournament Status
            </h3>
            <div className="space-y-3">
              <div className="flex items-center justify-between">
                <div className="flex items-center space-x-2">
                  <div className="w-3 h-3 bg-blue-500 rounded-full"></div>
                  <span className="text-sm text-gray-700">Ongoing</span>
                </div>
                <span className="text-sm font-medium text-gray-900">{stats.ongoing}</span>
              </div>
              <div className="flex items-center justify-between">
                <div className="flex items-center space-x-2">
                  <div className="w-3 h-3 bg-orange-500 rounded-full"></div>
                  <span className="text-sm text-gray-700">Registration</span>
                </div>
                <span className="text-sm font-medium text-gray-900">{stats.registration}</span>
              </div>
              <div className="flex items-center justify-between">
                <div className="flex items-center space-x-2">
                  <div className="w-3 h-3 bg-green-500 rounded-full"></div>
                  <span className="text-sm text-gray-700">Completed</span>
                </div>
                <span className="text-sm font-medium text-gray-900">{stats.completed}</span>
              </div>
              <div className="flex items-center justify-between">
                <div className="flex items-center space-x-2">
                  <div className="w-3 h-3 bg-purple-500 rounded-full"></div>
                  <span className="text-sm text-gray-700">Ready</span>
                </div>
                <span className="text-sm font-medium text-gray-900">{stats.ready}</span>
              </div>
            </div>
          </div>

          {/* Recent Activity */}
          <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
            <h3 className="text-lg font-semibold text-gray-900 mb-4 flex items-center">
              <Clock className="h-5 w-5 mr-2 text-gray-600" />
              Recent Activity
            </h3>
            <div className="space-y-3">
              {Array.isArray(tournamentsList) && tournamentsList.length > 0 ? (
                // Sắp xếp theo ngày tạo hoặc ngày bắt đầu gần nhất
                [...tournamentsList].sort((a, b) => new Date(b.createdAt || b.startDate) - new Date(a.createdAt || a.startDate)).slice(0, 5).map((tournament) => (
                  <div key={tournament.id} className="flex items-center space-x-3">
                    <div className={`w-2 h-2 rounded-full ${getStatusColor(tournament.status).split(' ')[0].replace('text-', 'bg-')}`}></div> {/* Lấy màu nền từ trạng thái */}
                    <div className="flex-1 min-w-0">
                      <p className="text-sm font-medium text-gray-900 truncate">
                        {tournament.name}
                      </p>
                      <p className="text-xs text-gray-500">
                        {formatDate(tournament.createdAt || tournament.startDate)}
                      </p>
                    </div>
                  </div>
                ))
              ) : (
                <div className="text-center py-4 text-gray-500 text-sm">
                  Không có hoạt động gần đây.
                </div>
              )}
            </div>
          </div>

          {/* Performance Metrics */}
          <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
            <h3 className="text-lg font-semibold text-gray-900 mb-4 flex items-center">
              <TrendingUp className="h-5 w-5 mr-2 text-green-600" />
              Performance
            </h3>
            <div className="space-y-4">
              <div>
                <div className="flex items-center justify-between mb-1">
                  <span className="text-sm text-gray-600">Completion Rate</span>
                  <span className="text-sm font-medium text-gray-900">
                    {stats.total > 0 ? Math.round((stats.completed / stats.total) * 100) : 0}%
                  </span>
                </div>
                <div className="w-full bg-gray-200 rounded-full h-2">
                  <div
                    className="bg-green-600 h-2 rounded-full transition-all duration-300"
                    style={{
                      width: `${stats.total > 0 ? (stats.completed / stats.total) * 100 : 0}%`
                    }}
                  ></div>
                </div>
              </div>

              <div>
                <div className="flex items-center justify-between mb-1">
                  <span className="text-sm text-gray-600">Avg Teams/Tournament</span>
                  <span className="text-sm font-medium text-gray-900">{stats.avgTeamsPerTournament}</span>
                </div>
              </div>

              <div>
                <div className="flex items-center justify-between">
                  <span className="text-sm text-gray-600">Active Rate</span>
                  <span className="text-sm font-medium text-gray-900">
                    {stats.total > 0 ? Math.round((stats.ongoing / stats.total) * 100) : 0}%
                  </span>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Filters & Search */}
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
          <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between space-y-4 lg:space-y-0">
            <div className="flex items-center space-x-4">
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
                <input
                  type="text"
                  placeholder="Tìm kiếm giải đấu..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="pl-10 pr-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                />
              </div>

              <select
                value={statusFilter}
                onChange={(e) => setStatusFilter(e.target.value)}
                className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              >
                <option value="all">Tất cả trạng thái</option>
                <option value="REGISTRATION">Đang đăng ký</option>
                <option value="READY">Sẵn sàng</option>
                <option value="ONGOING">Đang diễn ra</option>
                <option value="COMPLETED">Đã hoàn thành</option>
              </select>
            </div>

            <div className="text-sm text-gray-600">
              Hiển thị {filteredTournaments.length} trên tổng số {stats.total} giải đấu
            </div>
          </div>
        </div>

        {/* Tournaments Table */}
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 overflow-hidden">
          <div className="px-6 py-4 border-b border-gray-200 flex justify-between items-center">
            <h3 className="text-lg font-semibold text-gray-900">Tất cả giải đấu</h3>
            <button className="flex items-center px-4 py-2 bg-blue-600 text-white rounded-lg shadow hover:bg-blue-700 transition-colors">
              <Plus className="h-4 w-4 mr-2" />
              Tạo giải đấu mới
            </button>
          </div>

          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Giải đấu
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Trạng thái
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Đội tham gia
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Ngày bắt đầu
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Tiến độ
                  </th>
                  <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Hành động
                  </th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {filteredTournaments.map((tournament) => (
                  <tr key={tournament.id} className="hover:bg-gray-50">
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div>
                        <div className="text-sm font-medium text-gray-900">{tournament.name}</div>
                        <div className="text-sm text-gray-500 truncate max-w-xs">
                          {tournament.description || 'Không có mô tả'}
                        </div>
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="flex items-center space-x-2">
                        {getStatusIcon(tournament.status)}
                        <span className={`px-2 py-1 inline-flex text-xs leading-5 font-semibold rounded-full ${getStatusColor(tournament.status)}`}>
                          {tournament.status === 'REGISTRATION' ? 'ĐANG ĐĂNG KÝ' :
                           tournament.status === 'READY' ? 'SẴN SÀNG' :
                           tournament.status === 'ONGOING' ? 'ĐANG DIỄN RA' :
                           tournament.status === 'COMPLETED' ? 'ĐÃ HOÀN THÀNH' :
                           'KHÔNG XÁC ĐỊNH'
                          }
                        </span>
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                      {tournament.currentTeams || 0}/{tournament.maxTeams || 'N/A'}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                      {formatDate(tournament.startDate)}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="flex items-center">
                        <div className="w-16 bg-gray-200 rounded-full h-2 mr-2">
                          <div
                            className="bg-blue-600 h-2 rounded-full"
                            style={{
                              width: `${tournament.maxTeams > 0 ?
                                ((tournament.currentTeams || 0) / tournament.maxTeams) * 100 : 0}%`
                            }}
                          ></div>
                        </div>
                        <span className="text-xs text-gray-600">
                          {tournament.maxTeams > 0 ?
                            Math.round(((tournament.currentTeams || 0) / tournament.maxTeams) * 100) : 0}%
                        </span>
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                      <div className="flex items-center justify-end space-x-2">
                        {/* Các nút hành động */}
                        {/* Message Button */}
                        <button
                          title="Message"
                          className="text-gray-600 hover:text-blue-600 p-2 rounded-full hover:bg-gray-100 transition-colors"
                        >
                          <MessageSquare className="h-4 w-4" />
                        </button>
                        {/* Edit Button */}
                        <button
                          title="Edit"
                          className="text-gray-600 hover:text-green-600 p-2 rounded-full hover:bg-gray-100 transition-colors"
                        >
                          <Edit className="h-4 w-4" />
                        </button>
                        {/* View Button */}
                        <button
                          title="View"
                          className="text-gray-600 hover:text-purple-600 p-2 rounded-full hover:bg-gray-100 transition-colors"
                        >
                          <Eye className="h-4 w-4" />
                        </button>
                        {/* Delete Button */}
                        <button
                          title="Delete"
                          onClick={() => handleDeleteTournament(tournament.id)}
                          className="text-gray-600 hover:text-red-600 p-2 rounded-full hover:bg-gray-100 transition-colors"
                          disabled={deleteTournamentMutation.isLoading}
                        >
                          {deleteTournamentMutation.isLoading ? (
                            <LoadingSpinner size={16} />
                          ) : (
                            <Trash2 className="h-4 w-4" />
                          )}
                        </button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>

            {filteredTournaments.length === 0 && (
              <div className="text-center py-12">
                <Trophy className="h-12 w-12 text-gray-300 mx-auto mb-4" />
                <h3 className="text-lg font-medium text-gray-900 mb-2">Không tìm thấy giải đấu nào</h3>
                <p className="text-gray-600">
                  {searchQuery || statusFilter !== 'all'
                    ? 'Hãy thử điều chỉnh tìm kiếm hoặc tiêu chí lọc của bạn.'
                    : 'Hãy tạo giải đấu đầu tiên của bạn để bắt đầu!'
                  }
                </p>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminTournamentDashboard;
