import React, { useState, useCallback, useRef, useMemo } from 'react';
import { useForm } from 'react-hook-form';
import { useMutation, useQueryClient } from 'react-query';
import { X, Trophy } from 'lucide-react';
import { tournamentService } from '../../services';
import { 
  SPORT_TYPES, 
  SPORT_TYPE_LABELS,
  TOURNAMENT_STATUS,
  VALIDATION_RULES,
  QUERY_KEYS
} from '../../utils/constants';
import { formatDateTimeForInput } from '../../utils/helpers';

// ULTRA-SIMPLE DATE FORMATTERS FOR JAVA LocalDateTime
const formatDateForJava = (date) => {
  if (!date) return null;

  try {
    const d = new Date(date);
    if (isNaN(d.getTime())) return null;

    const year = d.getFullYear();
    const month = String(d.getMonth() + 1).padStart(2, '0');
    const day = String(d.getDate()).padStart(2, '0');
    const hours = String(d.getHours()).padStart(2, '0');
    const minutes = String(d.getMinutes()).padStart(2, '0');
    const seconds = String(d.getSeconds()).padStart(2, '0');

    // Simple format without milliseconds: YYYY-MM-DDTHH:mm:ss
    return `${year}-${month}-${day}T${hours}:${minutes}:${seconds}`;
  } catch (error) {
    console.error('Date formatting error:', error);
    return null;
  }
};

const TournamentCreateFormUltraFixed = ({ isOpen, onClose, onSuccess }) => {
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState('');
  const formRef = useRef(null);
  const queryClient = useQueryClient();

  const {
    register,
    handleSubmit,
    watch,
    reset,
    formState: { errors: formErrors }
  } = useForm({
    defaultValues: {
      name: '',
      description: '',
      sportType: 'FOOTBALL',
      maxTeams: 16,
      startDate: '',
      endDate: '',
      registrationDeadline: '',
      location: '',
      rules: '',
      prizeInfo: '',
      contactInfo: ''
    },
    mode: 'onChange'
  });

  // Safe notification
  const showError = useCallback((message) => {
    console.error('‚ùå ERROR:', message);
    setError(message);
    setTimeout(() => alert(`‚ùå ${message}`), 100);
  }, []);

  const createTournamentMutation = useMutation(
    async (tournamentData) => {
      console.log('üöÄ Creating tournament with data:', tournamentData);

      // MULTIPLE FORMAT ATTEMPTS for Java LocalDateTime compatibility
      const formatAttempts = [
        // Format 1: Simple without milliseconds
        {
          name: 'Simple ISO',
          formatter: (date) => {
            const d = new Date(date);
            const year = d.getFullYear();
            const month = String(d.getMonth() + 1).padStart(2, '0');
            const day = String(d.getDate()).padStart(2, '0');
            const hours = String(d.getHours()).padStart(2, '0');
            const minutes = String(d.getMinutes()).padStart(2, '0');
            const seconds = String(d.getSeconds()).padStart(2, '0');
            return `${year}-${month}-${day}T${hours}:${minutes}:${seconds}`;
          }
        },
        // Format 2: Space separated
        {
          name: 'Space separated',
          formatter: (date) => {
            const d = new Date(date);
            const year = d.getFullYear();
            const month = String(d.getMonth() + 1).padStart(2, '0');
            const day = String(d.getDate()).padStart(2, '0');
            const hours = String(d.getHours()).padStart(2, '0');
            const minutes = String(d.getMinutes()).padStart(2, '0');
            const seconds = String(d.getSeconds()).padStart(2, '0');
            return `${year}-${month}-${day} ${hours}:${minutes}:${seconds}`;
          }
        },
        // Format 3: Array format
        {
          name: 'Array format',
          formatter: (date) => {
            const d = new Date(date);
            return [d.getFullYear(), d.getMonth() + 1, d.getDate(), 
                   d.getHours(), d.getMinutes(), d.getSeconds()];
          }
        },
        // Format 4: ISO String slice
        {
          name: 'ISO slice',
          formatter: (date) => {
            return new Date(date).toISOString().slice(0, 19);
          }
        }
      ];

      for (let i = 0; i < formatAttempts.length; i++) {
        const attempt = formatAttempts[i];

        try {
          console.log(`üîÑ Trying format ${i + 1}: ${attempt.name}`);

          const dataWithFormattedDates = {
            ...tournamentData,
            startDate: attempt.formatter(tournamentData.startDate),
            endDate: attempt.formatter(tournamentData.endDate),
            registrationDeadline: attempt.formatter(tournamentData.registrationDeadline)
          };

          console.log(`üì§ Sending data with format ${i + 1}:`, {
            startDate: dataWithFormattedDates.startDate,
            endDate: dataWithFormattedDates.endDate,
            registrationDeadline: dataWithFormattedDates.registrationDeadline
          });

          const result = await tournamentService.createTournament(dataWithFormattedDates);
          console.log(`‚úÖ SUCCESS with format ${i + 1}: ${attempt.name}!`);
          return result;

        } catch (error) {
          console.log(`‚ùå Format ${i + 1} (${attempt.name}) failed:`, error.message);

          if (i === formatAttempts.length - 1) {
            // Last attempt failed
            throw new Error(`T·∫•t c·∫£ ${formatAttempts.length} ƒë·ªãnh d·∫°ng ng√†y ƒë·ªÅu th·∫•t b·∫°i. L·ªói cu·ªëi: ${error.message}`);
          }
          // Continue to next format
        }
      }
    },
    {
      onSuccess: (response) => {
        console.log('‚úÖ Tournament created successfully:', response);

        setTimeout(() => {
          try {
            // Success notification
            alert('‚úÖ T·∫°o gi·∫£i ƒë·∫•u th√†nh c√¥ng!');

            // Invalidate queries
            setTimeout(() => {
              queryClient.invalidateQueries(QUERY_KEYS.TOURNAMENTS).catch(console.warn);
            }, 100);

            // Call success callback
            if (onSuccess) {
              onSuccess(response?.data || response);
            }

            // Close form
            handleClose();

          } catch (error) {
            console.warn('Success handler error:', error);
            handleClose();
          }
        }, 100);
      },
      onError: (error) => {
        console.error('‚ùå Tournament creation failed:', error);

        let errorMessage = 'C√≥ l·ªói x·∫£y ra khi t·∫°o gi·∫£i ƒë·∫•u';

        if (error.response?.data?.message) {
          errorMessage = error.response.data.message;
        } else if (error.message) {
          errorMessage = error.message;
        }

        // Handle specific Java LocalDateTime error
        if (errorMessage.includes('LocalDateTime') || errorMessage.includes('deserialize')) {
          errorMessage = 'L·ªói ƒë·ªãnh d·∫°ng ng√†y th√°ng t·ª´ server. Vui l√≤ng li√™n h·ªá h·ªó tr·ª£.';
        }

        showError(errorMessage);
        setIsSubmitting(false);
      }
    }
  );

  const watchStartDate = watch('startDate');

  const onSubmit = async (data) => {
    if (isSubmitting) return;

    try {
      console.log('üìù Form submitted with data:', data);
      setIsSubmitting(true);
      setError('');

      // Basic validation
      if (!data.name?.trim() || !data.description?.trim() || 
          !data.location?.trim() || !data.contactInfo?.trim()) {
        showError('Vui l√≤ng ƒëi·ªÅn ƒë·∫ßy ƒë·ªß th√¥ng tin b·∫Øt bu·ªôc');
        setIsSubmitting(false);
        return;
      }

      if (!data.startDate || !data.endDate || !data.registrationDeadline) {
        showError('Vui l√≤ng ch·ªçn ƒë·∫ßy ƒë·ªß ng√†y th√°ng');
        setIsSubmitting(false);
        return;
      }

      const startDate = new Date(data.startDate);
      const endDate = new Date(data.endDate);
      const regDeadline = new Date(data.registrationDeadline);
      const now = new Date();

      // Date validation
      const nowPlus1Min = new Date(now.getTime() + 60000);
      if (regDeadline <= nowPlus1Min) {
        showError('H·∫°n ƒëƒÉng k√Ω ph·∫£i sau th·ªùi ƒëi·ªÉm hi·ªán t·∫°i');
        setIsSubmitting(false);
        return;
      }

      if (regDeadline >= startDate) {
        showError('H·∫°n ƒëƒÉng k√Ω ph·∫£i tr∆∞·ªõc ng√†y b·∫Øt ƒë·∫ßu');
        setIsSubmitting(false);
        return;
      }

      if (startDate >= endDate) {
        showError('Ng√†y k·∫øt th√∫c ph·∫£i sau ng√†y b·∫Øt ƒë·∫ßu');
        setIsSubmitting(false);
        return;
      }

      // Prepare tournament data - keep dates as Date objects
      const tournamentData = {
        name: data.name.trim(),
        description: data.description.trim(),
        sportType: data.sportType || 'FOOTBALL',
        maxTeams: parseInt(data.maxTeams) || 16,
        startDate: startDate,  // Let the mutation handle formatting
        endDate: endDate,
        registrationDeadline: regDeadline,
        location: data.location.trim(),
        rules: data.rules?.trim() || '',
        prizeInfo: data.prizeInfo?.trim() || '',
        contactInfo: data.contactInfo.trim()
      };

      console.log('üéØ Tournament data prepared (dates as objects):', tournamentData);

      await createTournamentMutation.mutateAsync(tournamentData);

    } catch (error) {
      console.error('üí• Form submission error:', error);
      setIsSubmitting(false);
    }
  };

  const handleClose = useCallback(() => {
    try {
      console.log('üîí Closing tournament form...');
      setIsSubmitting(false);
      setError('');
      reset();

      if (onClose) {
        onClose();
      }
    } catch (error) {
      console.error('Error in handleClose:', error);
      if (onClose) {
        onClose();
      }
    }
  }, [reset, onClose]);

  // Min dates calculation
  const now = useMemo(() => new Date(), []);
  const currentPlus10Min = useMemo(() => new Date(now.getTime() + 10 * 60000), [now]);
  const tomorrow = useMemo(() => {
