import React, { useState } from 'react';
import { useMutation, useQueryClient } from 'react-query';
import { 
  Play, 
  Trophy, 
  ArrowRight, 
  CheckCircle, 
  Settings,
  Users,
  Target,
  Award,
  AlertTriangle,
  Clock
} from 'lucide-react';
import { tournamentKnockoutService } from '../../services';
import toast from 'react-hot-toast';

const TournamentManagement = ({ tournament, onTournamentUpdate }) => {
  const queryClient = useQueryClient();
  const [isGenerating, setIsGenerating] = useState(false);

  // Generate Bracket Mutation
  const generateBracketMutation = useMutation(
    () => {
      console.log('üéØ Generating bracket for tournament:', tournament.id);
      return tournamentKnockoutService.generateBracket(tournament.id, {
        matchType: 'KNOCKOUT',
        randomSeed: true
      });
    },
    {
      onSuccess: (response) => {
        console.log('‚úÖ Bracket generated successfully:', response);
        toast.success('üéØ Bracket ƒë√£ ƒë∆∞·ª£c t·∫°o th√†nh c√¥ng!', {
          duration: 5000,
          icon: 'üèÜ'
        });
        
        // Refresh tournament and bracket data
        queryClient.invalidateQueries(['tournament', tournament.id]);
        queryClient.invalidateQueries(['tournament-bracket', tournament.id]);
        queryClient.invalidateQueries(['tournament-matches', tournament.id]);
        
        onTournamentUpdate?.();
      },
      onError: (error) => {
        console.error('‚ùå Bracket generation failed:', error);
        const errorMessage = error.response?.data?.message || 'Kh√¥ng th·ªÉ t·∫°o bracket. Vui l√≤ng th·ª≠ l·∫°i.';
        toast.error(`‚ùå ${errorMessage}`, {
          duration: 6000
        });
      }
    }
  );

  // Start Tournament Mutation
  const startTournamentMutation = useMutation(
    () => {
      console.log('üöÄ Starting knockout tournament:', tournament.id);
      return tournamentKnockoutService.startKnockout(tournament.id);
    },
    {
      onSuccess: (response) => {
        console.log('‚úÖ Tournament started successfully:', response);
        toast.success('üöÄ Tournament ƒë√£ b·∫Øt ƒë·∫ßu!', {
          duration: 5000,
          icon: 'üèÅ'
        });
        
        queryClient.invalidateQueries(['tournament', tournament.id]);
        onTournamentUpdate?.();
      },
      onError: (error) => {
        console.error('‚ùå Tournament start failed:', error);
        const errorMessage = error.response?.data?.message || 'Kh√¥ng th·ªÉ b·∫Øt ƒë·∫ßu tournament. Vui l√≤ng th·ª≠ l·∫°i.';
        toast.error(`‚ùå ${errorMessage}`, {
          duration: 6000
        });
      }
    }
  );

  // Advance Round Mutation
  const advanceRoundMutation = useMutation(
    () => {
      console.log('‚è≠Ô∏è Advancing to next round:', tournament.id);
      return tournamentKnockoutService.advanceRound(tournament.id);
    },
    {
      onSuccess: (response) => {
        console.log('‚úÖ Round advanced successfully:', response);
        toast.success('‚è≠Ô∏è ƒê√£ chuy·ªÉn sang round ti·∫øp theo!', {
          duration: 5000,
          icon: 'üéØ'
        });
        
        queryClient.invalidateQueries(['tournament', tournament.id]);
        queryClient.invalidateQueries(['tournament-bracket', tournament.id]);
        queryClient.invalidateQueries(['tournament-matches', tournament.id]);
        
        onTournamentUpdate?.();
      },
      onError: (error) => {
        console.error('‚ùå Round advance failed:', error);
        const errorMessage = error.response?.data?.message || 'Kh√¥ng th·ªÉ chuy·ªÉn round. Vui l√≤ng ho√†n th√†nh t·∫•t c·∫£ matches.';
        toast.error(`‚ùå ${errorMessage}`, {
          duration: 6000
        });
      }
    }
  );

  // Complete Tournament Mutation
  const completeTournamentMutation = useMutation(
    () => {
      console.log('üèÜ Completing tournament:', tournament.id);
      return tournamentKnockoutService.completeTournament(tournament.id);
    },
    {
      onSuccess: (response) => {
        console.log('‚úÖ Tournament completed successfully:', response);
        toast.success('üèÜ Tournament ƒë√£ ho√†n th√†nh! Ch√∫c m·ª´ng nh√† v√¥ ƒë·ªãch!', {
          duration: 8000,
          icon: 'üëë'
        });
        
        queryClient.invalidateQueries(['tournament', tournament.id]);
        onTournamentUpdate?.();
      },
      onError: (error) => {
        console.error('‚ùå Tournament completion failed:', error);
        const errorMessage = error.response?.data?.message || 'Kh√¥ng th·ªÉ ho√†n th√†nh tournament. Vui l√≤ng th·ª≠ l·∫°i.';
        toast.error(`‚ùå ${errorMessage}`, {
          duration: 6000
        });
      }
    }
  );

  const handleGenerateBracket = () => {
    if (window.confirm('T·∫°o bracket s·∫Ω random shuffle t·∫•t c·∫£ teams. B·∫°n c√≥ ch·∫Øc ch·∫Øn?')) {
      generateBracketMutation.mutate();
    }
  };

  const handleStartTournament = () => {
    if (window.confirm('B·∫Øt ƒë·∫ßu tournament? Teams s·∫Ω kh√¥ng th·ªÉ ƒëƒÉng k√Ω th√™m.')) {
      startTournamentMutation.mutate();
    }
  };

  const handleAdvanceRound = () => {
    if (window.confirm('Chuy·ªÉn sang round ti·∫øp theo? ƒê·∫£m b·∫£o t·∫•t c·∫£ matches ƒë√£ ho√†n th√†nh.')) {
      advanceRoundMutation.mutate();
    }
  };

  const handleCompleteTournament = () => {
    if (window.confirm('Ho√†n th√†nh tournament v√† c√¥ng b·ªë nh√† v√¥ ƒë·ªãch?')) {
      completeTournamentMutation.mutate();
    }
  };

  const isAnyMutationLoading = 
    generateBracketMutation.isLoading || 
    startTournamentMutation.isLoading || 
    advanceRoundMutation.isLoading || 
    completeTournamentMutation.isLoading;

  // Get tournament status for conditional rendering
  const status = tournament.status;
  const canGenerateBracket = status === 'REGISTRATION' || status === 'UPCOMING';
  const canStartTournament = status === 'READY';
  const canAdvanceRound = status === 'ONGOING';
  const canCompleteTournament = status === 'ONGOING';

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="bg-gradient-to-r from-blue-600 to-purple-600 rounded-xl p-6 text-white">
        <div className="flex items-center justify-between">
          <div>
            <h2 className="text-2xl font-bold mb-2">Tournament Management</h2>
            <p className="text-blue-100">Qu·∫£n l√Ω v√† ƒëi·ªÅu khi·ªÉn tournament knockout</p>
          </div>
          <div className="bg-white/20 backdrop-blur-sm rounded-lg p-3">
            <Settings className="h-8 w-8" />
          </div>
        </div>
      </div>

      {/* Tournament Status */}
      <div className="bg-white rounded-xl border border-gray-200 p-6">
        <h3 className="text-lg font-semibold text-gray-900 mb-4 flex items-center">
          <Trophy className="h-5 w-5 mr-2 text-yellow-500" />
          Tournament Status
        </h3>
        
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <div className="text-center">
            <div className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-medium ${
              status === 'REGISTRATION' ? 'bg-blue-100 text-blue-800' : 'bg-gray-100 text-gray-600'
            }`}>
              <Users className="h-4 w-4 mr-1" />
              Registration
            </div>
          </div>
          
          <div className="text-center">
            <div className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-medium ${
              status === 'READY' ? 'bg-green-100 text-green-800' : 'bg-gray-100 text-gray-600'
            }`}>
              <Target className="h-4 w-4 mr-1" />
              Ready
            </div>
          </div>
          
          <div className="text-center">
            <div className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-medium ${
              status === 'ONGOING' ? 'bg-orange-100 text-orange-800' : 'bg-gray-100 text-gray-600'
            }`}>
              <Clock className="h-4 w-4 mr-1" />
              Ongoing
            </div>
          </div>
          
          <div className="text-center">
            <div className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-medium ${
              status === 'COMPLETED' ? 'bg-purple-100 text-purple-800' : 'bg-gray-100 text-gray-600'
            }`}>
              <Award className="h-4 w-4 mr-1" />
              Completed
            </div>
          </div>
        </div>
      </div>

      {/* Management Actions */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        
        {/* Generate Bracket */}
        <div className="bg-white rounded-xl border border-gray-200 p-6">
          <div className="flex items-start justify-between mb-4">
            <div>
              <h3 className="text-lg font-semibold text-gray-900 mb-2 flex items-center">
                <Target className="h-5 w-5 mr-2 text-blue-500" />
                1. Generate Bracket
              </h3>
              <p className="text-sm text-gray-600">
                T·∫°o bracket knockout t·ª´ teams ƒë√£ ƒëƒÉng k√Ω. Teams s·∫Ω ƒë∆∞·ª£c random shuffle.
              </p>
            </div>
          </div>
          
          <button
            onClick={handleGenerateBracket}
            disabled={!canGenerateBracket || isAnyMutationLoading}
            className={`w-full flex items-center justify-center space-x-2 px-4 py-3 rounded-lg font-medium transition-colors ${
              canGenerateBracket && !isAnyMutationLoading
                ? 'bg-blue-600 hover:bg-blue-700 text-white'
                : 'bg-gray-100 text-gray-400 cursor-not-allowed'
            }`}
          >
            {generateBracketMutation.isLoading ? (
              <>
                <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white"></div>
                <span>ƒêang t·∫°o bracket...</span>
              </>
            ) : (
              <>
                <Target className="h-4 w-4" />
                <span>Generate Bracket</span>
              </>
            )}
          </button>
          
          {!canGenerateBracket && (
            <div className="mt-2 flex items-center text-xs text-gray-500">
              <AlertTriangle className="h-3 w-3 mr-1" />
              Ch·ªâ c√≥ th·ªÉ t·∫°o bracket khi tournament ·ªü tr·∫°ng th√°i Registration
            </div>
          )}
        </div>

        {/* Start Tournament */}
        <div className="bg-white rounded-xl border border-gray-200 p-6">
          <div className="flex items-start justify-between mb-4">
            <div>
              <h3 className="text-lg font-semibold text-gray-900 mb-2 flex items-center">
                <Play className="h-5 w-5 mr-2 text-green-500" />
                2. Start Tournament
              </h3>
              <p className="text-sm text-gray-600">
                B·∫Øt ƒë·∫ßu tournament knockout. Teams s·∫Ω kh√¥ng th·ªÉ ƒëƒÉng k√Ω th√™m.
              </p>
            </div>
          </div>
          
          <button
            onClick={handleStartTournament}
            disabled={!canStartTournament || isAnyMutationLoading}
            className={`w-full flex items-center justify-center space-x-2 px-4 py-3 rounded-lg font-medium transition-colors ${
              canStartTournament && !isAnyMutationLoading
                ? 'bg-green-600 hover:bg-green-700 text-white'
                : 'bg-gray-100 text-gray-400 cursor-not-allowed'
            }`}
          >
            {startTournamentMutation.isLoading ? (
              <>
                <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white"></div>
                <span>ƒêang b·∫Øt ƒë·∫ßu...</span>
              </>
            ) : (
              <>
                <Play className="h-4 w-4" />
                <span>Start Tournament</span>
              </>
            )}
          </button>
          
          {!canStartTournament && (
            <div className="mt-2 flex items-center text-xs text-gray-500">
              <AlertTriangle className="h-3 w-3 mr-1" />
              C·∫ßn t·∫°o bracket tr∆∞·ªõc khi b·∫Øt ƒë·∫ßu tournament
            </div>
          )}
        </div>

        {/* Advance Round */}
        <div className="bg-white rounded-xl border border-gray-200 p-6">
          <div className="flex items-start justify-between mb-4">
            <div>
              <h3 className="text-lg font-semibold text-gray-900 mb-2 flex items-center">
                <ArrowRight className="h-5 w-5 mr-2 text-orange-500" />
                3. Advance Round
              </h3>
              <p className="text-sm text-gray-600">
                Chuy·ªÉn sang round ti·∫øp theo. T·∫°o matches m·ªõi t·ª´ winners c·ªßa round hi·ªán t·∫°i.
              </p>
            </div>
          </div>
          
          <button
            onClick={handleAdvanceRound}
            disabled={!canAdvanceRound || isAnyMutationLoading}
            className={`w-full flex items-center justify-center space-x-2 px-4 py-3 rounded-lg font-medium transition-colors ${
              canAdvanceRound && !isAnyMutationLoading
                ? 'bg-orange-600 hover:bg-orange-700 text-white'
                : 'bg-gray-100 text-gray-400 cursor-not-allowed'
            }`}
          >
            {advanceRoundMutation.isLoading ? (
              <>
                <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white"></div>
                <span>ƒêang chuy·ªÉn round...</span>
              </>
            ) : (
              <>
                <ArrowRight className="h-4 w-4" />
                <span>Advance Round</span>
              </>
            )}
          </button>
          
          {!canAdvanceRound && (
            <div className="mt-2 flex items-center text-xs text-gray-500">
              <AlertTriangle className="h-3 w-3 mr-1" />
              Ch·ªâ c√≥ th·ªÉ advance round khi tournament ƒëang di·ªÖn ra
            </div>
          )}
        </div>

        {/* Complete Tournament */}
        <div className="bg-white rounded-xl border border-gray-200 p-6">
          <div className="flex items-start justify-between mb-4">
            <div>
              <h3 className="text-lg font-semibold text-gray-900 mb-2 flex items-center">
                <Trophy className="h-5 w-5 mr-2 text-purple-500" />
                4. Complete Tournament
              </h3>
              <p className="text-sm text-gray-600">
                Ho√†n th√†nh tournament v√† c√¥ng b·ªë nh√† v√¥ ƒë·ªãch ch√≠nh th·ª©c.
              </p>
            </div>
          </div>
          
          <button
            onClick={handleCompleteTournament}
            disabled={!canCompleteTournament || isAnyMutationLoading}
            className={`w-full flex items-center justify-center space-x-2 px-4 py-3 rounded-lg font-medium transition-colors ${
              canCompleteTournament && !isAnyMutationLoading
                ? 'bg-purple-600 hover:bg-purple-700 text-white'
                : 'bg-gray-100 text-gray-400 cursor-not-allowed'
