// src/pages/AdminPanel.js
import React, { Suspense } from 'react';
import { useQuery } from 'react-query'; // Giữ lại useQuery nếu bạn có API thống kê dashboard
import {
  Users,
  Trophy,
  FileText,
  BarChart3,
  CheckCircle,
  AlertTriangle,
  Clock,
  Target,
  Info,
  DollarSign // Ví dụ: icon cho doanh thu
} from 'lucide-react';
import { useAuth } from '../context/AuthContext';
import LoadingSpinner from '../components/LoadingSpinner';
import { tournamentService, teamService, newsService } from '../services'; // Import services nếu cần lấy data
import toast from 'react-hot-toast'; // Có thể dùng cho thông báo lỗi khi tải dashboard

// Component Dashboard chính của admin
// Đảm bảo đường dẫn đúng nếu bạn đã tách component này ra
const AdminTournamentDashboard = React.lazy(() => import('../components/admin/AdminTournamentDashboard'));

const AdminPanel = () => {
  const { user } = useAuth();

  // Kiểm tra quyền ở đây không còn cần thiết vì AdminLayout đã xử lý.
  // Tuy nhiên, bạn có thể giữ lại các biến isAdmin, isOrganizer nếu logic dashboard cần chúng.
  const isAdmin = user?.role === 'ADMIN';
  const isOrganizer = user?.role === 'ORGANIZER';

  // Ví dụ: Lấy số liệu thống kê chung cho dashboard
  // Bạn cần thay thế bằng API thực tế của mình để lấy các số liệu này
  const { data: dashboardStats, isLoading: statsLoading, error: statsError } = useQuery(
    'adminDashboardStats',
    async () => {
      // Giả định có các service để lấy số liệu tổng quan
      const [tournamentsRes, teamsRes, newsRes] = await Promise.all([
        tournamentService.getAllTournaments(),
        teamService.getAllTeams(), // Giả định có API này để lấy tổng số đội
        newsService.getAllNews() // Giả định có API này để lấy tổng số bài viết
      ]);

      const totalTournaments = tournamentsRes?.data?.total || 0;
      const completedTournaments = tournamentsRes?.data?.tournaments?.filter(t => t.status === 'COMPLETED').length || 0;
      const totalTeams = teamsRes?.data?.total || 0;
      const totalNews = newsRes?.data?.total || 0;
      // Thêm các số liệu khác nếu bạn có (người dùng, doanh thu...)

      return {
        totalTournaments,
        completedTournaments,
        totalTeams,
        totalNews,
        // ... các số liệu khác
      };
    },
    {
      staleTime: 60 * 1000, // Thống kê có thể làm mới mỗi phút
      onError: (err) => {
        toast.error('Lỗi khi tải dữ liệu thống kê dashboard.');
        console.error("Error fetching dashboard stats:", err);
      }
    }
  );


  // Nếu bạn muốn AdminPanel hiển thị các thành phần riêng lẻ thay vì AdminTournamentDashboard
  // (ví dụ: các card thống kê nhỏ) thì bạn sẽ render chúng ở đây.
  // Hiện tại, tôi sẽ để nó render AdminTournamentDashboard.

  return (
    // AdminLayout đã được bọc ở App.js, nên không cần bọc lại ở đây
    <div className="space-y-8"> {/* Thêm padding và khoảng cách cho nội dung */}
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold text-gray-900">Dashboard</h1>
        {/* Có thể thêm các nút hành động nhanh nếu cần */}
      </div>

      {statsLoading ? (
        <LoadingSpinner text="Đang tải dữ liệu dashboard..." />
      ) : statsError ? (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative" role="alert">
          <strong className="font-bold">Lỗi!</strong>
          <span className="block sm:inline"> Không thể tải dữ liệu dashboard.</span>
        </div>
      ) : (
        <>
          {/* Các card thống kê nhanh */}
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
            <div className="bg-white rounded-lg shadow-md p-6 flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-500">Tổng số giải đấu</p>
                <p className="text-3xl font-bold text-gray-900">{dashboardStats?.totalTournaments || 0}</p>
              </div>
              <Trophy className="h-10 w-10 text-primary-500" />
            </div>

            <div className="bg-white rounded-lg shadow-md p-6 flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-500">Giải đấu đã hoàn thành</p>
                <p className="text-3xl font-bold text-gray-900">{dashboardStats?.completedTournaments || 0}</p>
              </div>
              <CheckCircle className="h-10 w-10 text-green-500" />
            </div>

            <div className="bg-white rounded-lg shadow-md p-6 flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-500">Tổng số đội</p>
                <p className="text-3xl font-bold text-gray-900">{dashboardStats?.totalTeams || 0}</p>
              </div>
              <Users className="h-10 w-10 text-blue-500" />
            </div>

            <div className="bg-white rounded-lg shadow-md p-6 flex items-center justify-between">
              <div>
