import React, { useState, useCallback, useRef, useMemo } from 'react';
import { useForm } from 'react-hook-form';
import { useMutation, useQueryClient } from 'react-query';
import { X, Trophy, CheckCircle, ArrowRight } from 'lucide-react';
import { tournamentService } from '../../services';
import { 
  SPORT_TYPES, 
  SPORT_TYPE_LABELS,
  TOURNAMENT_STATUS,
  VALIDATION_RULES,
  QUERY_KEYS,
  STORAGE_KEYS
} from '../../utils/constants';
import { formatDateTimeForInput } from '../../utils/helpers';

const TournamentCreateFormCleanUI = ({ isOpen, onClose, onSuccess }) => {
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState('');
  const [showSuccessModal, setShowSuccessModal] = useState(false);
  const [createdTournament, setCreatedTournament] = useState(null);
  const formRef = useRef(null);
  const queryClient = useQueryClient();

  const {
    register,
    handleSubmit,
    watch,
    reset,
    formState: { errors: formErrors }
  } = useForm({
    defaultValues: {
      name: '',
      description: '',
      sportType: 'FOOTBALL',
      maxTeams: 16,
      startDate: '',
      endDate: '',
      registrationDeadline: '',
      location: '',
      rules: '',
      prizeInfo: '',
      contactInfo: ''
    },
    mode: 'onChange'
  });

  const showError = useCallback((message) => {
    console.error('❌ ERROR:', message);
    setError(message);
  }, []);

  const createTournamentMutation = useMutation(
    async (tournamentData) => {
      console.log('🚀 Creating tournament with data:', tournamentData);

      // Check authentication
      const token = localStorage.getItem(STORAGE_KEYS.ACCESS_TOKEN);
      if (!token) {
        throw new Error('No authentication token found');
      }

      // Format data for backend (array format works)
      const formatArray = (date) => {
        const d = new Date(date);
        return [d.getFullYear(), d.getMonth() + 1, d.getDate(), 
               d.getHours(), d.getMinutes(), d.getSeconds()];
      };

      const dataWithFormattedDates = {
        ...tournamentData,
        startDate: formatArray(tournamentData.startDate),
        endDate: formatArray(tournamentData.endDate),
        registrationDeadline: formatArray(tournamentData.registrationDeadline)
      };

      console.log('📤 Sending formatted data:', dataWithFormattedDates);

      const result = await tournamentService.createTournament(dataWithFormattedDates);
      console.log('✅ Tournament created successfully:', result);
      return result;
    },
    {
      onSuccess: (response) => {
        console.log('✅ Tournament creation success:', response);

        // Extract tournament info
        const tournamentId = response?.id || response?.data?.id || response?.tournament?.id;
        const tournamentName = response?.name || response?.data?.name || response?.tournament?.name;

        const tournamentInfo = {
          id: tournamentId,
          name: tournamentName || 'New Tournament',
          response: response
        };

        setCreatedTournament(tournamentInfo);
        setShowSuccessModal(true);
        setIsSubmitting(false);
      },
      onError: (error) => {
        console.error('❌ Tournament creation failed:', error);

        let errorMessage = 'Có lỗi xảy ra khi tạo giải đấu';

        if (error.response?.data?.message) {
          errorMessage = error.response.data.message;
        } else if (error.message) {
          errorMessage = error.message;
        }

        if (error.isAuthError || error.statusCode === 401) {
          errorMessage = 'Phiên đăng nhập đã hết hạn. Vui lòng đăng nhập lại.';
        }

        showError(errorMessage);
        setIsSubmitting(false);
      }
    }
  );

  const watchStartDate = watch('startDate');

  const onSubmit = async (data) => {
    if (isSubmitting) return;

    try {
      console.log('📝 Form submitted with data:', data);
      setIsSubmitting(true);
      setError('');

      // Basic validation
      if (!data.name?.trim() || !data.description?.trim() || 
          !data.location?.trim() || !data.contactInfo?.trim()) {
        showError('Vui lòng điền đầy đủ thông tin bắt buộc');
        setIsSubmitting(false);
        return;
      }

      if (!data.startDate || !data.endDate || !data.registrationDeadline) {
        showError('Vui lòng chọn đầy đủ ngày tháng');
        setIsSubmitting(false);
        return;
      }

      const startDate = new Date(data.startDate);
      const endDate = new Date(data.endDate);
      const regDeadline = new Date(data.registrationDeadline);
      const now = new Date();

      // Date validation
      const nowPlus1Min = new Date(now.getTime() + 60000);
      if (regDeadline <= nowPlus1Min) {
        showError('Hạn đăng ký phải sau thời điểm hiện tại');
        setIsSubmitting(false);
        return;
      }

      if (regDeadline >= startDate) {
        showError('Hạn đăng ký phải trước ngày bắt đầu');
        setIsSubmitting(false);
        return;
      }

      if (startDate >= endDate) {
        showError('Ngày kết thúc phải sau ngày bắt đầu');
        setIsSubmitting(false);
        return;
      }

      // Prepare tournament data
      const tournamentData = {
        name: data.name.trim(),
        description: data.description.trim(),
        sportType: data.sportType || 'FOOTBALL',
        maxTeams: parseInt(data.maxTeams) || 16,
        startDate: startDate,
        endDate: endDate,
        registrationDeadline: regDeadline,
        location: data.location.trim(),
        rules: data.rules?.trim() || '',
        prizeInfo: data.prizeInfo?.trim() || '',
        contactInfo: data.contactInfo.trim()
      };

      console.log('🎯 Tournament data prepared:', tournamentData);

      await createTournamentMutation.mutateAsync(tournamentData);

    } catch (error) {
      console.error('💥 Form submission error:', error);
      setIsSubmitting(false);
    }
  };

  const handleSuccessConfirm = () => {
    console.log('✅ User confirmed success, proceeding...');

    // Hide success modal
    setShowSuccessModal(false);

    // Reset form
    reset();
    setError('');
    setCreatedTournament(null);

    // Invalidate queries to refresh tournament list
    queryClient.invalidateQueries(QUERY_KEYS.TOURNAMENTS).catch(console.warn);

    // Call success callback if provided
    if (onSuccess && createdTournament) {
      onSuccess(createdTournament.response);
    }

    // Close the form and return to admin panel
    setTimeout(() => {
      if (onClose) {
        onClose();
      }
    }, 100);
  };

  const handleClose = useCallback(() => {
    console.log('🔒 Closing tournament form...');
    setIsSubmitting(false);
    setError('');
    setShowSuccessModal(false);
    setCreatedTournament(null);
    reset();

    setTimeout(() => {
      if (onClose && typeof onClose === 'function') {
        onClose();
      }
    }, 50);
  }, [reset, onClose]);

  // Min dates calculation
  const now = useMemo(() => new Date(), []);
  const currentPlus10Min = useMemo(() => new Date(now.getTime() + 10 * 60000), [now]);
  const tomorrow = useMemo(() => {
    const date = new Date(now);
    date.setDate(now.getDate() + 1);
    return date;
  }, [now]);

  const minStartDate = useMemo(() => formatDateTimeForInput(tomorrow), [tomorrow]);
  const minEndDate = useMemo(() => {
    if (!watchStartDate) return minStartDate;
    try {
      const startDateObj = new Date(watchStartDate);
      const nextDay = new Date(startDateObj.getTime() + 24 * 60 * 60 * 1000);
      return formatDateTimeForInput(nextDay);
    } catch (error) {
      return minStartDate;
    }
  }, [watchStartDate, minStartDate]);

  const minRegDeadline = useMemo(() => formatDateTimeForInput(currentPlus10Min), [currentPlus10Min]);

  const maxRegDeadline = useMemo(() => {
    if (!watchStartDate) return '';
    try {
      const startDate = new Date(watchStartDate);
      const maxRegDate = new Date(startDate.getTime() - 24 * 60 * 60 * 1000);

      if (maxRegDate > currentPlus10Min) {
        return formatDateTimeForInput(maxRegDate);
      }
      return '';
    } catch (error) {
      return '';
    }
  }, [watchStartDate, currentPlus10Min]);

  if (!isOpen) return null;

  // Success Modal
  if (showSuccessModal && createdTournament) {
    return (
      <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
        <div className="bg-white rounded-xl shadow-xl max-w-md w-full overflow-hidden">
          {/* Success Header */}
          <div className="bg-green-50 p-6 border-b border-green-200">
            <div className="flex items-center">
              <div className="flex-shrink-0">
                <CheckCircle className="h-10 w-10 text-green-500" />
              </div>
              <div className="ml-4">
                <h3 className="text-lg font-semibold text-green-900">
                  Tạo giải đấu thành công!
                </h3>
                <p className="text-sm text-green-700">
                  Giải đấu đã được lưu vào hệ thống
                </p>
              </div>
            </div>
          </div>

          {/* Success Content */}
          <div className="p-6">
            <div className="space-y-3">
              <div className="flex items-center justify-between py-2 border-b border-gray-100">
                <span className="text-sm font-medium text-gray-600">Tên giải đấu:</span>
                <span className="text-sm text-gray-900">{createdTournament.name}</span>
              </div>

              {createdTournament.id && (
                <div className="flex items-center justify-between py-2 border-b border-gray-100">
                  <span className="text-sm font-medium text-gray-600">Mã giải đấu:</span>
                  <span className="text-sm font-mono text-gray-900">#{createdTournament.id}</span>
                </div>
              )}

              <div className="flex items-center justify-between py-2">
                <span className="text-sm font-medium text-gray-600">Trạng thái:</span>
                <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
                  Đã tạo thành công
                </span>
              </div>
            </div>
          </div>

          {/* Success Footer */}
          <div className="bg-gray-50 px-6 py-4 flex justify-end">
            <button
              onClick={handleSuccessConfirm}
              className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-green-600 hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500 transition-colors"
            >
              Về trang quản trị
              <ArrowRight className="ml-2 h-4 w-4" />
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-xl shadow-xl max-w-4xl w-full max-h-[90vh] overflow-hidden">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-gray-200">
          <div className="flex items-center space-x-3">
            <div className="bg-orange-100 p-2 rounded-lg">
              <Trophy className="h-6 w-6 text-orange-600" />
            </div>
            <div>
              <h2 className="text-xl font-bold text-gray-900">Tạo Giải Đấu Mới</h2>
              <p className="text-sm text-gray-600">Điền thông tin để tạo giải đấu</p>
            </div>
          </div>
          <button
            onClick={handleClose}
            className="text-gray-400 hover:text-gray-600 transition-colors"
            disabled={isSubmitting}
          >
            <X className="h-6 w-6" />
          </button>
        </div>

        {/* Error notification */}
        {error && (
          <div className="p-4 border-b bg-red-50 border-red-200 text-red-800">
            <div className="flex items-center">
              <span className="text-sm font-medium">❌ {error}</span>
            </div>
          </div>
        )}

        {/* Form */}
        <form 
          ref={formRef}
          onSubmit={handleSubmit(onSubmit)} 
          className="overflow-y-auto max-h-[calc(90vh-140px)]"
        >
          <div className="p-6 space-y-6">
            {/* Tournament Name */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Tên giải đấu *
              </label>
              <input
                type="text"
                {...register('name', {
                  required: 'Tên giải đấu là bắt buộc',
                  minLength: { value: 3, message: 'Tên giải đấu phải có ít nhất 3 ký tự' },
                  maxLength: { value: 100, message: 'Tên giải đấu không được vượt quá 100 ký tự' }
                })}
                className={`w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent ${formErrors.name ? 'border-red-500' : ''}`}
                placeholder="Nhập tên giải đấu"
                disabled={isSubmitting}
              />
              {formErrors.name && (
                <p className="mt-1 text-sm text-red-600">{formErrors.name.message}</p>
              )}
            </div>

            {/* Sport Type & Max Teams */}
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Môn thể thao *
                </label>
                <select
                  {...register('sportType', { required: 'Vui lòng chọn môn thể thao' })}
                  className={`w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent ${formErrors.sportType ? 'border-red-500' : ''}`}
                  disabled={isSubmitting}
                >
                  <option value="FOOTBALL">Bóng đá</option>
                  <option value="BASKETBALL">Bóng rổ</option>
                  <option value="VOLLEYBALL">Bóng chuyền</option>
                  <option value="BADMINTON">Cầu lông</option>
                  <option value="TENNIS">Quần vợt</option>
                  <option value="PING_PONG">Bóng bàn</option>
                  <option value="GENERAL">Tổng hợp</option>
                </select>
                {formErrors.sportType && (
                  <p className="mt-1 text-sm text-red-600">{formErrors.sportType.message}</p>
                )}
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Số đội tối đa *
                </label>
                <select
                  {...register('maxTeams', { required: 'Vui lòng chọn số đội tối đa' })}
                  className={`w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent ${formErrors.maxTeams ? 'border-red-500' : ''}`}
                  disabled={isSubmitting}
                >
                  <option value={4}>4 đội</option>
                  <option value={8}>8 đội</option>
                  <option value={16}>16 đội</option>
                  <option value={32}>32 đội</option>
                  <option value={64}>64 đội</option>
                </select>
                {formErrors.maxTeams && (
                  <p className="mt-1 text-sm text-red-600">{formErrors.maxTeams.message}</p>
                )}
              </div>
            </div>

            {/* Description */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Mô tả giải đấu *
              </label>
              <textarea
                {...register('description', {
                  required: 'Mô tả giải đấu là bắt buộc',
                  minLength: { value: 10, message: 'Mô tả phải có ít nhất 10 ký tự' }
                })}
                rows={3}
                className={`w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent resize-none ${formErrors.description ? 'border-red-500' : ''}`}
                placeholder="Mô tả chi tiết về giải đấu"
                disabled={isSubmitting}
              />
              {formErrors.description && (
                <p className="mt-1 text-sm text-red-600">{formErrors.description.message}</p>
              )}
            </div>

            {/* Dates */}
            <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Ngày bắt đầu *
                </label>
                <input
                  type="datetime-local"
                  {...register('startDate', { required: 'Ngày bắt đầu là bắt buộc' })}
                  min={minStartDate}
                  className={`w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent ${formErrors.startDate ? 'border-red-500' : ''}`}
                  disabled={isSubmitting}
                />
                {formErrors.startDate && (
                  <p className="mt-1 text-sm text-red-600">{formErrors.startDate.message}</p>
                )}
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Ngày kết thúc *
                </label>
                <input
                  type="datetime-local"
                  {...register('endDate', { required: 'Ngày kết thúc là bắt buộc' })}
                  min={minEndDate}
                  className={`w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent ${formErrors.endDate ? 'border-red-500' : ''}`}
                  disabled={isSubmitting}
                />
                {formErrors.endDate && (
                  <p className="mt-1 text-sm text-red-600">{formErrors.endDate.message}</p>
                )}
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Hạn đăng ký *
                </label>
                <input
                  type="datetime-local"
                  {...register('registrationDeadline', { required: 'Hạn đăng ký là bắt buộc' })}
                  min={minRegDeadline}
                  max={maxRegDeadline}
                  className={`w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent ${formErrors.registrationDeadline ? 'border-red-500' : ''}`}
                  disabled={isSubmitting}
                />
                {formErrors.registrationDeadline && (
                  <p className="mt-1 text-sm text-red-600">{formErrors.registrationDeadline.message}</p>
                )}
              </div>
            </div>

            {/* Location */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Địa điểm *
              </label>
              <input
                type="text"
                {...register('location', { required: 'Địa điểm là bắt buộc' })}
                className={`w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent ${formErrors.location ? 'border-red-500' : ''}`}
                placeholder="Nhập địa điểm tổ chức"
                disabled={isSubmitting}
              />
              {formErrors.location && (
                <p className="mt-1 text-sm text-red-600">{formErrors.location.message}</p>
              )}
            </div>

            {/* Contact Info */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Thông tin liên hệ *
              </label>
              <input
                type="text"
                {...register('contactInfo', { required: 'Thông tin liên hệ là bắt buộc' })}
                className={`w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent ${formErrors.contactInfo ? 'border-red-500' : ''}`}
                placeholder="Email hoặc số điện thoại liên hệ"
                disabled={isSubmitting}
              />
              {formErrors.contactInfo && (
                <p className="mt-1 text-sm text-red-600">{formErrors.contactInfo.message}</p>
              )}
            </div>

            {/* Additional Info */}
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Luật thi đấu
                </label>
                <textarea
                  {...register('rules')}
                  rows={3}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent resize-none"
                  placeholder="Mô tả luật thi đấu và quy định"
                  disabled={isSubmitting}
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Thông tin giải thưởng
                </label>
                <textarea
                  {...register('prizeInfo')}
                  rows={3}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent resize-none"
                  placeholder="Mô tả giải thưởng và phần thưởng"
                  disabled={isSubmitting}
                />
              </div>
            </div>
          </div>

          {/* Footer */}
          <div className="flex items-center justify-end space-x-3 p-6 border-t border-gray-200 bg-gray-50">
            <button
              type="button"
              onClick={handleClose}
              className="px-4 py-2 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-md hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:opacity-50 disabled:cursor-not-allowed"
              disabled={isSubmitting}
            >
              Hủy
            </button>
            <button
              type="submit"
              className="px-4 py-2 text-sm font-medium text-white bg-blue-600 border border-transparent rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:opacity-50 disabled:cursor-not-allowed flex items-center"
              disabled={isSubmitting}
            >
              {isSubmitting ? (
                <>
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                  Đang tạo...
                </>
              ) : (
                <>
                  <Trophy className="h-4 w-4 mr-2" />
                  Tạo giải đấu
                </>
              )}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default TournamentCreateFormCleanUI;
