          console.log('ðŸ“Š [HomePage] Dashboard stats loaded at', new Date().toLocaleTimeString());
        },
        select: (response) => {
          console.log('ðŸ“Š [HomePage] Dashboard Stats Response:', response);
          return response?.data || null;
