import React, { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from 'react-query';
import {
  FileText,
  Search,
  Filter,
  Plus,
  Edit,
  Trash2,
  Eye,
  Calendar,
  User,
  Upload,
  Image,
  AlertTriangle,
  X // Thêm icon X cho modal đóng
} from 'lucide-react';
import newsService from '../../services/newsService'; // Import đúng newsService
import LoadingSpinner from '../LoadingSpinner'; // Đảm bảo đường dẫn đúng
import toast from 'react-hot-toast'; // Đảm bảo đã cài react-hot-toast

// Helper functions (đảm bảo chúng có trong utils/helpers.js)
const formatDate = (dateString) => {
  if (!dateString) return 'N/A';
  const options = { year: 'numeric', month: 'short', day: 'numeric' };
  return new Date(dateString).toLocaleDateString(undefined, options);
};

const truncateText = (text, maxLength) => {
  if (!text) return '';
  if (text.length <= maxLength) return text;
  return text.substring(0, maxLength) + '...';
};

const NewsManagement = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [page, setPage] = useState(1);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [showUploadModal, setShowUploadModal] = useState(false);
  const [selectedNewsId, setSelectedNewsId] = useState(null);
  const [filesToUpload, setFilesToUpload] = useState([]);

  const queryClient = useQueryClient();

  // Fetch all news
  const { data: news, isLoading, error } = useQuery(
    ['admin-news', { page, searchTerm }],
    async () => {
      const allNews = await newsService.getAllNews();

      let filteredNews = allNews;
      if (searchTerm) {
        filteredNews = allNews.filter(article =>
          article.name.toLowerCase().includes(searchTerm.toLowerCase()) || // Đổi title thành name
          article.content.toLowerCase().includes(searchTerm.toLowerCase()) ||
          (article.shortDescription && article.shortDescription.toLowerCase().includes(searchTerm.toLowerCase())) // Đổi summary thành shortDescription
        );
      }

      // Mock pagination (because backend API doesn't support it yet)
      const itemsPerPage = 6; // Giảm số lượng để dễ test
      const startIndex = (page - 1) * itemsPerPage;
      const endIndex = startIndex + itemsPerPage;
      const paginatedNews = filteredNews.slice(startIndex, endIndex);

      return {
        data: paginatedNews,
        pagination: {
          currentPage: page,
          totalPages: Math.ceil(filteredNews.length / itemsPerPage),
          totalItems: filteredNews.length,
          hasNext: page < Math.ceil(filteredNews.length / itemsPerPage),
          hasPrev: page > 1
        }
      };
    },
    {
      staleTime: 2 * 60 * 1000,
      keepPreviousData: true,
      onError: (err) => {
        toast.error(err.response?.data?.message || 'Failed to fetch news articles.');
      }
    }
  );

  // Mutation for deleting news
  const deleteNewsMutation = useMutation(
    (newsId) => newsService.deleteNews(newsId),
    {
      onSuccess: () => {
        toast.success('News article deleted successfully');
        queryClient.invalidateQueries('admin-news'); // Refetch news after deletion
      },
      onError: (error) => {
        toast.error(error.response?.data?.message || 'Failed to delete news article');
      }
    }
  );

  // Mutation for creating news
  const createNewsMutation = useMutation(
    (newsData) => newsService.createNews(newsData),
    {
      onSuccess: () => {
        toast.success('News article created successfully');
        queryClient.invalidateQueries('admin-news'); // Refetch news after creation
        setShowCreateModal(false); // Close modal
      },
      onError: (error) => {
        toast.error(error.response?.data?.message || 'Failed to create news article');
      }
    }
  );

  // Mutation for uploading attachments
  const uploadAttachmentsMutation = useMutation(
    ({ newsId, files }) => newsService.uploadNewsAttachments(newsId, files),
    {
      onSuccess: (data) => {
        toast.success(data.message || 'Attachments uploaded successfully!');
        queryClient.invalidateQueries('admin-news'); // Refetch news to show new attachments if needed
        setShowUploadModal(false); // Close modal
        setFilesToUpload([]); // Clear selected files
      },
      onError: (error) => {
        toast.error(error.response?.data?.message || 'Failed to upload attachments.');
      }
    }
  );

  const handleSearch = (e) => {
    e.preventDefault();
    setPage(1); // Reset to first page on new search
    queryClient.invalidateQueries('admin-news'); // Invalidate to trigger re-fetch with new search term
  };

  const handleDeleteNews = (newsId) => {
    if (window.confirm('Are you sure you want to delete this news article?')) {
      deleteNewsMutation.mutate(newsId);
    }
  };

  const handleCreateNews = (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const newsData = {
      name: formData.get('name'), // Đổi title thành name
      content: formData.get('content'),
      shortDescription: formData.get('shortDescription') || truncateText(formData.get('content'), 200) // Đổi summary thành shortDescription
    };

    createNewsMutation.mutate(newsData);
  };

  const handleFileChange = (e) => {
    setFilesToUpload(Array.from(e.target.files));
  };

  const handleUploadFiles = () => {
    if (selectedNewsId && filesToUpload.length > 0) {
      uploadAttachmentsMutation.mutate({ newsId: selectedNewsId, files: filesToUpload });
    } else {
      toast.error('Please select files to upload.');
    }
  };

  if (error) {
    return (
      <div className="text-center py-8">
        <AlertTriangle className="h-12 w-12 text-red-500 mx-auto mb-4" />
        <p className="text-red-600">Error loading news articles. Please try again later.</p>
      </div>
    );
  }

  return (
    <div className="space-y-6 p-6"> {/* Thêm padding cho toàn bộ trang */}
      {/* Header */}
      <div className="flex flex-col sm:flex-row items-center justify-between gap-4">
        <div>
          <h2 className="text-3xl font-bold text-gray-900">News Management</h2>
          <p className="text-gray-600">Create, edit, and publish news articles and announcements.</p>
        </div>
        <div className="flex items-center space-x-3">
          <div className="bg-purple-100 text-purple-800 px-4 py-2 rounded-full text-sm font-medium">
            {news?.pagination?.totalItems || 0} Articles
          </div>
          <button
            onClick={() => setShowCreateModal(true)}
            className="btn-primary"
          >
            <Plus className="h-4 w-4 mr-2" />
            Create Article
          </button>
        </div>
      </div>

      {/* Search */}
      <div className="bg-white rounded-lg border border-gray-200 p-4 shadow-sm">
        <form onSubmit={handleSearch} className="flex flex-col sm:flex-row gap-4">
          <div className="flex-1">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-5 w-5" />
              <input
                type="text"
                placeholder="Search news articles by title or content..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pl-10 input-field"
              />
            </div>
          </div>
          <button
            type="submit"
            className="btn-primary whitespace-nowrap"
          >
            <Filter className="h-4 w-4 mr-2" />
            Search
          </button>
        </form>
      </div>

      {/* News Grid */}
      <div className="bg-white rounded-lg border border-gray-200 overflow-hidden shadow-sm">
        {isLoading ? (
          <div className="p-8 text-center">
            <LoadingSpinner />
            <p className="text-gray-500 mt-2">Loading news articles...</p>
          </div>
        ) : news?.data?.length === 0 ? (
          <div className="p-8 text-center">
            <FileText className="h-16 w-16 text-gray-400 mx-auto mb-4" />
            <p className="text-gray-600 text-lg mb-4">No news articles found.</p>
            <button
              onClick={() => setShowCreateModal(true)}
              className="btn-primary"
            >
              <Plus className="h-4 w-4 mr-2" />
              Create Your First Article
            </button>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 p-6">
            {news?.data?.map((article) => (
              <div key={article.id} className="border border-gray-200 rounded-lg overflow-hidden bg-white shadow-md hover:shadow-lg transition-shadow duration-300 flex flex-col">
                {/* Article Image */}
                <div className="w-full h-48 bg-gray-200 flex items-center justify-center overflow-hidden">
                  {article.attachments && article.attachments.length > 0 ? (
                    <img
                      src={newsService.getImageUrl(article.attachments[0].url)} // Lấy ảnh đầu tiên
                      alt={article.name}
                      className="w-full h-full object-cover"
                      onError={(e) => { e.target.onerror = null; e.target.src = 'https://via.placeholder.com/400x200?text=No+Image'; }} // Fallback image
                    />
                  ) : (
                    <Image className="h-16 w-16 text-gray-400" /> // Placeholder icon if no image
                  )}
                </div>

                {/* Article Content */}
                <div className="p-4 flex flex-col flex-grow">
                  <h3 className="text-xl font-semibold text-gray-900 mb-2 line-clamp-2">
                    {article.name} {/* Đổi title thành name */}
                  </h3>

                  <p className="text-gray-600 text-sm mb-3 line-clamp-3 flex-grow">
                    {truncateText(article.shortDescription || article.content, 120)} {/* Sử dụng shortDescription hoặc content */}
                  </p>

                  {/* Article Meta */}
                  <div className="flex items-center text-xs text-gray-500 mb-4 mt-auto">
                    <Calendar className="h-3 w-3 mr-1" />
                    <span>{formatDate(article.createdAt)}</span>
                    <User className="h-3 w-3 ml-4 mr-1" />
                    <span>{article.author || 'EduSports Team'}</span> {/* `author` chưa có trong backend News entity */}
                  </div>

                  {/* Actions */}
                  <div className="flex items-center justify-between pt-2 border-t border-gray-100">
                    <div className="flex items-center space-x-3">
                      <button
                        onClick={() => window.open(`/news/${article.id}`, '_blank')}
                        className="btn-icon"
                        title="View Article"
                      >
                        <Eye className="h-4 w-4" />
                      </button>
                      <button
                        onClick={() => window.open(`/admin/news/${article.id}/edit`, '_blank')} // Hoặc mở modal edit
                        className="btn-icon text-blue-600 hover:text-blue-700"
                        title="Edit Article"
                      >
                        <Edit className="h-4 w-4" />
                      </button>
                      <button
                        onClick={() => { setSelectedNewsId(article.id); setShowUploadModal(true); }}
                        className="btn-icon text-green-600 hover:text-green-700"
                        title="Upload Files"
                      >
                        <Upload className="h-4 w-4" />
                      </button>
                    </div>

                    <button
                      onClick={() => handleDeleteNews(article.id)}
                      className="btn-icon text-red-600 hover:text-red-700"
                      title="Delete Article"
                      disabled={deleteNewsMutation.isLoading}
                    >
                      <Trash2 className="h-4 w-4" />
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}

        {/* Pagination */}
        {news?.pagination?.totalPages > 1 && (
          <div className="bg-white px-4 py-3 border-t border-gray-200 sm:px-6 flex items-center justify-between">
            <div className="text-sm text-gray-700">
              Showing page <span className="font-medium">{news.pagination.currentPage}</span> of <span className="font-medium">{news.pagination.totalPages}</span> ({news.pagination.totalItems} items)
            </div>
            <div className="flex space-x-2">
              <button
                onClick={() => setPage(prev => prev - 1)}
                disabled={!news.pagination.hasPrev || deleteNewsMutation.isLoading || createNewsMutation.isLoading || uploadAttachmentsMutation.isLoading}
                className="btn-secondary disabled:opacity-50 disabled:cursor-not-allowed"
              >
                Previous
              </button>
              <button
                onClick={() => setPage(prev => prev + 1)}
                disabled={!news.pagination.hasNext || deleteNewsMutation.isLoading || createNewsMutation.isLoading || uploadAttachmentsMutation.isLoading}
                className="btn-secondary disabled:opacity-50 disabled:cursor-not-allowed"
              >
                Next
              </button>
            </div>
          </div>
        )}
      </div>

      {/* Create News Modal */}
      {showCreateModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-xl shadow-xl max-w-2xl w-full mx-auto max-h-[90vh] overflow-y-auto">
            <div className="p-6">
              <div className="flex justify-between items-center mb-4">
                <h3 className="text-2xl font-bold text-gray-900">Create News Article</h3>
                <button
                  onClick={() => setShowCreateModal(false)}
                  className="text-gray-500 hover:text-gray-700 transition-colors"
                >
                  <X className="h-6 w-6" />
                </button>
              </div>
              <form onSubmit={handleCreateNews} className="space-y-4">
                <div>
                  <label htmlFor="name" className="block text-sm font-medium text-gray-700 mb-2">
                    Article Name
                  </label>
                  <input
                    id="name"
                    name="name" // Đổi title thành name
                    type="text"
                    required
                    className="input-field"
                    placeholder="Enter article name..."
                  />
                </div>

                <div>
                  <label htmlFor="shortDescription" className="block text-sm font-medium text-gray-700 mb-2">
                    Short Description (Optional)
                  </label>
                  <textarea
                    id="shortDescription"
                    name="shortDescription" // Đổi summary thành shortDescription
                    rows={3}
                    className="input-field"
                    placeholder="Brief summary of the article..."
                  />
                </div>

                <div>
                  <label htmlFor="content" className="block text-sm font-medium text-gray-700 mb-2">
                    Article Content
                  </label>
                  <textarea
                    id="content"
                    name="content"
                    rows={8}
                    required
                    className="input-field"
                    placeholder="Write your article content here..."
                  />
                </div>

                <div className="flex flex-col sm:flex-row space-y-3 sm:space-y-0 sm:space-x-3 pt-4">
                  <button
                    type="button"
                    onClick={() => setShowCreateModal(false)}
                    className="flex-1 btn-secondary"
                  >
                    Cancel
                  </button>
                  <button
                    type="submit"
                    disabled={createNewsMutation.isLoading}
                    className="flex-1 btn-primary disabled:opacity-50"
                  >
                    {createNewsMutation.isLoading ? (
                      <div className="flex items-center justify-center space-x-2">
                        <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
                        <span>Creating...</span>
                      </div>
                    ) : (
                      'Create Article'
                    )}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}

      {/* Upload Attachments Modal */}
      {showUploadModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-xl shadow-xl max-w-lg w-full mx-auto max-h-[90vh] overflow-y-auto">
            <div className="p-6">
              <div className="flex justify-between items-center mb-4">
                <h3 className="text-2xl font-bold text-gray-900">Upload Attachments for News ID: {selectedNewsId}</h3>
                <button
                  onClick={() => { setShowUploadModal(false); setFilesToUpload([]); }}
                  className="text-gray-500 hover:text-gray-700 transition-colors"
                >
                  <X className="h-6 w-6" />
                </button>
              </div>
              <div className="space-y-4">
                <div>
                  <label htmlFor="file-upload" className="block text-sm font-medium text-gray-700 mb-2">
                    Select Images (Max 5 files, 10MB each, image format only)
                  </label>
                  <input
                    id="file-upload"
                    type="file"
                    multiple
                    accept="image/*"
                    onChange={handleFileChange}
                    className="block w-full text-sm text-gray-500
                                file:mr-4 file:py-2 file:px-4
                                file:rounded-full file:border-0
                                file:text-sm file:font-semibold
                                file:bg-primary-50 file:text-primary-700
                                hover:file:bg-primary-100"
                  />
                  {filesToUpload.length > 0 && (
                    <div className="mt-3 text-sm text-gray-600">
                      Selected files: {filesToUpload.map(f => f.name).join(', ')}
                    </div>
                  )}
                  {uploadAttachmentsMutation.isError && (
                    <p className="text-red-500 text-sm mt-2">
                      {uploadAttachmentsMutation.error.response?.data?.message || 'Error uploading files.'}
                    </p>
                  )}
                </div>

                <div className="flex flex-col sm:flex-row space-y-3 sm:space-y-0 sm:space-x-3 pt-4">
                  <button
                    type="button"
                    onClick={() => { setShowUploadModal(false); setFilesToUpload([]); }}
                    className="flex-1 btn-secondary"
                  >
                    Cancel
                  </button>
                  <button
                    type="button"
                    onClick={handleUploadFiles}
                    disabled={uploadAttachmentsMutation.isLoading || filesToUpload.length === 0}
                    className="flex-1 btn-primary disabled:opacity-50"
                  >
                    {uploadAttachmentsMutation.isLoading ? (
                      <div className="flex items-center justify-center space-x-2">
                        <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
                        <span>Uploading...</span>
                      </div>
                    ) : (
                      <><Upload className="h-4 w-4 mr-2" /> Upload Files</>
                    )}
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default NewsManagement;
