import React, { useState, useCallback, useRef, useMemo } from 'react';
import { useForm } from 'react-hook-form';
import { useMutation, useQueryClient } from 'react-query';
import { X, Trophy } from 'lucide-react';
import { tournamentService } from '../../services';
import { 
  SPORT_TYPES, 
  SPORT_TYPE_LABELS,
  TOURNAMENT_STATUS,
  VALIDATION_RULES,
  QUERY_KEYS
} from '../../utils/constants';
import { formatDateTimeForInput } from '../../utils/helpers';

const TournamentCreateFormZeroDOM = ({ isOpen, onClose, onSuccess }) => {
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState('');
  const [isSuccess, setIsSuccess] = useState(false);
  const formRef = useRef(null);
  const queryClient = useQueryClient();

  const {
    register,
    handleSubmit,
    watch,
    reset,
    formState: { errors: formErrors }
  } = useForm({
    defaultValues: {
      name: '',
      description: '',
      sportType: 'FOOTBALL',
      maxTeams: 16,
      startDate: '',
      endDate: '',
      registrationDeadline: '',
      location: '',
      rules: '',
      prizeInfo: '',
      contactInfo: ''
    },
    mode: 'onChange'
  });

  // ZERO DOM MANIPULATION notification
  const showError = useCallback((message) => {
    console.error('‚ùå ERROR:', message);
    setError(message);

    // Native browser alert - NO DOM manipulation
    setTimeout(() => {
      try {
        alert(`‚ùå L·ªói: ${message}`);
      } catch (e) {
        console.error('Alert error:', e);
      }
    }, 50);
  }, []);

  // COMPLETELY SAFE success handler - NO DOM OPERATIONS
  const handleSuccess = useCallback((response) => {
    console.log('‚úÖ Tournament created successfully!');

    // NO DOM manipulation at all
    setIsSuccess(true);
    setIsSubmitting(false);
    setError('');

    // Show native success alert
    setTimeout(() => {
      alert('‚úÖ T·∫°o gi·∫£i ƒë·∫•u th√†nh c√¥ng!');
    }, 100);

    // Completely manual page refresh to avoid DOM conflicts
    setTimeout(() => {
      console.log('üîÑ Refreshing page to update tournament list...');
      window.location.reload();
    }, 1500);

  }, []);

  const createTournamentMutation = useMutation(
    async (tournamentData) => {
      console.log('üöÄ Creating tournament with data:', tournamentData);

      // Array format works best for your backend
      const formatArray = (date) => {
        const d = new Date(date);
        return [d.getFullYear(), d.getMonth() + 1, d.getDate(), 
               d.getHours(), d.getMinutes(), d.getSeconds()];
      };

      const dataWithFormattedDates = {
        ...tournamentData,
        startDate: formatArray(tournamentData.startDate),
        endDate: formatArray(tournamentData.endDate),
        registrationDeadline: formatArray(tournamentData.registrationDeadline)
      };

      console.log('üì§ Sending data with array format:', {
        startDate: dataWithFormattedDates.startDate,
        endDate: dataWithFormattedDates.endDate,
        registrationDeadline: dataWithFormattedDates.registrationDeadline
      });

      const result = await tournamentService.createTournament(dataWithFormattedDates);
      console.log('‚úÖ Backend responded successfully!');
      return result;
    },
    {
      onSuccess: (response) => {
        console.log('‚úÖ Mutation success:', response);
        handleSuccess(response);
      },
      onError: (error) => {
        console.error('‚ùå Tournament creation failed:', error);

        let errorMessage = 'C√≥ l·ªói x·∫£y ra khi t·∫°o gi·∫£i ƒë·∫•u';

        if (error.response?.data?.message) {
          errorMessage = error.response.data.message;
        } else if (error.message) {
          errorMessage = error.message;
        }

        showError(errorMessage);
        setIsSubmitting(false);
      }
    }
  );

  const watchStartDate = watch('startDate');

  const onSubmit = async (data) => {
    if (isSubmitting) return;

    try {
      console.log('üìù Form submitted with data:', data);
      setIsSubmitting(true);
      setError('');
      setIsSuccess(false);

      // Basic validation
      if (!data.name?.trim() || !data.description?.trim() || 
          !data.location?.trim() || !data.contactInfo?.trim()) {
        showError('Vui l√≤ng ƒëi·ªÅn ƒë·∫ßy ƒë·ªß th√¥ng tin b·∫Øt bu·ªôc');
        setIsSubmitting(false);
        return;
      }

      if (!data.startDate || !data.endDate || !data.registrationDeadline) {
        showError('Vui l√≤ng ch·ªçn ƒë·∫ßy ƒë·ªß ng√†y th√°ng');
        setIsSubmitting(false);
        return;
      }

      const startDate = new Date(data.startDate);
      const endDate = new Date(data.endDate);
      const regDeadline = new Date(data.registrationDeadline);
      const now = new Date();

      // Date validation
      const nowPlus1Min = new Date(now.getTime() + 60000);
      if (regDeadline <= nowPlus1Min) {
        showError('H·∫°n ƒëƒÉng k√Ω ph·∫£i sau th·ªùi ƒëi·ªÉm hi·ªán t·∫°i');
        setIsSubmitting(false);
        return;
      }

      if (regDeadline >= startDate) {
        showError('H·∫°n ƒëƒÉng k√Ω ph·∫£i tr∆∞·ªõc ng√†y b·∫Øt ƒë·∫ßu');
        setIsSubmitting(false);
        return;
      }

      if (startDate >= endDate) {
        showError('Ng√†y k·∫øt th√∫c ph·∫£i sau ng√†y b·∫Øt ƒë·∫ßu');
        setIsSubmitting(false);
        return;
      }

      // Prepare tournament data
      const tournamentData = {
        name: data.name.trim(),
        description: data.description.trim(),
        sportType: data.sportType || 'FOOTBALL',
        maxTeams: parseInt(data.maxTeams) || 16,
        startDate: startDate,
        endDate: endDate,
        registrationDeadline: regDeadline,
        location: data.location.trim(),
        rules: data.rules?.trim() || '',
        prizeInfo: data.prizeInfo?.trim() || '',
        contactInfo: data.contactInfo.trim()
      };

      console.log('üéØ Tournament data prepared:', tournamentData);

      await createTournamentMutation.mutateAsync(tournamentData);

    } catch (error) {
      console.error('üí• Form submission error:', error);
      setIsSubmitting(false);
    }
  };

  const handleClose = useCallback(() => {
    console.log('üîí Closing tournament form...');

    // Simple state cleanup - NO DOM manipulation
    setIsSubmitting(false);
    setError('');
    setIsSuccess(false);

    // Safe form reset
    try {
      reset();
    } catch (e) {
      console.warn('Reset error (non-critical):', e);
    }

    // Call parent close
    setTimeout(() => {
      if (onClose && typeof onClose === 'function') {
        onClose();
      }
    }, 50);
  }, [reset, onClose]);

  // Min dates calculation
  const now = useMemo(() => new Date(), []);
  const currentPlus10Min = useMemo(() => new Date(now.getTime() + 10 * 60000), [now]);
  const tomorrow = useMemo(() => {
    const date = new Date(now);
    date.setDate(now.getDate() + 1);
    return date;
  }, [now]);

  const minStartDate = useMemo(() => formatDateTimeForInput(tomorrow), [tomorrow]);
  const minEndDate = useMemo(() => {
    if (!watchStartDate) return minStartDate;
    try {
      const startDateObj = new Date(watchStartDate);
      const nextDay = new Date(startDateObj.getTime() + 24 * 60 * 60 * 1000);
      return formatDateTimeForInput(nextDay);
    } catch (error) {
      return minStartDate;
    }
  }, [watchStartDate, minStartDate]);

  const minRegDeadline = useMemo(() => formatDateTimeForInput(currentPlus10Min), [currentPlus10Min]);

  const maxRegDeadline = useMemo(() => {
    if (!watchStartDate) return '';
    try {
      const startDate = new Date(watchStartDate);
      const maxRegDate = new Date(startDate.getTime() - 24 * 60 * 60 * 1000);

      if (maxRegDate > currentPlus10Min) {
        return formatDateTimeForInput(maxRegDate);
      }
      return '';
    } catch (error) {
      return '';
    }
  }, [watchStartDate, currentPlus10Min]);

  if (!isOpen) return null;

  // Success state - show simple success message, no complex DOM
  if (isSuccess) {
    return (
      <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
        <div className="bg-white rounded-xl shadow-xl max-w-md w-full p-8 text-center">
          <div className="bg-green-100 p-4 rounded-full w-20 h-20 mx-auto mb-4 flex items-center justify-center">
            <Trophy className="h-10 w-10 text-green-600" />
          </div>
          <h2 className="text-2xl font-bold text-gray-900 mb-2">Th√†nh c√¥ng!</h2>
          <p className="text-gray-600 mb-4">Gi·∫£i ƒë·∫•u ƒë√£ ƒë∆∞·ª£c t·∫°o th√†nh c√¥ng.</p>
          <p className="text-sm text-gray-500">Trang s·∫Ω t·ª± ƒë·ªông c·∫≠p nh·∫≠t...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-xl shadow-xl max-w-4xl w-full max-h-[90vh] overflow-hidden">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-gray-200">
          <div className="flex items-center space-x-3">
            <div className="bg-orange-100 p-2 rounded-lg">
              <Trophy className="h-6 w-6 text-orange-600" />
            </div>
            <div>
              <h2 className="text-xl font-bold text-gray-900">T·∫°o Gi·∫£i ƒê·∫•u M·ªõi</h2>
              <p className="text-sm text-gray-600">ƒêi·ªÅn th√¥ng tin ƒë·ªÉ t·∫°o gi·∫£i ƒë·∫•u</p>
            </div>
          </div>
          <button
            onClick={handleClose}
            className="text-gray-400 hover:text-gray-600 transition-colors"
            disabled={isSubmitting}
          >
            <X className="h-6 w-6" />
          </button>
        </div>

        {/* Error notification */}
        {error && (
          <div className="p-4 border-b bg-red-50 border-red-200 text-red-800">
            <div className="flex items-center">
              <span className="text-sm font-medium">‚ùå {error}</span>
            </div>
          </div>
        )}

        {/* Form */}
        <form 
          ref={formRef}
