import React, { useState, useEffect } from 'react';
import { useMutation, useQueryClient, useQuery } from 'react-query';
import { 
  Trophy, 
  Save, 
  Clock, 
  Play, 
  CheckCircle, 
  AlertCircle,
  Users,
  Target,
  Edit3,
  Loader
} from 'lucide-react';
import { matchService } from '../../services';
import LoadingSpinner from '../LoadingSpinner';

const MatchResultsManager = ({ tournament, currentRound = 1, onMatchResultUpdated }) => {
  const [editingMatch, setEditingMatch] = useState(null);
  const [matchScores, setMatchScores] = useState({});
  const [notification, setNotification] = useState(null);
  const queryClient = useQueryClient();

  // Láº¥y dá»¯ liá»‡u cÃ¡c tráº­n Ä‘áº¥u cho giáº£i Ä‘áº¥u vÃ  vÃ²ng hiá»‡n táº¡i
  const { data: matches, isLoading, error, refetch } = useQuery(
    ['tournament-matches', tournament.id, currentRound],
    () => matchService.getMatchesByTournament(tournament.id, { round: currentRound }),
    {
      enabled: !!tournament.id,
      staleTime: 30000, // 30 giÃ¢y
      onSuccess: (data) => {
        console.log('ğŸ“‹ [MatchResultsManager] Dá»¯ liá»‡u tráº­n Ä‘áº¥u Ä‘Ã£ nháº­n:', data);
        console.log('ğŸ“‹ [MatchResultsManager] Loáº¡i dá»¯ liá»‡u:', typeof data);
        console.log('ğŸ“‹ [MatchResultsManager] CÃ³ pháº£i máº£ng khÃ´ng:', Array.isArray(data));
        if (data?.data) {
          console.log('ğŸ“‹ [MatchResultsManager] Dá»¯ liá»‡u lá»“ng:', data.data);
          console.log('ğŸ“‹ [MatchResultsManager] Loáº¡i dá»¯ liá»‡u lá»“ng:', typeof data.data);
          console.log('ğŸ“‹ [MatchResultsManager] Lá»“ng cÃ³ pháº£i máº£ng khÃ´ng:', Array.isArray(data.data));
        }
      },
      onError: (error) => {
        console.error('KhÃ´ng thá»ƒ láº¥y dá»¯ liá»‡u tráº­n Ä‘áº¥u:', error);
        showNotification('error', 'KhÃ´ng thá»ƒ táº£i cÃ¡c tráº­n Ä‘áº¥u');
      }
    }
  );

  // Mutation cáº­p nháº­t Ä‘iá»ƒm tráº­n Ä‘áº¥u
  const updateScoreMutation = useMutation(
    ({ matchId, scoreData }) => matchService.updateMatchScore(matchId, scoreData),
    {
      onSuccess: (data, variables) => {
        showNotification('success', 'Äiá»ƒm tráº­n Ä‘áº¥u Ä‘Ã£ Ä‘Æ°á»£c cáº­p nháº­t thÃ nh cÃ´ng!');
        queryClient.invalidateQueries(['tournament-matches', tournament.id]);
        queryClient.invalidateQueries(['tournament-bracket', tournament.id]);
        setEditingMatch(null);
        setMatchScores(prev => ({ ...prev, [variables.matchId]: {} }));
        // KÃ­ch hoáº¡t callback cho component cha
        onMatchResultUpdated?.();
      },
      onError: (error) => {
        console.error('KhÃ´ng thá»ƒ cáº­p nháº­t Ä‘iá»ƒm tráº­n Ä‘áº¥u:', error);
        showNotification('error', error.response?.data?.message || 'KhÃ´ng thá»ƒ cáº­p nháº­t Ä‘iá»ƒm tráº­n Ä‘áº¥u');
      }
    }
  );

  // Mutation cáº­p nháº­t tráº¡ng thÃ¡i tráº­n Ä‘áº¥u
  const updateStatusMutation = useMutation(
