// src/layouts/AdminLayout.js (hoặc src/components/AdminLayout.js)
import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom'; // Thêm useLocation
import {
  Users,
  Trophy,
  FileText,
  Shield,
  BarChart3,
  Home,
  User,
  AlertTriangle
} from 'lucide-react';
import { useAuth } from '../context/AuthContext';
import LoadingSpinner from '../components/LoadingSpinner'; // Đảm bảo đường dẫn đúng
import toast from 'react-hot-toast';

const AdminLayout = ({ children }) => {
  const { user, loading } = useAuth(); // Lấy loading từ useAuth
  const location = useLocation(); // Lấy thông tin về URL hiện tại

  const isAdmin = user?.role === 'ADMIN';
  const isOrganizer = user?.role === 'ORGANIZER';

  const allTabs = [
    {
      id: 'dashboard',
      name: 'Dashboard',
      icon: BarChart3,
      path: '/admin', // Đường dẫn cho Dashboard
      roles: ['ADMIN']
    },
    {
      id: 'tournaments',
      name: 'Quản lý giải đấu',
      icon: Trophy,
      path: '/admin/tournaments', // Đường dẫn cho Quản lý giải đấu
      roles: ['ADMIN', 'ORGANIZER']
    },
    {
      id: 'users',
      name: 'Quản lý tài khoản',
      icon: Users,
      path: '/admin/users', // Đường dẫn cho Quản lý tài khoản
      roles: ['ADMIN']
    },
    {
      id: 'news',
      name: 'Quản lý tin tức',
      icon: FileText,
      path: '/admin/news', // Đường dẫn cho Quản lý tin tức
      roles: ['ADMIN', 'ORGANIZER']
    },
  ];

  const accessibleTabs = allTabs.filter(tab => tab.roles.includes(user?.role));

  // Xác định tab đang hoạt động dựa trên URL hiện tại
  const [activeTab, setActiveTab] = useState('');

  useEffect(() => {
    // Tìm tab phù hợp với đường dẫn hiện tại
    const currentPath = location.pathname;
    const foundTab = accessibleTabs.find(tab => currentPath.startsWith(tab.path));
    if (foundTab) {
      setActiveTab(foundTab.id);
    } else {
      // Fallback nếu không khớp với tab nào, ví dụ: trang chi tiết giải đấu
      if (currentPath.startsWith('/admin/tournaments/')) {
        setActiveTab('tournaments'); // Vẫn highlight tab giải đấu khi ở trang chi tiết giải đấu
      } else if (isAdmin && currentPath === '/admin') {
        setActiveTab('dashboard');
      } else if ((isAdmin || isOrganizer) && currentPath === '/admin/tournaments') {
        setActiveTab('tournaments');
      }
    }
  }, [location.pathname, accessibleTabs, isAdmin, isOrganizer]);


  if (loading) { // Hiển thị loading spinner nếu đang tải thông tin user
    return <LoadingSpinner text="Đang kiểm tra quyền truy cập..." />;
  }

  if (!isAdmin && !isOrganizer) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <AlertTriangle className="h-16 w-16 text-red-500 mx-auto mb-4" />
          <h2 className="text-2xl font-bold text-gray-900 mb-2">Access Denied</h2>
          <p className="text-gray-600">Bạn không có quyền truy cập trang này.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="flex min-h-screen bg-gray-100">
      {/* Top Header */}
      <div className="fixed top-0 left-0 right-0 z-50 flex items-center justify-between h-16 bg-gradient-to-r from-red-700 to-blue-700 text-white shadow-lg">
        <div className="flex items-center ml-4">
          <Link to="/" className="flex items-center text-white no-underline">
            <Shield className="h-8 w-8 mr-3 text-white" />
            <span className="text-xl font-bold">Hệ thống quản lý giải đấu</span>
          </Link>
        </div>
        <div className="flex items-center space-x-6 mr-6">
          <Link to="/" className="text-white hover:text-gray-200 transition-colors flex items-center space-x-2">
            <Home className="h-5 w-5" />
            <span>Trang chủ</span>
          </Link>
          <div className="flex items-center space-x-2">
            <User className="h-5 w-5" />
            <span className="font-medium">{user?.name || 'Guest'}</span>
          </div>
        </div>
      </div>

      {/* Sidebar */}
      <div className="fixed top-16 left-0 w-64 h-[calc(100vh-4rem)] bg-gray-800 text-white p-4 shadow-xl z-40">
        <div className="mb-6 mt-4">
          <h2 className="text-xl font-semibold mb-4 border-b border-gray-700 pb-2">Admin Menu</h2>
          <nav>
            <ul>
              {accessibleTabs.map((tab) => (
                <li key={tab.id} className="mb-2">
                  <Link
                    to={tab.path} // Sử dụng Link với path
                    className={`flex items-center w-full px-4 py-2 rounded-lg transition-colors duration-200 ${
                      activeTab === tab.id
                        ? 'bg-blue-600 text-white'
                        : 'text-gray-300 hover:bg-gray-700 hover:text-white'
                    }`}
                  >
                    <tab.icon className="h-5 w-5 mr-3" />
                    <span className="font-medium">{tab.name}</span>
                  </Link>
                </li>
              ))}
            </ul>
          </nav>
        </div>

        <div className="absolute bottom-4 left-4 right-4 text-xs text-gray-400">
          <p>Tải ứng dụng:</p>
          <div className="flex space-x-2 mt-2">
            <a href="https://play.google.com" target="_blank" rel="noopener noreferrer">
              <img src="https://play.google.com/intl/en_us/badges/images/generic/en_badge_web_generic.png" alt="Google Play" className="h-7" />
            </a>
            <a href="https://www.apple.com/app-store/" target="_blank" rel="noopener noreferrer">
              <img src="https://developer.apple.com/assets/elements/badges/download-on-the-app-store.svg" alt="App Store" className="h-7" />
            </a>
          </div>
        </div>
      </div>

      {/* Main Content Area - Render children here */}
      <div className="flex-1 ml-64 mt-16 p-8 bg-gray-50 overflow-auto">
        {children}
      </div>
    </div>
  );
};

export default AdminLayout;
