import React, { useState, useCallback } from 'react';
import { useForm } from 'react-hook-form';
import { useMutation, useQueryClient } from 'react-query';
import { X, Calendar, Users, MapPin, Trophy, FileText, Upload } from 'lucide-react';
import { tournamentService } from '../../services';
import { 
  SPORT_TYPES, 
  SPORT_TYPE_LABELS,
  TOURNAMENT_STATUS,
  VALIDATION_RULES,
  QUERY_KEYS
} from '../../utils/constants';
import { formatDateTimeForInput } from '../../utils/helpers';
import toast from 'react-hot-toast';

const TournamentCreateFormSafe = ({ isOpen, onClose, onSuccess }) => {
  const [isSubmitting, setIsSubmitting] = useState(false);
  const queryClient = useQueryClient();

  const {
    register,
    handleSubmit,
    watch,
    reset,
    formState: { errors, isValid }
  } = useForm({
    defaultValues: {
      name: '',
      description: '',
      sportType: 'FOOTBALL',
      maxTeams: 16,
      startDate: '',
      endDate: '',
      registrationDeadline: '',
      location: '',
      rules: '',
      prizeInfo: '',
      contactInfo: ''
    },
    mode: 'onChange'
  });

  // Safe toast function to prevent DOM errors
  const showToast = useCallback((type, message) => {
    try {
      // Use setTimeout to ensure DOM is ready
      setTimeout(() => {
        switch (type) {
          case 'success':
            toast.success(message, { 
              id: 'tournament-create-success',
              duration: 3000 
            });
            break;
          case 'error':
            toast.error(message, { 
              id: 'tournament-create-error',
              duration: 4000 
            });
            break;
          default:
            toast(message, { 
              id: 'tournament-create-info',
              duration: 3000 
            });
        }
      }, 0);
    } catch (error) {
      console.error('Toast error:', error);
      // Fallback to console if toast fails
      console.log(`${type.toUpperCase()}: ${message}`);
    }
  }, []);

  const createTournamentMutation = useMutation(
    (tournamentData) => {
      console.log('üöÄ Calling tournamentService.createTournament with:', tournamentData);
      return tournamentService.createTournament(tournamentData);
    },
    {
      onSuccess: (response) => {
        console.log('‚úÖ Tournament created successfully:', response);

        // Safe success handling
        try {
          showToast('success', 'T·∫°o gi·∫£i ƒë·∫•u th√†nh c√¥ng!');
          queryClient.invalidateQueries(QUERY_KEYS.TOURNAMENTS);

          if (onSuccess) {
            onSuccess(response?.data || response);
          }

          handleClose();
        } catch (error) {
          console.error('Error in success handler:', error);
          handleClose();
        }
      },
      onError: (error) => {
        console.error('‚ùå Tournament creation failed:', error);
        console.error('Error details:', {
          message: error.message,
          response: error.response?.data,
          status: error.response?.status
        });

        const errorMessage = error.response?.data?.message || 
                           error.response?.data?.error || 
                           error.message || 
                           'C√≥ l·ªói x·∫£y ra khi t·∫°o gi·∫£i ƒë·∫•u';

        showToast('error', errorMessage);
        setIsSubmitting(false);
      }
    }
  );

  const watchStartDate = watch('startDate');

  const onSubmit = async (data) => {
    try {
      console.log('üìù Form submitted with data:', data);
      setIsSubmitting(true);

      // Validate required fields
      if (!data.name || !data.description || !data.location || !data.contactInfo) {
        showToast('error', 'Vui l√≤ng ƒëi·ªÅn ƒë·∫ßy ƒë·ªß th√¥ng tin b·∫Øt bu·ªôc');
        setIsSubmitting(false);
        return;
      }

      // Validate dates
      if (!data.startDate || !data.endDate || !data.registrationDeadline) {
        showToast('error', 'Vui l√≤ng ch·ªçn ƒë·∫ßy ƒë·ªß ng√†y th√°ng');
        setIsSubmitting(false);
        return;
      }

      const startDate = new Date(data.startDate);
      const endDate = new Date(data.endDate);
      const regDeadline = new Date(data.registrationDeadline);
      const now = new Date();

      if (regDeadline <= now) {
        showToast('error', 'H·∫°n ƒëƒÉng k√Ω ph·∫£i sau th·ªùi ƒëi·ªÉm hi·ªán t·∫°i');
        setIsSubmitting(false);
        return;
      }

      if (regDeadline >= startDate) {
        showToast('error', 'H·∫°n ƒëƒÉng k√Ω ph·∫£i tr∆∞·ªõc ng√†y b·∫Øt ƒë·∫ßu gi·∫£i ƒë·∫•u');
        setIsSubmitting(false);
        return;
      }

      if (startDate >= endDate) {
        showToast('error', 'Ng√†y k·∫øt th√∫c ph·∫£i sau ng√†y b·∫Øt ƒë·∫ßu');
        setIsSubmitting(false);
        return;
      }

      // Prepare tournament data to match backend expectations
      const tournamentData = {
        name: data.name.trim(),
        description: data.description.trim(),
        sportType: data.sportType || 'FOOTBALL',
        maxTeams: parseInt(data.maxTeams) || 16,
        startDate: startDate.toISOString(),
        endDate: endDate.toISOString(),
        registrationDeadline: regDeadline.toISOString(),
        location: data.location.trim(),
        rules: data.rules?.trim() || '',
        prizeInfo: data.prizeInfo?.trim() || '',
        contactInfo: data.contactInfo.trim()
      };

      console.log('üéØ Final tournament data to send:', tournamentData);
      await createTournamentMutation.mutateAsync(tournamentData);
    } catch (error) {
      console.error('üí• Tournament creation error in onSubmit:', error);
      setIsSubmitting(false);
    }
  };

  const handleClose = useCallback(() => {
    try {
      setIsSubmitting(false);
      reset();
      if (onClose) {
        onClose();
      }
    } catch (error) {
      console.error('Error in handleClose:', error);
      // Force close even if there's an error
      if (onClose) {
        onClose();
      }
    }
  }, [reset, onClose]);

  // Generate min dates
  const today = new Date();
  const tomorrow = new Date(today);
  tomorrow.setDate(today.getDate() + 1);

  const minStartDate = formatDateTimeForInput(tomorrow);
  const minEndDate = watchStartDate ? 
    formatDateTimeForInput(new Date(new Date(watchStartDate).getTime() + 24 * 60 * 60 * 1000)) : 
    minStartDate;
  const maxRegDeadline = watchStartDate ? 
    formatDateTimeForInput(new Date(new Date(watchStartDate).getTime() - 24 * 60 * 60 * 1000)) : 
    '';

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-xl shadow-xl max-w-4xl w-full max-h-[90vh] overflow-hidden">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-gray-200">
          <div className="flex items-center space-x-3">
            <div className="bg-orange-100 p-2 rounded-lg">
              <Trophy className="h-6 w-6 text-orange-600" />
            </div>
            <div>
              <h2 className="text-xl font-bold text-gray-900">T·∫°o Gi·∫£i ƒê·∫•u M·ªõi</h2>
              <p className="text-sm text-gray-600">ƒêi·ªÅn th√¥ng tin ƒë·ªÉ t·∫°o gi·∫£i ƒë·∫•u</p>
            </div>
          </div>
          <button
            onClick={handleClose}
            className="text-gray-400 hover:text-gray-600 transition-colors"
            disabled={isSubmitting}
          >
            <X className="h-6 w-6" />
          </button>
        </div>

        {/* Form */}
        <form onSubmit={handleSubmit(onSubmit)} className="overflow-y-auto max-h-[calc(90vh-140px)]">
          <div className="p-6 space-y-6">
            {/* Basic Information */}
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              {/* Tournament Name */}
              <div className="lg:col-span-2">
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  T√™n gi·∫£i ƒë·∫•u *
                </label>
                <input
                  type="text"
                  {...register('name', {
                    required: 'T√™n gi·∫£i ƒë·∫•u l√† b·∫Øt bu·ªôc',
                    minLength: {
                      value: 3,
                      message: 'T√™n gi·∫£i ƒë·∫•u ph·∫£i c√≥ √≠t nh·∫•t 3 k√Ω t·ª±'
                    },
                    maxLength: {
                      value: 100,
                      message: 'T√™n gi·∫£i ƒë·∫•u kh√¥ng ƒë∆∞·ª£c v∆∞·ª£t qu√° 100 k√Ω t·ª±'
                    }
                  })}
                  className={`w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent ${errors.name ? 'border-red-500' : ''}`}
                  placeholder="Nh·∫≠p t√™n gi·∫£i ƒë·∫•u"
                  disabled={isSubmitting}
                />
                {errors.name && (
                  <p className="mt-1 text-sm text-red-600">{errors.name.message}</p>
                )}
              </div>

              {/* Sport Type */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  M√¥n th·ªÉ thao *
                </label>
                <select
                  {...register('sportType', { required: 'Vui l√≤ng ch·ªçn m√¥n th·ªÉ thao' })}
                  className={`w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent ${errors.sportType ? 'border-red-500' : ''}`}
                  disabled={isSubmitting}
                >
                  <option value="FOOTBALL">B√≥ng ƒë√°</option>
                  <option value="BASKETBALL">B√≥ng r·ªï</option>
                  <option value="VOLLEYBALL">B√≥ng chuy·ªÅn</option>
                  <option value="BADMINTON">C·∫ßu l√¥ng</option>
                  <option value="TENNIS">Qu·∫ßn v·ª£t</option>
                  <option value="PING_PONG">B√≥ng b√†n</option>
                  <option value="GENERAL">T·ªïng h·ª£p</option>
                </select>
                {errors.sportType && (
                  <p className="mt-1 text-sm text-red-600">{errors.sportType.message}</p>
                )}
              </div>

              {/* Max Teams */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  S·ªë ƒë·ªôi t·ªëi ƒëa *
                </label>
                <select
                  {...register('maxTeams', { 
                    required: 'Vui l√≤ng ch·ªçn s·ªë ƒë·ªôi t·ªëi ƒëa'
                  })}
                  className={`w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent ${errors.maxTeams ? 'border-red-500' : ''}`}
                  disabled={isSubmitting}
                >
                  <option value={4}>4 ƒë·ªôi</option>
                  <option value={8}>8 ƒë·ªôi</option>
                  <option value={16}>16 ƒë·ªôi</option>
                  <option value={32}>32 ƒë·ªôi</option>
                  <option value={64}>64 ƒë·ªôi</option>
                </select>
                {errors.maxTeams && (
                  <p className="mt-1 text-sm text-red-600">{errors.maxTeams.message}</p>
                )}
              </div>
            </div>

            {/* Description */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                M√¥ t·∫£ gi·∫£i ƒë·∫•u *
              </label>
              <textarea
                {...register('description', {
                  required: 'M√¥ t·∫£ gi·∫£i ƒë·∫•u l√† b·∫Øt bu·ªôc',
                  minLength: { value: 10, message: 'M√¥ t·∫£ ph·∫£i c√≥ √≠t nh·∫•t 10 k√Ω t·ª±' }
                })}
                rows={3}
                className={`w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent resize-none ${errors.description ? 'border-red-500' : ''}`}
                placeholder="M√¥ t·∫£ chi ti·∫øt v·ªÅ gi·∫£i ƒë·∫•u"
                disabled={isSubmitting}
              />
              {errors.description && (
                <p className="mt-1 text-sm text-red-600">{errors.description.message}</p>
              )}
            </div>

            {/* Dates */}
            <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Ng√†y b·∫Øt ƒë·∫ßu *
                </label>
                <input
                  type="datetime-local"
                  {...register('startDate', { required: 'Ng√†y b·∫Øt ƒë·∫ßu l√† b·∫Øt bu·ªôc' })}
                  min={minStartDate}
                  className={`w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent ${errors.startDate ? 'border-red-500' : ''}`}
                  disabled={isSubmitting}
                />
                {errors.startDate && (
                  <p className="mt-1 text-sm text-red-600">{errors.startDate.message}</p>
                )}
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Ng√†y k·∫øt th√∫c *
                </label>
                <input
                  type="datetime-local"
                  {...register('endDate', { required: 'Ng√†y k·∫øt th√∫c l√† b·∫Øt bu·ªôc' })}
                  min={minEndDate}
                  className={`w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent ${errors.endDate ? 'border-red-500' : ''}`}
                  disabled={isSubmitting}
                />
                {errors.endDate && (
                  <p className="mt-1 text-sm text-red-600">{errors.endDate.message}</p>
                )}
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  H·∫°n ƒëƒÉng k√Ω *
                </label>
                <input
                  type="datetime-local"
                  {...register('registrationDeadline', { required: 'H·∫°n ƒëƒÉng k√Ω l√† b·∫Øt bu·ªôc' })}
                  min={formatDateTimeForInput(today)}
                  max={maxRegDeadline}
                  className={`w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent ${errors.registrationDeadline ? 'border-red-500' : ''}`}
                  disabled={isSubmitting}
