import React, { useState, useRef, useEffect } from 'react';
import { useQuery, useMutation, useQueryClient } from 'react-query';
import {
  FileText,
  Search,
  Filter,
  Plus,
  Edit,
  Trash2,
  Eye,
  Calendar,
  User,
  Image,
  AlertTriangle,
  X
} from 'lucide-react';
import newsService from '../../services/newsService'; // Đảm bảo đường dẫn đúng
import LoadingSpinner from '../LoadingSpinner'; // Đảm bảo đường dẫn đúng
import toast from 'react-hot-toast'; // Đảm bảo bạn đã cài react-hot-toast

// Helper functions (Đảm bảo các hàm này tồn tại hoặc tạo file utils/helpers.js và import vào)
const formatDate = (dateString) => {
  if (!dateString) return 'N/A';
  const options = { year: 'numeric', month: 'short', day: 'numeric' };
  try {
    return new Date(dateString).toLocaleDateString(undefined, options);
  } catch (e) {
    console.error("Invalid date format:", dateString, e);
    return 'Invalid Date';
  }
};

const truncateText = (text, maxLength) => {
  if (!text) return '';
  if (text.length <= maxLength) return text;
  return text.substring(0, maxLength) + '...';
};

const NewsManagement = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [page, setPage] = useState(1);
  const [showCreateModal, setShowCreateModal] = useState(false);

  // State cho form tạo tin tức, mô phỏng NewsDTO và selectedFile
  const [newsForm, setNewsForm] = useState({
    name: '',
    shortDescription: '',
    content: ''
  });
  const [selectedFile, setSelectedFile] = useState(null); // Chỉ 1 file như Angular cũ

  const fileInputRef = useRef(null); // Ref cho input file

  const queryClient = useQueryClient();

  // --- API Integration: Fetch All News Articles ---
  const { data: news, isLoading, error } = useQuery(
    ['admin-news', { page, searchTerm }],
    async () => {
      console.log("DEBUG FETCH: Fetching news with page:", page, "searchTerm:", searchTerm);
      const allNews = await newsService.getAllNews();

      let filteredNews = allNews;
      if (searchTerm) {
        filteredNews = allNews.filter(article =>
          article.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
          article.content.toLowerCase().includes(searchTerm.toLowerCase()) ||
          (article.shortDescription && article.shortDescription.toLowerCase().includes(searchTerm.toLowerCase()))
        );
      }

      const itemsPerPage = 6;
      const startIndex = (page - 1) * itemsPerPage;
      const endIndex = startIndex + itemsPerPage;
      const paginatedNews = filteredNews.slice(startIndex, endIndex);

      return {
        data: paginatedNews,
        pagination: {
          currentPage: page,
          totalPages: Math.ceil(filteredNews.length / itemsPerPage),
          totalItems: filteredNews.length,
          hasNext: page < Math.ceil(filteredNews.length / itemsPerPage),
          hasPrev: page > 1
        }
      };
    },
    {
      staleTime: 2 * 60 * 1000,
      keepPreviousData: true,
      onError: (err) => {
        console.error("DEBUG FETCH ERROR: Error fetching news articles:", err);
        toast.error(err.response?.data?.message || 'Failed to fetch news articles.');
      }
    }
  );

  // --- API Integration: Delete News Article ---
  const deleteNewsMutation = useMutation(
    (newsId) => newsService.deleteNews(newsId),
    {
      onSuccess: () => {
        toast.success('News article deleted successfully');
        queryClient.invalidateQueries('admin-news');
      },
      onError: (error) => {
        console.error("DEBUG DELETE ERROR: Error deleting news article:", error);
        toast.error(error.response?.data?.message || 'Failed to delete news article');
      }
    }
  );

  // --- API Integration: Create News Article and Upload Attachments ---
  const createNewsMutation = useMutation(
    async ({ newsData, file }) => { // Chỉ nhận 1 file
      console.log("DEBUG MUTATION START: newsData received:", newsData);
      console.log("DEBUG MUTATION START: file received:", file);

      // 1. Call API to create the news article first
      // Giả định response của createNews là { success: true, data: { id: '...', ... } }
      // hoặc { success: true, id: '...', ... }
      const createdNewsResponse = await newsService.createNews(newsData);

      // Điều chỉnh cách lấy ID cho phù hợp với backend của bạn
      // Kiểm tra cả `res.data.id` và `res.id`
      const newsId = createdNewsResponse.data?.id ?? createdNewsResponse.id;

      console.log("DEBUG MUTATION: News created with ID:", newsId);

      // 2. If there is a file, proceed with uploading it
      if (file) {
        if (!newsId) {
            console.error("DEBUG MUTATION ERROR: News ID is missing after creation, cannot upload attachment.");
            throw new Error("News ID not found after creation. Failed to upload attachment.");
        }
        console.log(`DEBUG MUTATION: Attempting to upload file '${file.name}' for news ID: ${newsId}`);
        // newsService.uploadNewsAttachments của bạn nhận mảng files, nên cần bọc 1 file vào mảng
        await newsService.uploadNewsAttachments(newsId, [file]);
        console.log("DEBUG MUTATION: Attachment uploaded successfully.");
      } else {
        console.log("DEBUG MUTATION: No file selected to upload, skipping attachment API call.");
      }
      return createdNewsResponse;
    },
    {
      onSuccess: () => {
        toast.success('News article created successfully' + (selectedFile ? ' and attachment uploaded!' : '!'));
        queryClient.invalidateQueries('admin-news');
        setShowCreateModal(false); // Đóng modal
        resetForm(); // Reset form
      },
      onError: (error) => {
        console.error("DEBUG MUTATION ERROR: Error in createNewsMutation:", error);
        let errorMessage = error.response?.data?.message || error.errorMessage || 'Failed to create news article or upload attachments.';
        if (error.errorData && typeof error.errorData === 'string' && error.errorData.includes("not-null property references a null or transient value")) {
            errorMessage = "Lỗi dữ liệu: Tên hoặc nội dung tin tức không được để trống.";
        } else if (error.errorMessage && error.errorMessage.includes("News ID not found")) {
            errorMessage = "Lỗi: Không thể lấy ID tin tức sau khi tạo. Vui lòng thử lại.";
        } else if (error.errorMessage && error.errorMessage.includes("Failed to upload attachment.")) {
            errorMessage = "Lỗi: Tin tức đã được tạo, nhưng không thể tải lên tệp đính kèm. Vui lòng kiểm tra lại.";
        }
        toast.error(errorMessage);
      }
    }
  );

  // --- Event Handlers ---

  const handleSearch = (e) => {
    e.preventDefault();
    setPage(1);
    queryClient.invalidateQueries('admin-news');
  };

  const handleDeleteNews = (newsId) => {
    if (window.confirm('Bạn có chắc muốn xóa bài đăng này?')) {
      deleteNewsMutation.mutate(newsId);
    }
  };

  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setNewsForm(prev => ({ ...prev, [name]: value }));
  };

  const handleFileChange = (e) => {
    const file = e.target.files && e.target.files[0] ? e.target.files[0] : null;
    setSelectedFile(file);
    console.log("DEBUG HANDLEFILECHANGE: File selected:", file);
  };

  const handleCreateNews = (e) => {
    e.preventDefault();

    // Frontend validation
    if (!newsForm.name || newsForm.name.trim() === '') {
        toast.error('Tên tin tức không được để trống.');
        return;
    }
    if (!newsForm.content || newsForm.content.trim() === '') {
        toast.error('Nội dung tin tức không được để trống.');
        return;
    }

    // DEBUG: Log form state and selected file before calling mutate
    console.log("DEBUG HANDLECREATE: newsForm state before calling mutate:", newsForm);
    console.log("DEBUG HANDLECREATE: selectedFile state before calling mutate:", selectedFile);

    // Call the mutation
    createNewsMutation.mutate({ newsData: newsForm, file: selectedFile });
  };

  const resetForm = () => {
    setNewsForm({
      name: '',
      shortDescription: '',
      content: ''
    });
    setSelectedFile(null);
    if (fileInputRef.current) {
      fileInputRef.current.value = ''; // Reset the native file input element
    }
  };

  // --- Effect for Modal Cleanup ---
  useEffect(() => {
    if (!showCreateModal) {
      resetForm(); // Reset form state when modal closes
    }
  }, [showCreateModal]);

  // --- UI Rendering ---

  if (error) {
    return (
      <div className="text-center py-8">
        <AlertTriangle className="h-12 w-12 text-red-500 mx-auto mb-4" />
        <p className="text-red-600">Error loading news articles. Please try again later.</p>
      </div>
    );
  }

  return (
    <div className="space-y-6 p-6">
      {/* Header Section */}
      <div className="flex flex-col sm:flex-row items-center justify-between gap-4">
        <div>
          <h2 className="text-3xl font-bold text-gray-900">News Management</h2>
          <p className="text-gray-600">Create, edit, and publish news articles and announcements.</p>
        </div>
        <div className="flex items-center space-x-3">
          <div className="bg-purple-100 text-purple-800 px-4 py-2 rounded-full text-sm font-medium">
            {news?.pagination?.totalItems || 0} Articles
          </div>
          <button
            onClick={() => setShowCreateModal(true)}
            className="btn-primary"
          >
            <Plus className="h-4 w-4 mr-2" />
            Create Article
          </button>
        </div>
      </div>

      {/* Search Section */}
      <div className="bg-white rounded-lg border border-gray-200 p-4 shadow-sm">
        <form onSubmit={handleSearch} className="flex flex-col sm:flex-row gap-4">
          <div className="flex-1">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-5 w-5" />
              <input
                type="text"
                placeholder="Search news articles by title or content..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pl-10 input-field"
              />
            </div>
          </div>
          <button
            type="submit"
            className="btn-primary whitespace-nowrap"
          >
            <Filter className="h-4 w-4 mr-2" />
            Search
          </button>
        </form>
      </div>

      {/* News Grid Display */}
      <div className="bg-white rounded-lg border border-gray-200 overflow-hidden shadow-sm">
        {isLoading ? (
          <div className="p-8 text-center">
            <LoadingSpinner />
            <p className="text-gray-500 mt-2">Loading news articles...</p>
          </div>
        ) : news?.data?.length === 0 ? (
          <div className="p-8 text-center">
            <FileText className="h-16 w-16 text-gray-400 mx-auto mb-4" />
            <p className="text-gray-600 text-lg mb-4">No news articles found.</p>
            <button
              onClick={() => setShowCreateModal(true)}
              className="btn-primary"
            >
              <Plus className="h-4 w-4 mr-2" />
              Create Your First Article
            </button>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 p-6">
            {news?.data?.map((article) => (
              <div key={article.id} className="border border-gray-200 rounded-lg overflow-hidden bg-white shadow-md hover:shadow-lg transition-shadow duration-300 flex flex-col">
                {/* Article Image (using newsService.getImageUrl) */}
                <div className="w-full h-48 bg-gray-200 flex items-center justify-center overflow-hidden">
                  {article.attachments && article.attachments.length > 0 ? (
                    <img
                      src={newsService.getImageUrl(article.attachments[0].url)}
                      alt={article.name}
                      className="w-full h-full object-cover"
                      onError={(e) => { e.target.onerror = null; e.target.src = 'https://via.placeholder.com/400x200?text=No+Image'; }}
                    />
                  ) : (
                    <Image className="h-16 w-16 text-gray-400" />
                  )}
                </div>

                {/* Article Content Display */}
                <div className="p-4 flex flex-col flex-grow">
                  <h3 className="text-xl font-semibold text-gray-900 mb-2 line-clamp-2">
                    {article.name}
                  </h3>

                  <p className="text-gray-600 text-sm mb-3 line-clamp-3 flex-grow">
                    {truncateText(article.shortDescription || article.content, 120)}
                  </p>

                  {/* Article Meta Data */}
                  <div className="flex items-center text-xs text-gray-500 mb-4 mt-auto">
                    <Calendar className="h-3 w-3 mr-1" />
                    <span>{formatDate(article.createdAt)}</span>
                    <User className="h-3 w-3 ml-4 mr-1" />
                    <span>{article.author || 'EduSports Team'}</span>
                  </div>

                  {/* Action Buttons for each article */}
                  <div className="flex items-center justify-between pt-2 border-t border-gray-100">
                    <div className="flex items-center space-x-3">
                      <button
                        onClick={() => window.open(`/news/${article.id}`, '_blank')}
                        className="btn-icon"
                        title="View Article"
                      >
                        <Eye className="h-4 w-4" />
                      </button>
                      <button
                        onClick={() => window.open(`/admin/news/${article.id}/edit`, '_blank')}
                        className="btn-icon text-blue-600 hover:text-blue-700"
                        title="Edit Article"
                      >
                        <Edit className="h-4 w-4" />
                      </button>
                    </div>

                    <button
                      onClick={() => handleDeleteNews(article.id)}
                      className="btn-icon text-red-600 hover:text-red-700"
                      title="Delete Article"
                      disabled={deleteNewsMutation.isLoading}
                    >
                      <Trash2 className="h-4 w-4" />
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}

        {/* Pagination Controls */}
        {news?.pagination?.totalPages > 1 && (
          <div className="bg-white px-4 py-3 border-t border-gray-200 sm:px-6 flex items-center justify-between">
            <div className="text-sm text-gray-700">
              Showing page <span className="font-medium">{news.pagination.currentPage}</span> of <span className="font-medium">{news.pagination.totalPages}</span> ({news.pagination.totalItems} items)
            </div>
            <div className="flex space-x-2">
              <button
                onClick={() => setPage(prev => prev - 1)}
                disabled={!news.pagination.hasPrev || deleteNewsMutation.isLoading || createNewsMutation.isLoading}
                className="btn-secondary disabled:opacity-50 disabled:cursor-not-allowed"
              >
                Previous
              </button>
              <button
                onClick={() => setPage(prev => prev + 1)}
                disabled={!news.pagination.hasNext || deleteNewsMutation.isLoading || createNewsMutation.isLoading}
                className="btn-secondary disabled:opacity-50 disabled:cursor-not-allowed"
              >
                Next
              </button>
            </div>
          </div>
        )}
      </div>

      {/* Create News Modal (includes file upload functionality) */}
      {showCreateModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-xl shadow-xl max-w-2xl w-full mx-auto max-h-[90vh] overflow-y-auto">
            <div className="p-6">
              <div className="flex justify-between items-center mb-4">
                <h3 className="text-2xl font-bold text-gray-900">Create New News Article</h3>
                <button
                  onClick={() => setShowCreateModal(false)}
                  className="text-gray-500 hover:text-gray-700 transition-colors"
                >
                  <X className="h-6 w-6" />
                </button>
              </div>
              <form onSubmit={handleCreateNews} className="space-y-4">
                <div>
                  <label htmlFor="name" className="block text-sm font-medium text-gray-700 mb-2">
                    Article Name <span className="text-red-500">*</span>
                  </label>
                  <input
                    id="name"
                    name="name"
                    type="text"
                    required
                    className="input-field"
                    placeholder="Enter article name..."
                    value={newsForm.name}
                    onChange={handleFormChange}
                  />
                </div>

                <div>
                  <label htmlFor="shortDescription" className="block text-sm font-medium text-gray-700 mb-2">
                    Short Description (Optional)
                  </label>
                  <textarea
                    id="shortDescription"
                    name="shortDescription"
                    rows={3}
                    className="input-field"
                    placeholder="Brief summary of the article..."
                    value={newsForm.shortDescription}
                    onChange={handleFormChange}
                  />
                </div>

                <div>
                  <label htmlFor="content" className="block text-sm font-medium text-gray-700 mb-2">
                    Article Content <span className="text-red-500">*</span>
                  </label>
                  <textarea
                    id="content"
                    name="content"
                    rows={8}
                    required
                    className="input-field"
                    placeholder="Write your article content here..."
                    value={newsForm.content}
                    onChange={handleFormChange}
                  />
                </div>

                {/* File Upload Section (chỉ 1 file) */}
                <div>
                  <label htmlFor="file-upload" className="block text-sm font-medium text-gray-700 mb-2">
                    Upload Image (Max 1 file, 10MB, image format only)
                  </label>
                  <button type="button"
                          onClick={() => fileInputRef.current?.click()}
                          className="btn btn-outline-primary btn-sm mb-2"
                  >
                    Chọn ảnh…
                  </button>
                  <input
                    id="file-upload"
                    type="file"
                    accept="image/*"
                    onChange={handleFileChange}
                    ref={fileInputRef}
                    hidden // Ẩn input gốc đi
                  />
                  {selectedFile && (
                    <div className="mt-2 text-sm text-gray-600">
                      Ảnh đã chọn: {selectedFile.name} ({(selectedFile.size / 1024).toFixed(2)} KB)
                    </div>
                  )}
                </div>

                {/* Action Buttons in Modal */}
                <div className="flex flex-col sm:flex-row space-y-3 sm:space-y-0 sm:space-x-3 pt-4">
                  <button
                    type="button"
                    onClick={() => setShowCreateModal(false)}
                    className="flex-1 btn-secondary"
                  >
                    Hủy
                  </button>
                  <button
                    type="submit"
                    disabled={createNewsMutation.isLoading}
                    className="flex-1 btn-primary disabled:opacity-50"
                  >
                    {createNewsMutation.isLoading ? (
                      <div className="flex items-center justify-center space-x-2">
                        <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
                        <span>Tạo...</span>
                      </div>
                    ) : (
                      'Tạo'
                    )}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default NewsManagement;
