import apiClient from './apiClient'; 
import { createFileUploadConfig } from './apiClient'; 

const API_BASE_PATH = '/api/v1/news';

const newsService = {
  // Get all news articles
  getAllNews: async () => {
    try {
      console.log('ðŸ”— [NewsService] Calling API:', API_BASE_PATH);
      console.log('ðŸ”— [NewsService] Full URL:', `${process.env.REACT_APP_API_URL || 'http://localhost:8080'}${API_BASE_PATH}`);

      const response = await apiClient.get(API_BASE_PATH);
      console.log('âœ… [NewsService] Raw API Response:', response);
      console.log('âœ… [NewsService] Response type:', typeof response);
      console.log('âœ… [NewsService] Response is array:', Array.isArray(response));

      // Debug: Log all response properties
      if (response && typeof response === 'object') {
        console.log('âœ… [NewsService] Response keys:', Object.keys(response));

        // Check each key
        Object.keys(response).forEach(key => {
          console.log(`âœ… [NewsService] response.${key}:`, response[key]);
        });
      }

      // Handle different response structures
      let newsList;
      if (Array.isArray(response)) {
        console.log('âœ… [NewsService] Using direct array response');
        newsList = response;
      } else if (response?.data && Array.isArray(response.data)) {
        console.log('âœ… [NewsService] Using response.data array');
        newsList = response.data;
      } else if (response?.content && Array.isArray(response.content)) {
        console.log('âœ… [NewsService] Using response.content array');
        newsList = response.content;
      } else if (response?.items && Array.isArray(response.items)) {
        console.log('âœ… [NewsService] Using response.items array');
        newsList = response.items;
      } else {
        console.warn('âš ï¸ [NewsService] Unexpected response structure:', response);
        // As fallback, if response itself is truthy but not an array, return empty array
        newsList = [];
      }

      console.log('âœ… [NewsService] Final processed newsList:', newsList);
      console.log('âœ… [NewsService] newsList length:', newsList?.length || 0);

      // Ensure we always return an array
      const result = Array.isArray(newsList) ? newsList : [];
      console.log('âœ… [NewsService] Returning result:', result);
      return result;
    } catch (error) {
      console.error('âŒ [NewsService] API Error:', error);
      console.error('âŒ [NewsService] Error status:', error.response?.status);
      console.error('âŒ [NewsService] Error data:', error.response?.data);
      throw error; 
    }
  },

  // Get news by ID
  getNewsById: async (id) => {
    try {
      const newsArticle = await apiClient.get(`${API_BASE_PATH}/${id}`);
      return newsArticle;
    } catch (error) {
      console.error(`Error fetching news with ID ${id}:`, error);
      throw error;
    }
  },

  createNews: async (newsData) => {
    try {
      console.log('ðŸ”— [NewsService] Creating news:', newsData);
      const response = await apiClient.post(API_BASE_PATH, newsData);
      console.log('âœ… [NewsService] Create response:', response);

      // Handle different response structures
      const result = response?.data || response;
      console.log('âœ… [NewsService] Processed create result:', result);

      return result;
    } catch (error) {
      console.error('âŒ [NewsService] Create error:', error);
      throw error;
    }
  },

  // Update a news article
  updateNews: async (id, newsData) => {
    try {
      const response = await apiClient.put(`${API_BASE_PATH}/${id}`, newsData);
      return response;
    } catch (error) {
      console.error(`Error updating news with ID ${id}:`, error);
      throw error;
    }
  },

  // Delete a news article
  deleteNews: async (id) => {
    try {
      const response = await apiClient.delete(`${API_BASE_PATH}/${id}`);
      return response;
    } catch (error) {
      console.error(`Error deleting news with ID ${id}:`, error);
      throw error;
    }
  },

  uploadNewsAttachments: async (newsId, file, onUploadProgress) => {
    try {
      const formData = new FormData();
      formData.append('files', file); 

      const config = createFileUploadConfig(onUploadProgress);
      const response = await apiClient.post(`${API_BASE_PATH}/uploads/${newsId}`, formData, config);
      return response;
    } catch (error) {
      console.error(`Error uploading attachment for news ID ${newsId}:`, error);
      throw error;
    }
  },

  getImageUrl: (imageName) => {
    return `${process.env.REACT_APP_API_URL || 'http://localhost:8080'}${API_BASE_PATH}/image/${imageName}`;
  }
};

export default newsService;
